css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kopernik.bielsko.pl") {
  
  body{



background: url("http://koperbb.linuxpl.eu/background2.jpg");
	background-size: 100% ;
  background-repeat: no-repeat;
  background-attachment: fixed;
		font-family: 'Arial';
    color: gray;

}
	
	p{ 
        font-family: Arial;
	color: black;
		border-radius: 20px;
				padding: 10px;
		background-color: rgba(255, 255, 255, 0.5);
        font-size:12pt;
	margin:0.2cm 1cm 0.2cm 1cm;
	line-height:140%;
	text-indent:18pt;
	
}

	
  
  td           { text-align: center;
	           padding: 4px; 
	           font-family:Arial;
	           color: gray;
	           line-height:130%;
		         font-size:15pt;
 
}
  
  p.w{
	text-indent:0pt;
        background-color: white;
		    box-shadow: 0px 0px 20px 2px rgba(0,0,0,0.9);
	font-size:15pt;
	border:3px;
	font-family:Arial;
  border-radius: 10px;
	font-style: italic;
	font-weight: bolder;
	text-align:center;
	margin:0.1cm 0.1cm 0.1cm 0.1cm;
	padding: 4px;
	color: gray;
}
  
  p.i{ 
        
	font-family: Arial;
	color: black;
	font-style: italic;
  font-size:11pt;
	line-height:170%;
  margin:0.2cm 1cm 0.2cm 1cm
	}
	
li{ 
        font-family: Arial;
	      color: black;
        font-size:13pt;
	margin:0.2cm 1cm 0.2cm 1.5cm;
	line-height:130%;
	text-indent:-1pt;
}
	
	div{ 
        font-family: Arial;
	color: black;
        font-size:14pt;
	line-height:130%;
}
	
	td           { text-align: center;
	           padding: 2px; 
	           font-family: Arial;
	           background-color: rgba(255, 255, 255, 0.5);
		         color: black;
	           line-height:130%;
	        	 font-family: Arial;

	}

	.tytul{ /* komórka z tytu³em */
	background-color: white;
	color: gray;
	height:30px;
	font:bold 26px Arial;
  border-radius: 15px;
  box-shadow: 0px 0px 20px 2px rgba(0,0,0,0.9);
	text-align:center}
	
	th{
	background-color: white;
	color: gray;
	font-size:19px}
td.g{ /* godzina */
	background-color: white;
	color:gray;
	vertical-align:top;
	text-align:center;
	white-space:nowrap}
td.nr{ /* numer lekcji */
	background-color: white;
	color: gray;
	text-align:center;
	vertical-align:top}
td.l{ /* lekcja */
	background-color: rgba(255, 255, 255, 0.5);
	color: black;
	vertical-align:top}
a.n{ /* nauczyciel */
	color:black}
a.s{ /* sala */
	color:black}
a.k{ /* klasa */
	color:black}
.p{ /* przedmiot */
	color:black;
}

.sLink2 {font:italic bold 13pt Arial; }
.sLink2:link    {text-decoration:none;color: gray} 
.sLink2:visited {text-decoration:none;color: black} 
.sLink2:hover   {text-decoration:none;color: gray} 

.sLink3 {font:italic bold 11pt Arial;}
.sLink3:link    {text-decoration:none;color: gray} 
.sLink3:visited {text-decoration:none;color: gray} 
.sLink3:hover   {text-decoration:none;color: gray} 


	
	a { color: black}

	hr { border: 1px; height: 0; /* Firefox... */ box-shadow: 0 0 3px 2px black; }

	
	}
