css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {

html, body, .code, code {

    background: #e5e5e5 !important;
    color: #000000 !important;
}

    #bd a.tag {

    color: #d4d4d4;

}

    .message-table .tag {

     background: #d6d6d6;

      
}

    .boxlet {

    background-color: rgba(220, 220, 220, 0.5) !important;

    border: 1px solid #969696;

    border-radius:  0; 

}
  
     a {

    color: #000000 !important;

}

     a.visited {

    color: #000000 !important;  

}

    .infoblock{

     background: rgba(220, 220, 220, 0.6) !important;
}

    .messages .msg {

    background: none repeat scroll 0% 0% rgba(220, 220, 220, 0.6)
}

    .nav-buttons a {

    color: #d4d4d4 !important;
}
  
    #bd .btn {
      
    border: 1px solid #969696;
      
    color: #545454;
}
  
    .page-number {
        
     border: 1px solid;
        
     border-radius: 5px;
        
     background-color: #d4d4d4;
}
  
    #bd .forum table th {

    background: #8c8c8c !important;
        
    border: 1px solid #b1b1b1;    
}

    .message-table tr:hover {
        
     background: #d6d6d6 !important;
}
    
    .message-table a.secondary {
        
     color: #000000 !important;
}
    
    #sitetitle {

    color: #222222 !important;

}

    h2 a {

    color: #222222 !important;
}
  
    #bd h1, #bd h2 {
      
    color: #222222;

}

    .menu a {

    color: #222222 !important;

    text-decoration: none;

}

   #loginGreating a {

    color: #222222 !important;

    text-decoration: none;
}


   #loginGreating a:before, .head a[href*="people"]:before{

   content: "hello, ";

   font-size: 1em;
}    

    #bd .btn-selected {
   
    background-color: #adadad;
      
    color: #c1c1c1;      
}
  
    #bd .btn-default {
      
    background-color: #d6d6d6;
}

    #bd .forum table tbody td {

    border: 1px solid #b1b1b1;      
}

    #whois_userpic {

    background: none repeat scroll 0% 0% #e0e0e0
}


    #bd .btn-primary {
    background-color: #1fafaf;
    color: #dcdcdc;
}


    #bd .btn-danger {
    background-color: #1fafaf;
    color: #ffffff;  
}  
  
    img {

    filter: contrast(100%) brightness(90%);     
}     
      
    .stars {

    color: #1fafaf;      
}
 
   .group-label {
    background-color: #222222;
}

    .fav-buttons .selected [class^="icon-"] {
    color: #1fafaf;
}    

    .messages .msg:target {
    border: 1px solid #1fafaf;
}
    
    .user-remark {
    color: #3a3a3a;
}
    
    #bd .forum table thead, #bd .forum table tfoot {
    color: #ededed;
}
}
