css:
@-moz-document url-prefix(http://www.google.), url-prefix(http://google.) {

* {font-family:Tahoma!important;font-size:12px!important}

html {margin:0!important;padding:0!important;background:#fff!important;}
body {padding:10px!important;margin:5px!important;border:0;-moz-border-radius:9px!important;background:#ebeff9!important;}

div#ssb {background:none!important;margin:10px auto 10px auto!important;-moz-border-radius:20px!important;padding:0 10px 0 10px!important;border:0!important;width:80%!important;}
div#bsf {border:0!important;-moz-border-radius:6px!important;background:none!important}
div.s {line-height:1.4!important}
div.e {text-align:center!important}

#gbar {margin:0 0 0 10px!important}
#gb {padding:0 10px 0 0!important}
[class*="gb"], #gb a {text-decoration:none!important}

table#sft > tbody > tr > td:first-child {display:inline!important;width:100%!important;}
table#sft > tbody > tr > td:first-child img {margin-left:6px!important;}
table#sft {width:100%!important;text-align:center!important;margin:0!important;padding:0!important;background:#fff!important;-moz-border-radius:20px!important;border:0!important}
table#sft table {padding:8px!important;margin-bottom:10px!important;clear:both!important}
table#nav td {padding:5px!important}
table#nav {margin:0 auto 0 auto!important}
table[style="margin-top: 25px;"][cellpadding="0"][cellspacing="0"] {width:auto!important;margin:0 auto 0 auto}

div#res.med {margin:0!important;width:auto!important;background:#fff!important;padding:2px 8px 2px 8px!important;border:0!important;-moz-border-radius:20px!important}

li.g {padding:5px!important;margin:8px auto 0 auto!important;text-align:left!important;white-space:pre!important;overflow:hidden!important}
li.g:hover {background:#ebeff9!important;-moz-border-radius:6px!important;}
li.g a {text-decoration:none!important}
ol {-moz-column-count:1!important}

img[width="1"][height="1"],div.gbh, .csb  {display:none!important}

}
