css:
@-moz-document url-prefix("https://hb.en-japan.com/") {
/* ------------------------------------------------------------
　■サイト全体幅
------------------------------------------------------------ */

/* サイト全体 
------------------------------------------------------------ */
.md_header .navArea,
.md_header .headerArea,
#globalPankuzu .pankuzu {
/*    width: 985px; */
    width: 900px;
}
#pageSet {
/*    width: 985px; */
    width: 900px;
}
.md_pageHeader {
/*    width: 985px; */
    width: 900px;
}

#globalSeoFooterLink .base {
/*    width: 985px; */
    width: 900px;
}

#globalFooter .menuArea, #globalFooter .nameArea, #globalFooter .enLinkArea, #globalFooter .copyright {
/*    width: 985px; */
    width: 900px;
}

#md_pageTop {
    display:none !important;
}

/* ------------------------------------------------------------
　■検索結果
------------------------------------------------------------ */

.jobsearchLeftArea {
/*    width: 255px; */
}

.jobsearchRightArea {
/*    width: 710px; */
    width: 620px;
}
.komaContent a:link h2,
.komaContent a:link h3,
.pickupArea .komaList .listBtn .data .title a:link h2, 
.pickupArea .komaList .listBtn .data .title a:link h3
{ color: #0000ff!important; }
.komaContent a:visited h2,
.komaContent a:visited h3,
.pickupArea .komaList .listBtn .data .title a:visited h2, 
.pickupArea .komaList .listBtn .data .title a:visited h3
{ color: #000080!important; }
.komaContent a:hover h2,
.komaContent a:hover h3,
.pickupArea .komaList .listBtn .data .title a:hover h2, 
.pickupArea .komaList .listBtn .data .title a:hover h3
{ color: #ff0000!important; }
.komaContent a:active h2,
.komaContent a:active h3,
.pickupArea .komaList .listBtn .data .title a:active h2, 
.pickupArea .komaList .listBtn .data .title a:active h3
{ color: #ff8000!important; }

#jobKomaArea .komaContent .point p,
.pickupArea .komaList .listBtn .data .point{
    color:#333;
}

/* ------------------------------------------------------------
　■サイトTOP
------------------------------------------------------------ */

#pageContentArea {
/*    width: 985px; */
    width: 900px;
}

#pageContentArea #centerArea {
/*    width: 515px; */
    width: 400px;
}

.addConditionBanner,
#md_underRecommend {
    display:none !important;
}


/* TOP > 検索BOX 
------------------------------------------------------------ */
#pageTopArea .easySearchArea {
    width:auto;
}
#pageTopArea .easySearchArea .formArea {
/*    width: 750px; */
    width: 650px;
}
#pageTopArea .easySearchArea .formArea input {
/*    width: 332px; */
    width: 280px;
}
#pageTopArea .easySearchArea .formArea .space {
    left: 50%;
}

/* TOP > 今日の人気バイトランキング
------------------------------------------------------------ */
#centerArea .rankingArea .content .koma .btnArea {
/*    display: table-cell; */
    display: none;
}


/* ------------------------------------------------------------
　■詳細ページ 
------------------------------------------------------------ */

#pageSet .descLeftArea {
/*    width: 680px; */
    width: 580px;
}

#pageSet .descLeftArea {
    float:right;
}

#descDataSet .dataArea .data .title {
    color:#666;
}
