    width: 900px;
}
div#recommendArea4 .content .md_list_btn { 
/*    width: 1200px; */
    width: 900px;
}
.recommendArea.desc .recommendList {
/*    width: 985px; */
    width: 900px;
}
.recommendArea.desc .recommendList .jobList .jobKomaWrap .jobKoma {
/*    width: 311px; */
    width: 280px;
}
.recommendArea.desc .recommendList .jobList .jobKoma .title a h3 {
	font-size:85%;
}
.recommendArea.desc .recommendList .jobList .jobKoma .content .btn {
/*    display: table-cell; */
    display: none;
}

.pointArea .title .mainCopy {
    color:#333;
}

