css:
@-moz-document domain(forums.bistudio.com) {
#branding, #header_bar, #primary_nav {
    max-width: 80% !important;
}

#content, .main_width {
    max-width: 80% !important;
}

.topic_buttons li.important a, .post_controls li.important a, .topic_buttons li.important span, .post_controls li.important span, .ipsButton .important, .topic_buttons li a, .post_controls li a, .topic_buttons li span, .ipsButton, .__like a {
    background: #555555 none repeat scroll 0 0 !important;
}

.topic_buttons li.important a:hover, .post_controls li.important a:hover, .topic_buttons li a:hover, .post_controls li a:hover, .__like a:hover:hover {
    color: #EEE !important;
}
   
ul.post_controls li.report a {
    background-color: peru !important;
    opacity: 0.75 !important;
}

ul.post_controls li.report a:hover {
    background-color: orangered !important;
    opacity: 0.95 !important;
    color: #CCC !important;
}
    
.ipsLikeButton {
    padding-bottom: 3px;
    padding-top: 2px;
    color: #fff !important;
}


.post_wrap, .userpopup {
    background: -moz-linear-gradient(top,  hsla(0,0%,0%,0.12) 0%, hsla(0,0%,77%,0.20) 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,hsla(0,0%,0%,0.12)), color-stop(100%,hsla(0,0%,77%,0.30)));
    background: -webkit-linear-gradient(top,  hsla(0,0%,0%,0.12) 0%,hsla(0,0%,77%,0.30) 100%);
    background: -o-linear-gradient(top,  hsla(0,0%,0%,0.12) 0%,hsla(0,0%,77%,0.30) 100%);
    background: -ms-linear-gradient(top,  hsla(0,0%,0%,0.12) 0%,hsla(0,0%,77%,0.30) 100%);
    background: linear-gradient(to bottom,  hsla(0,0%,0%,0.12) 0%,hsla(0,0%,77%,0.30) 100%);
}
    
.post_body .post {
    line-height: 1.5;
    font-family: Verdana !important;
    font-size: 1.1em !important;
}
.post_body {
    border-left: 2px solid rgba(0, 0, 0, 0.05) !important;
}

.signature {
    border-top: 2px solid rgba(0, 0, 0, 0.02) !important;
}

.post-member-title {
     border-bottom: 2px solid rgba(0, 0, 0, 0.05) !important;
}
.row2 { display: initial !important; }
    
.ipsUserPhoto {
    background: #888 none repeat scroll 0 0 !important;
    border: 2px solid #444 !important;
    border-radius: 2px !important;
    padding: 1px !important;
}
    
.ipsLikeBar_info {
    background: none !important;
    color: #666;
    display: inline-block;
    padding: 2px 10px;
}
.ipsLikeBar_info a:hover {
    color: #ED8351;
}
.ipsTag {
    text-transform: uppercase;
    color: #BBB;
    text-shadow: none;
    background: none !important;
    background-color: #555 !important;
    border-radius: 40px 6px 6px 40px; 
    -moz-border-radius: 40px 6px 6px 40px; 
    -webkit-border-radius: 40px 6px 6px 40px; 
    padding-bottom: 2px;
    padding-right: 10px;
    border: 0px solid #800000;
}
.ipsTag:hover
{
    color: #DDD;
    padding-right: 10px;
}
.userpopup h3 { background-color: #AAA; }
p.citation {
    background: #E0DFBA none repeat scroll 0 0;
}
blockquote.ipsBlockquote {
    background: #e6e5dc;
}

pre.prettyprint, code.prettyprint {
    background-color: #E8E7CF !important;
}   

li.L1, li.L3, li.L5, li.L7, li.L9 {
    background-color: #E0DFBA;
}
    
#page-header-wrapper .nav > li > .btn {
    padding: 12px 15px;
}
#page-header-wrapper .navbar-nav > li a {
    padding: 5px 15px;
}  
#page-header-wrapper .navbar-brand {
    background: url("http://i.imgur.com/VGULYpB.png") no-repeat scroll center center;
    padding: 0px 90px !important;
}
#page-header-wrapper {
    height: 45px;
}
#page-header-wrapper .container {
    height: 45px;
}
#primary_nav {
    padding: 20px 10px 0;
}
table.ipb_table .unread .topic_title {
    color: #ED8351;
}
li.L0, li.L1, li.L2, li.L3, li.L4, li.L5, li.L6, li.L7, li.L8, li.L9 {
    color: #555;
}
#secondary_navigation {
    margin-bottom: 20px;
}
}
