@-moz-document url-prefix("file:///C:/Users/Admin/Dropbox/13-yubbler/Yubbler%20Club%20Home%20Page.html"), domain("yubbler.com") {
/*general styles
------------------------------------------------*/

/*@import url(https://fonts.googleapis.com/css?family=Montserrat:400,700);*/

body, div, p, td, li, a {
	font-family: 'Montserrat', sans-serif !important;
}

a:hover {
    color: #4D89D1;
    text-decoration: none;
    transition: color 0.5s;
}

body {
	background-color:#f4f6f7;
	overflow-x: hidden;
}

/* old container - 1000px width

#container {
    width: 1000px;
    margin: 20px auto 0 auto;
    background-color: #FFFFFF;
    padding: 10px 0 0 0;
}*/

/*new container - wide 100%*/
#container {
    width: 100%;
    margin: 0;
    background-color: #FFFFFF;
    padding: 0;
}

#full_column {
    margin: 10px 5px 10px 5px;
    width: 1000px;
    margin: 0 auto;
}

#main > div.row {
    padding: 0;
	width:1000px;
    margin: 0 auto;
}

.club_div #accordion .ui-accordion-header {
	color: #000;
	font-size: 24px;
	padding:20px;
	text-transform: uppercase;
}

#accordion .tab-requests .barfull {
	background-color: #fff;
	color: #000;
	font-size: 30px;
	font-family: 'Montserrat', sans-serif;
	padding: 20px 0 20px 10px;
	text-transform: uppercase;
}



/*basic reset for ui*/
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 0px solid;
    font-weight: 400;
}


.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
    border: 0 solid;
    background: #cb4a09;
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 0 solid;
    font-weight: 400;
}






/*blue accordion to white*/
#accordion .ui-accordion-content {
    background: #fff !important;
}

/*shadow around boxes*/
.clubboxes2 {
    width: 225px;
    height: 158px;
    padding: 10px;
    float: left;
    margin: 15px;
    background-color: #fff;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -moz-box-shadow: 0 0 30px 2px #ccc;
    -webkit-box-shadow: 0 0 30px 2px #ccc;
    box-shadow: 0 0 30px 2px #ccc;
    position: relative;
}

/*album*/
.album-box {
    padding: 10px;
    float: left;
    margin: 15px 5px 15px 15px;
    background-color: #fff;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -moz-box-shadow: 0 0 30px 2px #ccc;
    -webkit-box-shadow: 0 0 30px 2px #ccc;
    box-shadow: 0 0 30px 2px #ccc;
    position: relative;
    cursor: pointer;
}





/*menu*/
.menu_button, .menu_button1, .menu_button_loggedon{
	border: none;
}

.menu_button1 {
	margin-left: 160px;
	margin-top: 3px;
}

.menu_button {
	margin-top: 3px;
}

.menu_button a, .menu_button1 a, .menu_button_loggedon a {
    color: #4C5A63;
    text-decoration: none;
}

.menu_button a:hover, .menu_button1 a:hover, .menu_button_loggedon a:hover {
    color: #4D89D1;
    text-decoration: none;
	transition: color 0.5s;
}





/*top header (left column)
------------------------------------------------*/

/*todo refactor rename #left_column to #nav-top */

#left_column {
	display: block;
	float: none;
	width: 100%;
	height:70px;
	margin: 0 auto;
}

#left_column:after {
	content:'';
	float: none;
	clear:both;
}

/*todo refactor each non-unique .whitebox must have unique name - this one with jqtree rename .whitebox to .nav-megamenu*/

.whitebox {
    background-color: #fff;
    padding-bottom: 0;
    margin: 0 0 0 0;
    overflow: visible;
}

/*todo refactor rename .box to .nav-container */
.box {
	display: block;
	position: relative;
    width: 1000px;
    overflow: visible;
	float:none;
	clear:both;
	margin: 0 auto;
}






/*top nav
-----------------------------------------------------*/
#clubtree {
    position: absolute;
    font-size: 18px;
    color: #000;
    display: block;
    left: 50px;
    z-index: 1000;
}

/*background for the jqtree and hover effect*/
#clubtree > ul {
    background-color: #fff;
    padding: 20px 40px;
    box-shadow: 0 30px 50px rgba(0,0,0,0.0);
	transition: box-shadow 1s;
}

#clubtree:hover > ul {
    box-shadow: 0 30px 50px rgba(0,0,0,0.2);
}


/*font and color for all links in the jqtree*/
.jqtree-title, .jqtree-title a {
    color: #4C5A63 !important;
	font-size: 18px;
}

/*todo edit jqtree arrow style*/
ul.jqtree-tree .jqtree-toggler {
	color:#ccc
}

/*todo refactor this mess to one class .nav-megamenu--home */
#left_column > div.box > div:nth-child(2) {
	position: absolute;
}

/*top menu - home icon*/
/*todo refactor rename #box-club-logo-wrapper to .megamenu-avatar */
#box-club-logo-wrapper img{
	width: 64px;
	height: 64px;
	border-radius:50%;
}

/*todo remove in html .whitebox .bar title - hidden for now*/
#left_column > div.box > div:nth-child(8) > div > span,
#left_column > div.box > div:nth-child(5) > div > span { 
	display:none
}




/*todo edit left this code here for further development*/

/*.jqtree_common.jqtree-folder:after {
    clear: both;
    float: none;
    content: '';

.jqtree-tree li.jqtree_common {
	clear: both;
	float: left;
}*/

/*jqtree selected item
.jqtree-selected {
	background-color:red !important;
	display: block;
}*/

/*menu toplevel #1 
#clubtree > ul > li > ul > li, 
#clubtree > ul > li > ul > li > ul > li, 
#clubtree > ul > li > ul > li > ul > li > ul > li {
	top:0;
	left:230px;
	margin:0;
}*/

/*top level #1 
#clubtree > ul > li > ul > li:nth-child(1){
	color:red; 
	position: absolute;
	margin-top: -22px;
	margin-left: 200px;
}

#clubtree > ul > li > ul > li > ul > li:nth-child(1) > div > span {
	color:purple; 
}

#clubtree > ul > li > ul > li > ul > li > ul > li:nth-child(1) > div > span {
	color:orange; 
}*/


/*#clubtree .jqtree-closed {
	background-color: yellow !important;
}*/






/*owner settings
------------------------------------------------*/

/*todo edit hidden 'owner settings' block*/
#left_column > div.box > div:nth-child(6) {
	display: none;
}





/*members
------------------------------------------------*/

#MembersBlock {
	position: absolute;
    top: 250px;
    width: 1000px;
    background-color: #fff;
    font-size: 18px;
    color: #ccc;
    z-index: 100;
}

/*todo edit fake banner image - remove it after actual content is coded */
#MembersBlock .bar {
    color: transparent;
    background-color: #ccc;
    background-image: url('http://i.imgur.com/W2DxfPz.png');
    height: 271px;
    background-size: contain;
    margin: 0;
}

/*todo suggestion - for scrolling members block we should use this custom scrollbar later: http://codepen.io/kqlambert/pen/tbJCD

For situations where more members than one row: http://i.imgur.com/Bqmqp9e.png
*/

#MembersBlock .thumbcontainer {
    margin: 0;
    padding: 40px 0 0 40px;
    width: 667px;
    height: 130px;
    overflow: auto;
}


.memberthumbs {
    width: 54px;
    height: 54px;
    display: block;
    margin: 0px;
    float: left;
    padding: 0;
    margin-right: 20px;
    margin-bottom: 20px;
}

.memberthumbs img {
    border-radius: 50%;
}

/*email invites, etc*/
#MembersBlock > ul {
    position: absolute;
    top: 308px;
    width: 200px;
    font-size: 14px;
    list-style: none;
    right: 50px;
}

.whitebox li a, .permissionsbox li, .box li a, .clubboxes li a {
    display: block;
    color: #4c5a63;
    text-decoration: none;
}

.whitebox li a:hover, .permissionsbox li a:hover, .box li a:hover, .clubboxes li a:hover {
    color: #4D89D1;
}





/*header
------------------------------------------------*/

#header {
    position: relative;
    border-bottom: 1px dotted #fff;
    padding: 0 0 0 0;
    width: 1000px;
    margin: 0 auto;
}

.logo {
    -webkit-filter: invert(.8);
    filter: invert(.8);
}






/*tabs
------------------------------------------------*/

.ui-tabs .ui-tabs-nav {
	padding:20px 0;
	text-transform: uppercase;
	margin: 0 auto;
    width: 1000px;
}

.ui-widget-header {
    border: 0 solid;
    background: #F4F6F7;
    color: #fff;
}

.ui-tabs .ui-tabs-panel {
    display: block;
    width: 1000px;
    margin: 400px auto 0;
    padding: 40px;
    background-color: #fff;
}

.ui-state-default, .ui-widget-content, .ui-widget-content .ui-state-default {
    border: 0 solid;
    background: transparent;
    color: #4c5a63;
}

.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited {
   	background-color:#f4f6f7;
	color: #4c5a63 !important;
	border-radius: 5px;
	padding: 20px !important;
	transition: background 0.3s;
}

.ui-state-hover a, .ui-state-hover a:hover, .ui-state-hover a:link, .ui-state-hover a:visited {
	color: #fff !important;
    background-color: #5d6971;
	transition: all 0.3s;
}






/*main area
------------------------------------------------*/

/*todo refactor to #page*/
#right_column {
    width: 100%;
    float: left;
    margin: 0;
}

/*todo refactor to .page-wrapper*/
.club_div {
    padding: 0;
}

/*todo refactor to .page-title*/
.clubtitle {
    display: block;
    background-color: #fff;
    color: #000;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    font-size: 36px;
	font-family: 'Montserrat', sans-serif;
    padding: 20px;
	width: 1000px;
	margin: 0 auto;
}

#tabs{
	color:#5d6971;
	background-color:#f4f6f7;
}






/*editor styles
-------------------------------------------------*/

/*todo edit WARNING: HACKS / TOO RIGID FOR THE PRIME TIME! - start of edits */

.editor_content p {
	font-family: 'RobotoSlab', sans-serif;
    font-size: 18px;
    line-height: 1.5;
    color: #000;
}

.editor_content p > span > strong, .editor_content p > strong {
	font-size: 36px !important;
	color: #4c5a63}

.editor_content p > span > strong:after, .editor_content p > strong:after {
	content:'';
	display:block;
	background-color:#4d89d1;
	width: 100px;
	height: 5px;
	margin: 25px auto 20px auto;
	border-radius: 5px
}

.editor_content img {
	border-radius: 10px;}

.editor_content table {
	font-size: 18px;
	line-height: 1.5;
    color: #000;
	margin:0 auto;
}

/*end of edits */




/*inner page -- start a yub
------------------------------------------------*/

/*todo refactor this mess to one class .yub-startayub--panel-primary */
#main > div.row > div.col-md-4 > .panel-primary {
	border: 0 solid;
	box-shadow: 0 0 0;
	padding-bottom: 80px
}

/*todo refactor this mess to one class .yub-startayub--panel-heading */
#main > div.row > div.col-md-4 > .panel-primary>.panel-heading{
	background: transparent;
	border: 0 solid;
	color: #000;
	font-size: 30px;
}

/*todo refactor this mess to one class .yub-startayub--panel-title */
#main > div.row > div.col-md-4 > .panel-primary .panel-title {
 	font-size: 24px;
	text-transform: uppercase;
}

/*footer
------------------------------------------------*/

#footer{
	padding:100px 0;
    width: 1000px;
    margin: 20px auto 0 auto;
	overflow: hidden;
    color: #99b1cf;
}

#footer:after {
    position: absolute;
    margin-top: 90px;
    margin-bottom: 200px;
    margin-left: 0px;
    content: 'THANK YOU!'; /*todo edit JS can randomize some catchy and relevant phrases here */
    font-size: 260px;
    color: #e5eaee;
    overflow: hidden;
    height: 240px;
    line-height: 1;
    white-space: nowrap;
}

#footer a {
    color: #99b1cf;
}

#footer span {
	text-transform: uppercase;
	font-size: 14px;
}

#footer > span:nth-child(1) {
	margin-left: 400px;
}
}
