css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.skype.com") {
  
  :root .swx .shell.notifications .notification.info {
    background-color: #2a3742 !important;
    border-color: #3a4752 !important;
  }
  
  :root .content .welcomeMessage {
    color: #000000 !important;
  }

  :root div.warning[role="alert"] {
    color: #4B4B5A !important;
  } 
  
  #footer {
    display: none;
  }
  
  :root .swx .TranslatorSettings {
    background-color: #353d43 !important;
  }
  
  :root .swx {
    color: #cedde5 !important;
  }
  
  :root .swx .SettingsDialog,
  :root .swx .SettingsDialog a,
  :root .swx .SettingsDialog h1,
  :root .swx .SettingsDialog h2,
  :root .swx .SettingsDialog h3,
  :root .swx .SettingsDialog h4 {
    color: #353d43 !important;
  }
  
  :root .swx .side .navigation .navItem.active,
  :root .swx .side .navigation .navItem:active {
    background-color: #353d43 !important;
  }
  
  :root .swx .side .recent .message {
    color: #727b7f !important;
  }
  
  :root .swx .side .recent.active .message {
    color: #475866 !important;
  }
  
  :root .swx .side .recent:hover {
    background-color: #353d43 !important;
  }
  
  :root .swx .side .recent.active {
    background-color: #00AFF0 !important;
  }
  
  :root .swx .chat .input.active {
    background-color: #202426 !important;
  }
  
  :root .swx .search .input.active input,
  :root .swx .contactPickerInput .contacts input {
    color: #cedde5 !important;
  }
  
  :root .swx .chat .input textarea {
    color: #cedde5 !important;
  }
  
  :root .swx .chat .history .message.live-session .bubble .content .liveSessionText,
  :root .swx .chat .history .message.live-session.their .bubble .content .liveSessionText {
    color: #475866;
  }
  
  input::-moz-placeholder, textarea::-moz-placeholder {
    color: #404a51 !important;
  }
  
  :root .swx .chat .conversation .their.message.media .content p.urlPreviewText {
    background-color: #353d43 !important;
  }

  :root .swx .chat .conversation .message.media.me .content p.urlPreviewText {
    background-color: #2a3742 !important;
  }

  :root .swx .chat .conversation .message.me .bubble {
    background-color: #2a3742 !important;
    color: #cedde5 !important;
  }
  
  :root .swx .chat .conversation .message.their .bubble {
    background-color: #353d43 !important;
    color: #cedde5 !important;
  }
  
  :root .swx .chat .conversation .message.me .arrow::after {
    background: none transparent !important;
    margin: 2px -2px;
    border-right: 0 solid transparent !important;
    border-left: 5px solid #2a3742 !important;
    border-top: 5px solid transparent !important;
    border-bottom: 5px solid transparent !important;
    width: 0 !important;
    height: 0 !important;
    transform: none !important;
  }
  
  :root .swx .chat .conversation .message.their .arrow::after {
    background: none transparent !important;
    margin: 2px -2px !important;
    border-left: 0 solid transparent !important;
    border-right: 5px solid #353d43 !important;
    border-top: 5px solid transparent !important;
    border-bottom: 5px solid transparent !important;
    width: 0 !important;
    height: 0 !important;
    transform: none !important;
  }
  
  :root .swx .themeWhite,
  :root .swx .themeLight {
    background: #202426 !important;
  }
  
  a, h1, h2, h3, h4 {
    color: #cedde5 !important;
  }
  
  /* contacts */
  
  :root .swx .peoplePicker .searchItem:hover,
  :root .swx .peoplePicker .searchItem.hover,
  :root .swx .peoplePicker .searchItem:focus,
  :root .swx .peoplePicker .searchItem.focus {
    background-color: #353d43 !important;
  }
  
  :root .swx .contactsPage-body li:focus .searchItem,
  :root .swx .contactsPage-body li.focus .searchItem {
    background-color: #353d43 !important;
  }
  
  /* settings */
  
  :root .swx .UserSettingsPage-category.active,
  :root .swx .UserSettingsPage-category:active,
  :root .swx .UserSettingsPage-category.hover,
  :root .swx .UserSettingsPage-category:hover{
    background-color: #353d43 !important;
  }
  
  /* new chat */
  
  :root .swx .chat .conversationHeader.active {
    background-color: inherit !important;
  }
  
  :root .swx .chat .conversationHeader .contactPickerWrapper {
    background-color: inherit !important;
    border-color: #353d43 !important;
  }
  
  :root .swx .chat .conversationHeader .contactPickerWrapper .conversationShareLink button .iconfont {
    color: #727b7f !important;
  }
  
  /* hint box */
  
  :root .swx .contactsPage-hint {
    background-color: #353d43 !important;
    color: #cedde5 !important;
  }
  
  /* borders */
  :root .swx .side::before,
  :root .swx .meApp .summary,
  :root .swx .Me-small::before,
  :root .swx .chat .conversationHeader::after,
  :root .swx .chat .input::before,
  :root .swx .search .input::before,
  :root .swx .chat .history .message.participant.their,
  :root .swx .contactPickerInput,
  :root .swx .chat .conversationHeader .contactPickerWrapper .conversationShareLink button::before,
  :root .swx .UserSettingsPage-detail--white::before {
    border-color: #353d43 !important;
  }
  
  :root .swx .search .splitter {
    background-color: #353d43 !important;
  }
  
  /* sky */
  :root .swx .Me-skyContainer::before,
  :root .swx .Me-skyContainer::after,
  :root .swx .Me-skyContainer .Me-sky,
  :root .swx .Me-skyContainer .Me-sky::before,
  :root .swx .Me-skyContainer .Me-sky::after {
    background: #202426 !important;
  }
  
  :root .swx .Me-skyContainer {
    background-color: #05354d;
  }
  
  :root .swx .Me-skyContainer .Me-sky {
    box-shadow: 0px 0px 0px 0.35em #0a75aa, 0px 0px 0px 0.7em #074e71;
  }
  
}
