css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.netrunner.com") {

    /* ----------------------------------------------------------------
    Winterbreeze Dark theme - v 1.0
    -----------------------------------------------------------------*/
    
    body, #content, #panel .upper, .tborder, .thead, .tfoot, .tcat, .trow1, .trow2 {
        background: #404145;
    }
    
    .post .post_author, .post_controls {
        background: #444549;
        border-bottom: 1px solid #393a3e;
    }

    .post.classic .post_author {
        border-radius: 4px;
        border: 1px solid #393a3e;
    }

    .post.classic .post_author div.author_avatar {
        margin-top: 8px;
    }

    .post .post_author div.author_avatar img {
        padding: 5px;
        border: 1px solid #5c5d61;
        background: #535458;
    }

    .post.classic .post_author div.author_statistics {
        border-top: 1px dotted #535458;
    }

    .post .post_head {
        border-bottom: 1px dotted #393a3e;
    }

    fieldset {
        border: 1px solid #393a3e;
    }

    #footer .upper {
        background: #343538;
        border-top: 1px solid #45464A;
        border-bottom: 1px solid #393a3e;
    }

    .thead {
       border-bottom: 1px solid #393a3e;
       border-top-left-radius: 0px;
       border-top-right-radius: 0px;
    }

    .thead_collapsed {
        border-bottom-left-radius: 0px;
        border-bottom-right-radius: 0px;
    }

    .trow1, .trow2 {
        border: 1px solid;
        border-color: #404145 #404145 #393a3e #404145;
    }

    .tcat {
        border-top: 1px solid #404145;
        border-bottom: 1px solid #393a3e;
    }
    
    .tfoot {
        border-top: 1px solid #404145;
    }

    .tborder tbody tr:last-child td:last-child {
        -webkit-border-bottom-right-radius: 0px;
        border-bottom-right-radius: 0px;
    }

    .navigation {
        background: #9c9c9e;
        box-shadow: inset 0 1px 0 #B6BAC4;
        border: 1px solid #404145;
    }
    
    .navigation a, .navigation a:link, .navigation a:visited {
        color: #333 !important;
    }

    .navigation a:hover {
        color: #111;
    }

    #panel .lower, blockquote, .codeblock {
        background: #323236;
        border: 1px solid #2B2B2E;
    }

    blockquote cite {
        border-bottom: 1px solid #404145;
    }
    
    textarea {
        background: #ddd none repeat scroll 0% 0%;
    }

    .thead, .thead a:link, .thead a:visited {
        color: #9db1c5;
        text-decoration: none;
    }

    #panel .upper a:link, #panel .upper a:visited, #panel .upper a:hover, #panel .upper a:active,
    .pagination .pagination_current {
        color: #7cb6e1;
    }

    #content a, #content a:link, #content a:visited {
        color: #7cb6e1;
        text-decoration: none;
    }

    #content a:hover {
        text-decoration: underline;
    }

    #container, table, blockquote cite {
        color: #B6BAC4;
    }

    #panel .upper, .tcat, #footer .lower, #footer .lower #current_time, .post .post_head span.post_date, .post .post_author div.author_statistics, .tfoot {
        color: #7B7D84;
    }

    #footer .lower a:link, #footer .lower a:visited {
        color: #7B7D84;
        font-weight: bold;
    }

    .postbit_buttons > a:link, .postbit_buttons > a:visited, .postbit_buttons > a:active,
    button, input.button, .button, a.button:link, a.button:visited {
        color: #fff !important;
    }

    .pagination a {
        background: #323236 none repeat scroll 0% 0%;
        border: 1px solid #2B2B2E;
    }

    .smilie {
        vertical-align: middle;
        background: #ddd;
        padding: 1px;
        border-radius: 4px;
        -moz-box-shadow: 0 0 3px #333;
        -webkit-box-shadow: 0 0 3px #333;
        box-shadow: 0px 0px 3px #333;
    }

    .dl-button {
        opacity: 0.6;
    }
  
} /* End @-moz-document */
