css:
@namespace url(http://www.w3.org/1999/xhtml);

/* YOU MAY ADJUST THE HEIGHT OF THE READER WIDGET USING THE FOLLOWING LINE IN YOUR CODE 
#feedReaderHeadlinesFrame, #feedReaderContentFrame {height:859px !important;}
*/

@-moz-document url-prefix(http://www.netvibes.com/dashboard/) {

body, img {font-size:75% !important;}
body {min-width:200px !important;}

#main-container {top:-30px !important;}

/* --- HEADER --- */
#smask-window, #top, #savingStatus {display: none !important;}
     
/*--- FOOTER ----*/
#footer {display:none !important}
#viewType-1 {padding-bottom:0px! important;}

/*--- WIGDET ---*/
.moduleHeader .uwa-widget-menu-button.uwa-icon.charts {display:none !important;}
.moduleHeader:not(hover) .menus {display:none !important;}
.moduleHeader:hover .menus {display:block !important;}

/* Link already seen */
li.read {opacity:0.4 !important;}
.nv-thumbnailedList .read .description, li.read .feedThumbnail, .sharing {display:none !important;}
li.read .main-link, .nv-thumbnailedList .read h3 {margin-left:0px !important; font-size:8px !important;}

/* Personnalization of the widgets */
.module, .moduleFrame, .moduleContent, c, .moduleHeader {border-radius: 0 8px !important;}
.resizable .module, .resizable .moduleEdit {margin: 0px !important; margin-bottom: 5px !important;}
.moduleContent{padding:5px 5px 5px 5px!important}
.item a.download {display:none !important;}
/* Pager (msg anciens)*/
.uwa-pager.nv-pager {display:none !important;} 

/* Share - To delete if you want to share */
.share {display: none !important;}

/* Gripper */
.gripper {background:none !important;}

/*--- READER ----*/
#feedReaderHeadlinesFrame, #feedReaderContentFrame {height:613px !important;} /* ADJUST HEIGHT */
#feedReader {top: -43px !important; bottom: 0px !important; margin-left:-12px !important; width:100% !important; position: absolute !important;}
#feedReaderFrame {top:0px !important; bottom: 0px !important; position: absolute !important;}
#feedReaderFrame .frame {height: 100% !important;}
#feedReaderHeadlinesFrame .unread {font-weight:bold !important;}
#feedReaderHeadlinesFrame .headline-link {white-space:normal !important; padding:5px !important;}
#feedReaderHeadlinesFrame .headline {padding:0px !important;}
#feedReaderContentFrame .content-website {padding-top:0px !important;}
#feedReaderFrame .frame .favicon, .btSwitchSize, .action-read, .nv-ir-feed-head .title .favicon, .nv-ir-feed .item-actions {display:none !important;}
#readerTable .item-actions-wrapper {width:1px !important; right:-85px !important; margin:0px !important; height:20px !important; position:absolute !important;}
#readerTable .item-actions .item-action {margin:0px !important;}
#readerTable .nv-ir-feed {
  max-width:800px !important;
  text-align:justify !important;
  padding-top:0px !important;}
#readerTable .nv-ir-article {margin-top: -15px !important;}
#readerTable .nv-ir-article-text {
  max-width:800px !important;
  line-height:18px !important;
  text-align:justify !important;
  font-size:12px !important;}
#readerTable .nv-ir-article-text p {padding-bottom:10px !important;}
#readerTable .nv-ir-feed-head {max-width:700px !important; margin-top:5px !important; padding-bottom:10px !important; bottom: 0px !important;}
#readerTable .nv-ir-feed-head .title {line-height:25px !important; font-size: 18px !important;}
#readerTable .nv-ir-feed-infos {margin-top:0px !important; padding-bottom:0px !important; bottom: 0px !important;}
#readerTable .feed.nv-item-renderer {margin:0px !important; padding-bottom:0px !important;}
#readerTable .website iframe {top:0px !important; bottom: 0px !important;}
#readerTable .content-website {padding-left:20px !important;}
#readerTable .headlines.tdHeadlines {width:100px !important;}
#readerTable img, #readerTable .entry-enclosures-list {float:left !important; margin: 10px 15px 15px 0px !important;}
#readerTable .tdContent {background-color: rgb(245,245,245) !important;}
/* Scrollbar */
.nv-quickshare-bar, .btUnReadAll {display: none !important;}
#readerTable .uwa-themed-scroller-scrollbar-y:before {opacity:0.4 !important;}

/*--- TABS ---*/
/* Slimmer tabs */
#topLinks {padding-top: 3px !important;}
#divTabs {margin-top: 1px !important; margin-left:-8px !important;}
#divTabs li {padding: 3px 5px !important;}
#divTabs .actions-tab-options, .tabs .icon {display:none !important;}
.tabs .innerTab {padding-left:4px !important; padding-right:4px !important;}
    
/* Delete "Tab +"*/
.nv-tabs-left, .nv-tabs-right {display:none !important;}

/* Number of links unread */
.innerTab .unread, .counter.unread {color:rgb(50,50,50) !important; font-weight:bold !important;}

/* Main div: removes the left padding */
#maintable{padding-left:2px!important;}

/*--- SPECIAL ---*/
#m_2295889831 .uwa-pager.nv-pager {display: inline !important;}  
}
