}
   .tid_bg2 {
    background-color: #333437 !important;
     color: White !important;
} 
   .tid_bg3 {
    background-color: rgba(159,162,170,0.4) !important;
    color: White !important;
}
   .tid_bg3 {
    background-color: #060708 !important;
    color: White !important;
}
    #tid_forum .tid_gameHeader {
    border-bottom: 5px solid transparent;
}
   .tid_bg4 {
    background-color: #008E8E !important;
