}
    .tid_twinoidAvatar table td {
     background-color: transparent !important;
}
   .tid_userSheet .tid_header .tid_headerRight {
    height: 24px !important;
    padding-top: 2px !important;
    color: #00BFFF !important;
    text-shadow: 0px 0px 10px #00BFFF !important;
}
   .tid_userSheet .tid_isUnknown .tid_userName {
    color: red !important;
    font-size: 10pt;
    font-weight: bold;
	  font-style: normal;
    text-shadow: 0px 0px 10px red !important;
}
   .tid_userSheet .tid_userName {
    color: #00BFFF !important;
	  font-size: 10pt;
    font-weight: bold;
	  font-style: normal;
    text-shadow: 0px 0px 10px #00BFFF !important;
}
   .tid_user.tid_userNoBg.tid_userFriend {
    color: #00BFFF !important;
    text-shadow: 0px 0px 10px #00BFFF !important;
}
   .tid_user.tid_userNoBg.tid_userUnknown {
    color: red !important;
    text-shadow: 0px 0px 10px red !important;
    font-weight: bold !important;
    font-style: normal !important;
}
   .tid_userSheet .tid_isContact img.tid_isContactIcon {
	opacity: 0 !important;
}
   .tid_userSheet .tid_isUnknown img.tid_isUnknownIcon {
	opacity: 0 !important;
}
   .tid_user.tid_userFriend {
    background-image: none !important;
}
   .tid_user.tid_userUnknown {
    background-image: none !important;
}
   .scontainer.userProfile .userDesc {
    border: 1px solid #1f2027 !important;
    border-top: 2px solid #1f2027 !important;
    border-bottom-color: #1f2027 !important;
    box-shadow: inset 0px 2px 4px #1f2027 !important;
    outline: 1px solid #1f2027 !important;
    background-color: #333437!important;
}
   #content {
    background-color: rgba(0,0,0,0.75) !important;
    background-image: none !important;
}
   #subBar {
    background-color: rgba(0,0,0,0.75) !important;
    background-image: none !important;
}
    #notification, .siteContent {
    color: #15B7B7  !important;
}
    #notification strong, .siteContent strong {
    color: #009898;
}
    #notification a, .siteContent a {
    color: #7FA3B9 !important;
}
    #notification a, .siteContent a:hover {
    color: white !important;
}
    .tid_twinoidWall .tid_wallEvent table td.tid_more a:hover {
    background: transparent !important;
    color: white !important;
}
    .scontainer.account .blockMenu a.active {
    color: #FFF;
    background-color: #333437;
}
    .scontainer.account .blockMenu a:hover {
    text-decoration: none;
    background-color: #03446A;
}   
   .tid_twinoidWall .tid_wallEvent table.tid_comments td.tid_content .tid_post {
    border: 1px solid #333437 !important;
    background-color: #1f2027 !important;
}
   .tid_twinoidWall .tid_wallEvent table.tid_comments td.tid_content .tid_post .tid_triangle {
	border: 8px solid transparent !important;
}
   .tid_twinoidWall .tid_wallEvent table td.tid_more a:hover {
    background: transparent !important;
    color: white !important;
}
   #subBar ul.tabs a {
    color: #7FA3B9 !important;
	text-decoration : none;
}
   #subBar ul.tabs a:hover {
	background-color : #1f2027 !important;
	color : white !important;
    text-decoration : underline !important;
}
   #subBar ul.tabs li.selected a:hover {
    text-decoration: underline !important;
	color : white !important;
}
   #subBar ul.tabs li.selected a {
    text-shadow: 0px 0px 0px #333437 !important;
    text-decoration: none !important;
    background-color: #333437 !important;
}
   #subBar {
    background-image: none ;
}
   .tid_userSheet .tid_compact {
    border: 1px solid #452314;
}
   .tid_userSheet .tid_compact .tid_bgRight {
    border: 1px solid #452314;
}
   .tid_userSheet .tid_compact .tid_userStatus .tid_bubble {
    max-width: 90px;
	min-width: 90px;
}
   .tid_userSheet .tid_compact a.tid_menu {
    visibility: hidden;
    margin-left: -30px;
}
   .tid_userSheet .tid_compact a.tid_menu:hover {
	background-color : transparent;
}
   .tid_userStatus .tid_arrow.tid_left {
    margin-top: 8px;
    margin-left: -6px;
    background-image: none;
}
   .tid_userSheet .tid_horizontal .tid_asv .tid_city {
    border-left: 1px solid #DDAB76;
    border-right: 1px solid #DDAB76;
}
   .scontainer.userProfile .profileLinks span {
    color: white;
    text-shadow: none;
}
   .tid_userStatus .tid_bubble {
    color: white;
    border: 1px solid #1f2027 !important;
    box-shadow: 0px 2px 4px #1f2027 !important;
    background-color: #333437 !important;
}
    .tid_userStatus .tid_bubble .tid_editorContent a {
    color: #7FA3B9 !important;
}
    .tid_userStatus .tid_bubble .tid_editorContent a:hover {
    color: white !important;
}
    .tid_userSheet.tid_mine .tid_editable .tid_editIcon {
    background: #1f2027 !important;
    box-shadow: 0px 2px 4px #1f2027 !important;
}
    #tid_simplePointer .tid_arrow, #tid_simpleTip .tid_arrow {
   display: none;
   visibility: hidden;
}
    #tid_simplePointer .tid_inner, #tid_simpleTip .tid_inner {
   text-shadow: 0px 0px 10px red !important;
   background-color: #290000!important;
   border: 1px solid #965C36!important;
   outline: 1px solid #4A261E!important;
   color: red!important;
}
   .tid_twinoidWall .tid_subMenu .tid_content span {
    color: white;
    text-shadow:none;
}
   .tid_twinoidWall .tid_subMenu .tid_content {
    box-shadow: inset 0px 2px 4px #1f2027;
    background-color: rgba(0,0,0,0.5);
    border: 1px solid rgba(0,0,0,0.5);
}
   .tid_twinoidWall .tid_subMenu a.tid_open.tid_opened {
    background-color: #321914;
}
   .tid_twinoidWall .tid_form form {
    background-color: #333437 !important; 
}
   body span.tid_button, body input.tid_button, body a.tid_button {
	 color : white !important;
	 background-color : #03517F;
	 box-shadow : inset 0px 15px 0px #03517F, inset 0px -1px 0px #03517F, 0px 2px 2px rgba(0,0,0,0.4);
	 border : 1px solid black;

}
   body span.tid_button:focus, body span.tid_button:hover, body input.tid_button:focus, body input.tid_button:hover, body a.tid_button:focus, body a.tid_button:hover {
	background-color : white;
	background-image : none !important;
	box-shadow : inset 0px 15px 0px white, inset 0px -1px 0px white, 0px 2px 2px white;
	text-decoration : none !important;
	color : black !important;
	border : 1px solid black !important;
}
    #frameContent.discuss td.msgPanel .discussMsg {
    background-color: #333437;
    border: 1px solid #1f2027;
    box-shadow: 0px 2px 1px #333437;
    word-wrap: break-word;
}
    #frameContent.discuss td.msgPanel .discussMsg .date {
    color: white;
}
       .siteContent h1, dt {
    text-shadow: 0 1px 0 black !important;
}
  .rightColumn, .leftColumn {
   color: #CCCCCC !important;
    box-shadow: 0 16px 8px #0F0F0F !important;
    border-bottom: 1px solid black !important;
    border-left: 1px solid black !important;
    border-right: 1px solid black !important;
    background-color: rgba(0, 0, 0, 0.4); 
}
  .siteContent h2 {
    color: #BBBBBB !important;
    text-shadow: 0 1px 0 black !important;
}
    #notification span.button, #notification input.button, #notification a.button, .siteContent span.button, .siteContent input.button, .siteContent a.button, #frameContent span.button, #frameContent input.button, #frameContent a.button {
    text-shadow: 0px 1px 2px #000;
    background-color: #03517F;
    box-shadow: 0px 15px 0px #03517F inset, 0px -1px 0px #03517F inset, 0px 2px 2px rgba(0, 0, 0, 0.4);
    border: 1px solid black;
}    
    #notification span.button:focus, #notification span.button:hover,#notification input.button:focus, #notification input.button:hover,#notification a.button:focus, #notification a.button:hover, .siteContent span.button:focus, .siteContent span.button:hover, .siteContent input.button:focus, .siteContent input.button:hover, .siteContent a.button:focus, .siteContent a.button:hover, #frameContent span.button:focus, #frameContent span.button:hover, #frameContent input.button:focus, #frameContent input.button:hover, #frameContent a.button:focus, #frameContent a.button:hover {
	background-color : white;
	background-image : none !important;
	box-shadow : inset 0px 15px 0px white, inset 0px -1px 0px white, 0px 2px 2px white;
	text-decoration : none !important;
	color : black !important;
	border : 1px solid black !important;
}
    #frameContent.discuss td.msgPanel .discussMsg {
    background-color: #333437;
    border: 1px solid #1f2027;
    box-shadow: 0px 2px 1px #333437;
    word-wrap: break-word;
}
    #frameContent.discuss td.msgPanel .discussMsg .date {
    color: white;
}
    #frameContent {
    color: white;
    background-color: #03446A;
    border-bottom: 1px solid #FFF;
        border-bottom-style: solid;
        border-bottom-color: #FFF;
}
    #frameContent.discuss td.threadsPanel table.lcontainer tr.new {
    opacity: 0.5;
    background-color: #2A698E;
}
    #frameContent.discuss td.threadsPanel table.lcontainer tr.selected {
    box-shadow: 0px 0px 4px #9F9D9B inset;
    border: 1px solid #9F9D9B;
    background-color: #333437;
}
    #frameContent.discuss td.msgPanel ul.participants {
    background: transparent none repeat scroll 0% 0%;
}
    #frameContent.discuss td.msgPanel .crel.good {
    font-size: 8pt;
    background-color: transparent;
}
    #frameContent.discuss td.msgPanel ul.participants li.label {
    margin-right: 4px;
    padding: 2px 4px;
    color: black;
    font-weight: bold;
    font-variant: small-caps;
    text-shadow: 0px 1px 0px #009898;
    font-size: 14px
}
    form .row label {
    display: block;
    margin-bottom: 3px;
    font-size: 11pt;
    font-weight: bold;
    text-shadow: 0px 1px 0px #009898;
}
    #frameContent a {
    color: white;
    text-decoration: none;
}    #frameContent h2 {
    margin: 0px 0px 20px;
    padding: 0px;
    color: black;
    font-size: 12pt;
    font-variant: small-caps;
    text-shadow: 0px 1px 1px #009898;
    border: 0px none;
}    
    #frameContent.discuss td.threadsPanel .menu .tabs a.tab {
    color: black;
}
    #frameContent.discuss td.msgPanel .crel.bad {
    background-color: transparent;
}    
    #frameContent.discuss td.threadsPanel table.lcontainer td.avatar, #frameContent.discuss td.threadsPanel table.lcontainer td.main {
    border-bottom: 1px dashed transparent;
}    
    #frameContent.discuss td.threadsPanel table.lcontainer tr.new td {
    border-bottom: 1px solid transparent;
}    
    #frameContent.discuss td.threadsPanel table.lcontainer td.main .details .date {
    font-size: 7pt;
    color: white;
}    
    #frameContent.discuss td.threadsPanel table.lcontainer td.main .subject a {
    text-decoration: none;
    color: #00BFFF;
}
    #frameContent.discuss td.threadsPanel table.lcontainer tr.unknown td.main .subject a {
    color: red;
}
