css:
@-moz-document domain("fantasysports.yahoo.com"), domain("profiles.sports.yahoo.com") {
.template-html5 .Subnav-main-wrap:before, .template-html5 .Subnav-main-wrap:after, .template-html5 .Page-bd:before, .template-html5 .Page-bd:after, .template-html5 .Page-ft:before, .template-html5 .Page-ft:after {
  background-color: #09090f;
  content: "";
  display: block;
  position: absolute;
  top: 0px;
  height: 100%;
}
   
    /* ----------------header and contest removal-------------- */  
#UH.Row.yucs.yucs-cb-standard.yucs-en-us.yucs-sports, .hd.Ta-c, .contests.Mb-20, .D-b.Ta-c.howtoplay, .dfPromo.Mb-20
  
{
display: none !important; visibility: hidden !important; margin-left: -10000pt !important; margin-top: -10000pt !important;
}
    /* ----------------main colors-------------- */  
html {
  color: #ffd;
  background-color: #fff;
}
    /* ----------------header font hover -------------- */  
  .Nav-h:not(.Nav-main) > .Navitem:hover:not(.Selected) > .Navtarget {
    color: #48f;
}
    /* ----------------game score font -------------- */ 
  p a, td a {
    color: #48f;
  }
    /* ----------------player name font -------------- */ 
  #Stencil .F-link {
    color: #48f;
}

    /* ---------------------------day pull down menu--------------------*/
.Navitem > .Navtarget, .Navitem-main > .Navtarget {
    display: block;
  background-color: #000;
}
  .Nav-flyout .Navtarget:hover 
  { background-color:  #113
  }
 /*.Navitem:hover > .Navtarget, Navitem-tabbed:hover > .Navtarget {
   font-weight: normal; 
   color: #f00 
 } 
  #Stencil .F-shade   
  {color: #f00
  } */
  /* ------------------------------------------league overview-------------- */  
  .Grid.Whs-nw, .Mend-20.Cur-p.rapidnofollow, .Grid-U.App-Title, .Mend-20.Cur-p.rapidnofollow, .Fz-s.SimpleTabs.NoTextDecoration.Mb-10 {
    color: #cc9 
  }  

.First.Ta-c.Px-sm.Tst-rank, .yfa-td-flex.Grid-h-mid.Px-sm.Tst-manager, .Nowrap.Ta-c.Px-sm.Tst-wlt, .Px-sm, .Ta-c.Px-sm, .Nowrap.Ta-c.Px-sm, .Ta-c.Px-sm.last{
  color: #cc9 ;
  background-color: #000
  }

  /* ------------------------------------------BUTTONS-------------- */  
  
      /* ----------------------------------smaller left buttons-------------- */ 
  #Stencil .Miwpx-40, #Stencil.Tablet .Tablet-Miwpx-40, #Stencil.Phone .Phone-Miwpx-40 {
    min-width: 20px;
}
#Stencil .Mawpx-40, #Stencil.Tablet .Tablet-Mawpx-40, #Stencil.Phone .Phone-Mawpx-40 {
    max-width: 40px;
}
  
  /* ------------------------------------------left button background-------------- */ 
.Btn-primary, .Btn.Primary {

    background-color: #114;
    background-image: none;
}
  /* ---------------button text highlight when cant move -------------- */ 
#Stencil:hover .Px-sm:hover, #Stencil:hover .Sm-px-sm:hover, #Stencil:hover.Tablet:hover .Tablet-px-sm:hover, #Stencil.Phone:hover .Phone-px-sm:hover {
 background-color: #219;
}

  /* ---------------------------------------top buttons-------------- */ 
.Btn, .Btn-secondary {
  background-color: #114;
  color: #d9d9d9;
}
.Btn:hover, .Btn-secondary:hover {
    background-color: #219;
    border-color: #0078FF;

}
  
    /* ---------------------------------------top button box-------------- */ 
  #Stencil .Py-lg, #Stencil.Tablet .Tablet-py-lg, #Stencil.Phone .Phone-py-lg {
    padding-top: 4px;
    padding-bottom: 4px;
  }
  
      /* ---------------------------------------smaller top-------------- */ 
  .Nav-h:not(.Nav-main) {
    border-bottom: 1px solid;
    border-top: 1px solid;
  }
  
  /* ----------------team names and date text color-------------- */  
a {
  color: #cccccc;
}
      /* ----------------outermost cell border color-------------- */  
.Page-maincontent, .Page-footercontent, .Page-ft {
  background-color: #222;
}
    /* --------------- bottom background color-------------- */ 
.Page-ft {
  background: #000 !important;
}
  /* ----------------roster line background color-------------- */  
.Subnav-main {
  background: #322;
}
      /* ----------------date range background color-------------- */ 
.Nav-h:not(.Nav-main) {
  border-bottom-color: #322;
  border-top-color: #322;
}
     /* ----------------cell filler color-------------- */ 
  #Stencil .F-shade {
    color: #48f;
  }
      /* ----------------cell filler color-------------- */ 
.Mod {
  background: #111000;
}
      /* ----------------position header -------------- */ 
.Table thead > tr:not(.First), .Table thead > tr.First.Last {
  background: #322;
}

      /* ----------------cell border pt 1-------------- */ 
.Table td {
  border-top: solid .3px #888 !important;
}
/* ----------------cell border padding -------------- */ 
  .Thm-inherit .ysf-rosterswapper tbody tr td {
    padding-top: 1px;
    padding-bottom: 1px;
  }
  /* ----------------cell border color pt 2 -------------- */ 
#Stencil .Bdr, #Stencil .Bdrx, #Stencil .Bdry, #Stencil .Bdrtop, #Stencil .Bdrbot, #Stencil .Bdrstart, #Stencil .Bdrend {
  border-color: #888 !important;
}
  /* ----------------text section header -------------- */ 
.Table th {
  color: #ffc;
  font-weight: normal;
  border: none;
}
  /* ---------------date range header submenu background color-------------- */ 
#Stencil .Bg-shade2 {
  background: #332;
}
  /* ---------------team slogan background color-------------- */ 
#Stencil .Bg-shade {
  background: #332;
}
  /* ---------------selected header text-------------- */ 
.Nav-h:not(.Nav-main) > .Navitem.Selected > .Navtarget {
  color: #fa8000;
}
  /* ---------------non selected header text-------------- */ 
.Nav-h:not(.Nav-main) > .Navitem > .Navtarget {
  color: #ccc;
}

  /* ---------------player note popup cell background odd-------------- */ 
.yui3-ysplayernote .teamsched .teamtable {
  margin-bottom: 0;
  border-collapse: collapse;
  background-color: #111;
}
  /* ---------------player note popup cell background even-------------- */ 
  .teamsched-bd .even {
  background: #333;
}
  /* ---------------player note popup note background-------------- */ 
.yui3-ysplayernote .playerdata, .yui3-ysplayernote .playernotes {
  background-color: #111;
}
  /* ---------------player note header-------------- */ 
.yui3-ysplayernote .teamsched {
  border-bottom: 10px solid #444;
  background-color: #322;
}

  /* --------------- player note rating text / pic-------------- */ 
.yui3-ysplayernote p.rating-value {
  color: #f33;
  margin: 5px 5px 0;
  padding: 2px;
}
  /* --------------- player note schedule header background-------------- */ 
.yui3-ysplayernote .teamsched-ft p, .yui3-ysplayernote .playernotes-ft p {
  background: #322;
  border-top: 2px solid #111;
  height: 10px;
  padding: 5px;
}
  /* --------------- player note spine-------------- */ 
.yui3-ysplayernote .playernoteview-notes {
  height: 292px;
  background-color: #444;
}
  /* --------------- player note cell border-------------- */ 
.teamsched-bd .teamtable td {
  border-top: solid 1px #999;
}

  /* ---------------team name-------------- */ 
#Stencil .F-reset {
    color: #fa8000;
}

  
  /* --------------------------------------------------PLAYER CELL HIGHLIGHTS-------------- */ 
.Thm-inherit:hover .ysf-rosterswapper:hover tbody:hover tr:hover {
    height: 2.6rem;
    background-color: #332;
}
.Table td:hover {
    background-color: #332;
}
.Table td:hover, .Table-plain td:hover {
    background-color: #332;
}
.Table-mid td:hover {
    background-color: #332;
}
  /*.Table th:hover {
    background-color: #f00;
}
*/
  /* ---------------matchup highlight -------------- */ 
.Grid-table:hover, .Tablet:hover .Tablet-grid-table:hover, .Phone:hover .Phone-grid-table:hover {
    background-color: #332;
}
  /* ---------------header text background highlight ------------
#Stencil:hover .Ta-c:hover {
    background-color: #332
}
 */
  /* ---------------top subheader-------------- */ 
.Page-wrap 
  {
  width: 100%;
/* background-color: #f00; */
}
  /* ---------------note to self highlighted-------------- */ 
#notetoself-content.is-editable:hover, #notetoself-content.is-editable:focus {
    background-color: #332;
  }
    /* ---------------minimum pitched-------------- */ 
#Stencil .F-positive {
    color: #4a2;
  }
  
.Fz-xxs.Fw-b {
    background-color: #332;
  }
  
