css:
@namespace url(http://www.w3.org/1999/xhtml);

/*

DARK GREY GMANE STYLISH USERSTYLE 
by nova@deviator.si

*/

@-moz-document domain("gmane.org")

{
  
  /* text&background ************************************************* */
  html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer {
    color: #bbb !important;
    background: #333 !important;
    font-size:small !important;
  }
  

  pre {
    font-size: medium !important;
  }

select, button {
  background: #383838 !important;
}

div, span {
  background-color: #333 !important;
}

select, input, span, code, textarea, pre, label, div, dd, option {
  color: #bbb !important; 
}


/*links ************************** */
a {
  color: #bb0 !important;
}

a:visited {
  color: #880 !important;
}

/*highlight*/
textarea, pre, input, code {
  background: #3a3a3a !important;
}

}
