css:
@-moz-document domain("lifeclips.jp") {
body,.comment_wrapper/* 文字の影 */ {
	text-shadow: 1px 1px 1px #555;  overflow: auto !important;/* 文字の影 */}

/* 背景色指定 */
body, .comment_wrapper, #comment_wrapper, input, .privacyButton li,#comment-text,
#wrapper > div.page > div > section, input, textarea, #content_write,
#wrapper > div.page > div > div.profile-top > table, #wrapper > div.page > div > div.div_stats > table,
#wrapper > div.page > div > section > div:nth-child(2) > div:nth-child(odd),#slidemenu,.slidemenu ul,
body.mobile .page{
	background-color: #000 !important;}
#wrapper > div.page > div > section > div:nth-child(2) > div:nth-child(odd),
#wrapper > div.page > div > section > div:nth-child(3) > div:nth-child(odd),
#new_clippers > div:nth-child(odd), #user_detail > div > div:nth-child(odd) {
    background-color: #111 !important;}

header/* ヘッダーを透明に */{background-color: rgba(0, 0, 0, 0) !important}
li.header_btn .write_submit_draft,li.header_btn #post_clips,
body.mobile .page{background-color:rgba(0, 0, 0, 0) !important}
li.header_btn #post_clips:hover{background-color: #195919 !important;}

/* 文字色指定 */
.user_profile .name,div.new_title,#article-title,tr.prof-name td,
#content_write,div.write-inpt #content_write,textarea#comment-text,.setting_contents .item_name,
#title_write,.text_black{
	color: #E3E3E3 !important;}

div.new_contents,article#article,div.new_name,td.user-info.comment.text a,td#user-clips.com,
td.user-info.notifications p.watched a,.data_name,div.tagsinput input,.tag_area a,div.tagsinput span.tag{
	color: #C8C8C8 !important;}

a,.div_15_all span,#wrapper > header > ul > li:nth-child(1) > a,
.reply,td.user-info.comment{
  color: #AAA !important;}

/* その他要素の色変更 */
.data_div_new {    border-bottom: 1px solid #808080;}
.tag_area a,div.tagsinput span.tag {    background: #474747;}
.notification .header_pop_count,#notification .notification_count/* ヘッダーの赤丸 */ {
  background-color: #282828;
  color: #CCC;
  border: 1px solid #AAA;}
div#banner img#user_detail/* アイコン（大）縁取り */{  border: 4px solid #000;}
.article_disallow_comment .allow_comment_icon, .article_allow_comment .allow_comment_icon/* コメント可否ボタン */{
  display: none !important;}
}
