css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("empeopled.com") {

  body,
  html {
    background-color: #FFFFFF;
  }
  
  .navbar h5 {
  font-size: small !important;
  }
  
  .feed,
  .topic .post-form {
    margin: 0 !important;
  }

  .feed .container, 
  .topic .container {
    margin-left: 56px;
    font-family: arial,sans-serif !important;
  }

  .feed .post,
  .topic .post {
    padding: 0 !important;
  }

  .feed h5,
  .topic h5{
    font-size: 1em;
  }
  
  .feed .feed-sort-option,
  .topic .feed-sort-option {
    padding: 2px 0 8px 0;
    line-height: 43px;
    font-size: small;
    margin-left: -170px;
  }

  .feed .feed-sort-option a,
  .topic .feed-sort-option a{
    color: #808080;
  }

  .feed .post-form .post-form-wrap,
  .topic .post-form .post-form-wrap {
    padding: 0;
  }

  .feed .col-md-10,
  .topic .col-md-10 {
    margin-left: 0px !important;
  }

  .feed h3,
  .topic h3{
    font-family: arial,sans-serif;
  }

  .feed h3.post-title,
  .topic h3.post-title {
    position: absolute;
    color: #1A0DAB;
    font-size: 18px;
    line-height: 1.2;
    font-weight: normal;
    width: auto;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  .feed .future .col-md-10 > a:after,
  .topic .future .col-md-10 > a:after {
    content: '' !important;
  }

  .feed .col-md-10 > a:after,
  .topic .col-md-10 > a:after {
    content: 'https://empeopled.com' attr(href);
    font-size: 14px;
    line-height: 14px;
    color: #006621;
    position: absolute;
    top: 22px;
  }

  /*comments count*/
  .feed .post .comments-count-wrap,
  .topic .post .comments-count-wrap {
    padding: 0 !important;
    margin: 0 -325px 0 1px !important;
    position: relative;
    top: 57px;
    z-index: 2;
  }
  
  .feed .stickie .comments-count-wrap,
  .topic .stickie .comments-count-wrap {
    padding: 0 !important;
    margin: 0 -325px 0 15px !important;
    position: relative;
    top: 57px;
    z-index: 2;
  }

  .feed .comments-count,
  .feed .comments-count-label,
  .topic .comments-count,
  .topic .comments-count-label {
    font-size: 14px;
    line-height: 14px;
    font-weight: normal;
    float: left;
    color: #545454;
  }

  .feed h3.comments-count:after,
  .topic h3.comments-count:after {
    content: '';
    padding-right: 3px;
  }

  /*post image*/
  .feed .col-sm-3,
  .topic .col-sm-3 {
    display: none;
  }

  .feed .post .post-info,
  .topic .post .post-info {
    border-left-color: transparent;
  }

  /*post context*/
  .feed .post .post-info .contextualinfo,
  .topic .post .post-info .contextualinfo {
    line-height: 1.4em;
    position: absolute;
    top: 37px;
    font-size: small;
    color: #545454;
  }

  .feed .post .post-info .contextualinfo a,
  .feed .user-level strong,
  .topic .post .post-info .contextualinfo a,
  .topic .user-level strong {
    color: #545454;
  }

  .feed .post .post-info,
  .topic .post .post-info {
    padding: 0;
  }

  .feed .post .post-info .contextualinfo,
  .topic .post .post-info .contextualinfo {
    margin: 0 !important;
  }

  .feed-posts div div:nth-child(2n+2),
  .topic-content div div:nth-child(2n+2),
  .row .stickie {
    margin-bottom: 20px;
    height: 94px;
    padding: 0 !important;
  }

  .feed .row .stickie {
    margin-left: -30px;
  }
  
  .topic .row .stickie {
    margin-left: -30px;
  }

  .post .stickie {
    height: 96px;
  }

  /*voting arrows*/
  .feed .post .actions,
  .topic .post .actions {
    position: absolute;
    top: 52px;
    margin: 0 0 0 1px !important;
  }

  .feed .vote-sum,
  .topic .vote-sum {
    top: 0 !important;
    color: #1A0DAB;
  }
  
  .feed .actions .fa,
  .topic .actions .fa{
    font-size: small !important;
  }

  .feed .fa-2x,
  .topic .fa-2x {
    font-size: 1em;
    color: #545454;
  }

  .feed .vote .vote-sum,
  .topic .vote .vote-sum {
    padding: 4px 0 0 0;
    color: #545454;
  }

  .feed .vote a .fa-arrow-circle-up:before,
  .topic .vote a .fa-arrow-circle-up:before {
    content: 'Tap';
    font-family: arial,sans-serif !important;
    color: #545454;
    margin:0;
  }
  
  .feed .vote a.active .fa-arrow-circle-up:before,
  .topic .vote a.active .fa-arrow-circle-up:before {
    content: '*Tap*' !important;
    font-family: arial,sans-serif !important;
    color: #545454;
  }

  .feed .vote .vote-sum:before,
  .topic .vote .vote-sum:before {
    content: '- ( ';
    font-family: arial,sans-serif !important;
    color: #545454;
  }

  .feed .vote .vote-sum:after,
  .topic .vote .vote-sum:after {
    content: ' Votes ) -';
    font-family: arial,sans-serif !important;
    color: #545454;
  }

  .feed .fa-arrow-circle-down:before,
  .topic .fa-arrow-circle-down:before {
    content: 'Slap';
    font-family: arial,sans-serif !important;
    color: #545454;
  }
  
  .feed .vote a.active .fa-arrow-circle-down:before,
  .topic .vote a.active .fa-arrow-circle-down:before {
    font-family: arial,sans-serif !important;
    content: '*Slap*';
    color: #545454;
  }

  .feed .post .comments-count-wrap,
  .topic .post .comments-count-wrap {
    padding: 0;
  }

  .feed .comments-count:after,
  .topic .comments-count:after {
    content: '';
  }

  .feed .post .comments-count-wrap,
  .topic .post .comments-count-wrap {
    padding-left: 96px !important;
  }

  .feed .col-md-2,
  .topic .col-md-2 {
    width: 200px;
  }

  .new-comment {
    position: absolute;
    width: 100%;
    color: #FFFFFF;
    left: 110px !important;
    top: -9px !important;
    text-shadow: 1px 1px 0 #7F8C8D, 1px -1px 0 #7F8C8D, -1px 1px 0 #7F8C8D, -1px -1px 0 #7F8C8D;
    z-index: 1;
  }

  .feed .user-level,
  .topic .user-level {
    margin-top: -4px;
  }

  .feed .user-level i,
  .topic .user-level i {
    color: #E9E3E3;
  }

  .post .stickie-hide-left,
  .post .stickie-hide-right {
    background-color: transparent;
  }

  .feed .post-select,
  .topic .post-select {
    color: transparent;
    border-bottom: 1px solid #EBEBEB;
    width: 200%;
  }

  .feed .row.post-form,
  .topic .row.post-form {
    margin-left: -364px !important;
  }

  .feed .post-form .input-group,
  .topic .post-form .input-group {
    padding-left: 500px;
  }

  .feed .post-form .post-select a,
  .topic .post-form .post-select a {
    background-color: transparent;
    color: #777;
    line-height: 53px;
    font-size: small;
  }

  .feed .post-form .post-select a:hover,
  .topic .post-form .post-select a:hover {
    background-color: transparent;
    color: #222;
  }

  .feed .post-select .fa-link:before,
  .feed .post-select .fa-font:before,
  .feed .post-select .fa-image:before,
  .feed .post-select .fa-tasks:before,
  .feed .post-select .fa-bookmark:before,
  .topic .post-select .fa-link:before,
  .topic .post-select .fa-font:before,
  .topic .post-select .fa-image:before,
  .topic .post-select .fa-tasks:before,
  .topic .post-select .fa-bookmark:before {
     content: '';
  }
  
  .navbar-default {
	 background-color: #F1F1F1 !important;
   height: 60px;
   border-bottom: 1px solid #E5E5E5;
  }

  .feed .post-form .form-control,
  .topic .post-form .form-control {
     width: 720px !important;
  }

  .feed .formatting-tips,
  .topic .formatting-tips {
     left: 240px;
  }

  .feed .col-xs-12.col-sm-9.col-md-10,
  .topic .col-xs-12.col-sm-9.col-md-10{
    left:110px;
  }

  .feed .post-form .actions,
  .feed .expert-title,
  .topic .post-form .actions,
  .topic .expert-title {
    padding-left: 500px;
  }

  .feed .checkbox,
  .topic .checkbox {
    padding-left: 530px;
  }

  .feed .checkbox .icons,
  .topic .checkbox .icons {
    left: 500px;
  }

  .feed .post-form .post-select a.active,
  .topic .post-form .post-select a.active {
    background-color: transparent;
    color: #4285F4;
    font-weight: bold;
    border-bottom: 3px solid #4285F4;
    border-radius: 0;
  }

  .feed body.auth .feed{
    margin-top: 0;
  }

  .feed .post-form .post-select a,
  .topic .post-form .post-select a{
    width: auto;
  }

  .topic-header {
    padding-left: 500px !important;
  }
  
  .container.post {
    margin-left: 0px !important;
  }

  .feed .container.post p,
  .topic .container.post p {
    line-height: 1.4em;
    position: absolute;
    top: 37px;
    font-size: small;
    color: #545454;
  }

  .feed .quest-FI .comments-count,
  .topic .quest-FI .comments-count {
    position: absolute;
  }

  .feed .quest-FI .comments-count-label,
  .topic .quest-FI .comments-count-label {
    margin: -39px 0 0 33px
  }
}
