css:
@-moz-document url-prefix("https://www.9lives.be/forum") {
/* www.rubenvermeulen.be */

#main-content {
    background: #181818;
}

/* Header
-----------------------*/
.above_body {
    border-bottom: 1px solid #751118;
}

/* Navigation
-----------------------*/

div.menu {
    background: #98161F !important;
    border: 0 !important;
}

.navtabs > li > a {
    background: #98161F !important;
}

.navtabs > li > a,
ul.usermenu > li > a {
    color: #fff !important;
}

.navtabs li a:hover,
ul.usermenu > li:hover {
    background: #751118 !important;
    color: #fff !important;
}

ul.usermenu > li {
    margin: 0 !important;
    padding: 0 10px !important;
}

ul.usermenu > li a:hover {
    text-decoration: none !important;
}

.forumhead {
    height: 38px !important;
}

.forumtitle a,
.forumbits .forumhead h2 {
    line-height: 38px !important;
    font-weight: normal !important;
    font-size: 15px !important;
}

.childforum,
#threads,
.threadlist ol {
    border: 0 !important;
    border-left: 1px solid #333 !important;
    border-right: 1px solid #333 !important;
}

.forumbit_post > div,
.threadbit > div {
    border-top: 0 !important;
    border-bottom: 1px solid #333 !important;
}

.forumbit_post,
.threadbit,
#forumrules,
.thread_info .blockbody,
.formcontrols .blockrow,
.toolsmenu,
.forum_info .blockbody,
li.activitybit,
.pmlist .blockrow,
#sidebar_container.member_summary,
.userprof_headers_border {
    background: #282828 !important;
}

.navlinks {
    border: 0 !important;
    background: #181818 !important;
}

.blockhead {
    background: #c41d28 !important;
    line-height: 38px !important;
    font-weight: normal !important;
    font-size: 15px !important;
}

.wgo_block .blockbody {
    background: #282828 !important;
    border: 0 !important;
    border-left: 1px solid #333 !important;
    border-right: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
}

.newcontent_textcontrol {
 	padding: 8px 15px;
}

.threadlist {
    margin-top: 15px;
}

#stickies li {
    background: #3A2424 !important;
}

.above_postlist, 
#above_postlist {
    height: auto !important;
}

.blocksubhead,
li.activitybit {
    border-color: #333 !important;
}

.prefix {
    color: #606060;
}

.below_postlist {
    margin-bottom: 25px;
}

.quickreply_title img,
.blockhead img {
    margin-top: 11px;
}

/* Pagination
------------------------*/

.threadbit .pagination span a {
    padding: 1px 4px !important;
	box-shadow: none !important;
}

.above_threadlist .pagination span a,
#pagination_top span a,
.pagination_bottom span a,
.threadpagenav span a,
#below_searchresults span a {
    padding: 5px 6px !important;
	box-shadow: none !important;
}

.pagination span a:hover,
#pagination_top span a:hover,
.pagination_bottom span a:hover,
.threadpagenav span a:hover,
#below_searchresults span a:hover {
    text-decoration: none;
}

.pagination span.prev_next a,
.pagination span.first_last a {
    top: -2px !important;
}

/* Thread
------------------------*/

.postbitlegacy,
.postbitlegacy .posthead {
    border-color: #333 !important;
}

.postbitlegacy .postfoot {
    border-color: #252525 !important;
}

.postbitlegacy .posthead {
    background: #555 !important;
}

.postbitlegacy .postdetails {
 	background: #262626 !important;   
}

.signature {
    border-color: #252525 !important;
}

.post_field {
    background: #1f1f1f !important;
}

#forumrules {
    margin: 0 !important;
}

#posting_rules,
#thread_tags_list,
.blockbody,
.toolsmenu,
#searchbits,
.pmlist .blockrow,
#sidebar_container.member_summary,
#sidebar_container .underblock,
.userprof_headers_border {
    border-color: #333 !important;
}

/* Toolsmenu
--------------------*/

.toolsmenu a:hover {
    color: #fff !important;
}

.toolsmenu a {
	font-size: 13px !important;
    padding-right: 20px !important;
}

/* Button
----------------------*/

.button,
.navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu a.textcontrol, .navpopupmenu a.popupctrl, .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.popupctrl {
    padding: 6px 8px !important;
    background: #fff !important;
}

.textcontrol {
    background: #fff !important;
}

/* Read / Unread
-------------------------------*/

.threadbit .title {
    color: #fff !important;
}

.threadbit .threadtitle_unread {
 	font-weight: bold !important;
    color: #c41d28 !important;
}

.forumicon .new {
 	background-position: 0 center !important;   
}

.forumicon .old {
 	background-position: -48px center !important;   
}

/* Announcement
----------------------------*/

.announcerow,
.announcerow .announceinfo, 
.announcerow .announcement {
    background: #fff !important;
}

.announcerow {
    padding: 15px !important;
    border: 0 !important;
}

.announcerow dt,
.announcerow dd a font {
    font-size: 16px !important;
}

.announcerow dl {
    margin-bottom: 8px;
}

.pagetitle {
    padding-top: 20px !important;
}

.pagetitle h1 {
    font-weight: normal !important;
    font-size: 17px !important;
}

/* Footer
--------------------------*/

.footer {
    padding: 6px 0px !important;
    border: 0 !important;
}

.footer select {
    height: 25px !important;
    font-size: 14px !important;
}

/* Login / Register
--------------------------*/

 ul.usermenu > li > a.loginbtn {
    color: #000 !important;
}

 ul.usermenu > li > a.registerbtn {
    color: #fff !important;
}

a.loginbtn, a.registerbtn {
    text-shadow: none !important;
}

/* Give admins, moderators, ... a different colour
-------------------------*/

a.username span[style="color: #EB4024"],
a.username span[style="font-weight: bold; color: #EB4024"],
.member_username span[style="font-weight: bold; color: #EB4024"] {
 	color: #E2CA35 !important;   
}

.moderators a.username span {
    color: #909090 !important;
}

/* Profile
--------------------------*/

dl.tabslight {
    padding-left: 0 !important;
}

#profile_tabs dd {
 	padding: 4px 15px !important;
    border: 0 !important;
    border-right: 1px solid #98161F !important;
}

#profile_tabs dd:last-child {
    border-right: 0 !important;
}

#profile_tabs dd:hover,
#profile_tabs dd.userprof_module {
 	background: #98161F !important;
}

.memberprofiletabunder {
    display: block !important;
    background: #98161F !important;
}

#activity_tab_container > div {
 	background: #333 !important;   
}

#activity_tab_container dd {
    padding: 4px 15px !important;
    font-size: 13px !important;
    background: #333 !important;
    border: 0 !important;
    border-right: 1px solid #252525 !important;
}

#activity_tab_container dd:hover,
#activity_tab_container dd.selected{
    background: #252525 !important;
}

#activity_tab_container dd a {
    margin-left: 0 !important;
    padding: 0 !important;
    background: none !important;
    font-size: 13px !important;
    color: #fff !important;
}

.userprof_headers_border #noresults {
	font-size: 13px !important;
    font-weight: normal !important;
}

.profile_widgets .textcontrol {
    background: none !important;
}

.profile_widgets .friends_list a {
 	border: 0 !important;   
}

#newactivitylink {
   	font-size: 13px !important; 
}

/* Text colors
---------------------------*/

.body_wrapper input.button, 
.body_wrapper a.textcontrol, 
.body_wrapper .navpopupmenu a.popupctrl, 
.body_wrapper #inlinemod_formctrls a.popupctrl {
 	color: #252525 !important; 
}

/* Darker red
---------------------------*/
/*
.forumhead,
.threadlisthead,
.blockhead {
    background: #98161F !important;
}
*/
}
