css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.rockhard.de") {
  
  /*#body-wrapper {
    width: 700px !important;
    margin: 0 auto !imporant;
    position: initial !important;
  }*/
  #wrap, #page-header {
    width: 1000px !important;
    max-width: 1000px !important;
    margin: 0 auto;
  }
  
  html, body, #body-wrapper, #postingbox  {
    color: #666 !important;
    background: #fff !important;
  }
  #wrap, .topiclist, .navbar, .post blockquote, textarea, .panel, #options-panel-tab {
    color: #666 !important;
    background: #eee !important;
  }  
  .post blockquote cite {
    color: #999 !important;
  }
      .header  {
        color: #999 !important;
        background: #ddd !important;        
        text-shadow: 1px 1px 0px #fff !important;
      }   
      .header a {
        color: #666 !important;
        
      }
  .post, .post .content {
    color: #444 !important;
     background: #fff !important; 
  }
  .postprofile, .postprofile strong {
    color: #555 !important;
    background: #fff !important; 
  }
  .username, .topic-title a, a.topictitle { color: #222 !important;}
  .row, .stat-block, .navlist, select, input, .pagination li a, .pagination li.ellipsis, .pagination li.ellipsis span {
    color: #666 !important;
    background: #fff !important;    
  }
  .row, .stat-block, .navlist, .header, .post, #postingbox, .panel { 
    border: 1px solid #999 !important;
  }
      .pagination li a, .pagination li.ellipsis { 
        border: 1px solid #ddd !important;
      }
  h2 a, h3 {
    color: #F04343 !important;
  }
  .postlink { 
   color: #666 !important;
    border-bottom: 1px dotted #BF0000 !important;   
  }
  .postlink:hover {
    text-decoration: none !important;
    border-bottom: 1px dotted #666 !important; 
  }
  #smiley-box 
  { background: #333 !important;}
  
  .copyright { display: none; }

}
