css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.acidch.at") {
	.xx-banned {
		color: red;
	}
	.xx-level-1, .xx-level-0 {
		color: teal
	}
	.xx-level-2, .xx-level-3, .xx-level-4, .xx-level-5, .xx-level-6, .xx-level-7, .xx-level-8 {
			color: orange
	}
	
	.xx-level-9 {
		color: #0065AA !important;
	}

body {
	background: #222;
	color: #fafafa;
	text-align: center;
	line-height: 1.4;
	margin: 0;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
	font-size: 13px;
	overflow-y: scroll;
}

a:link {
	color: #FD971F;
	text-decoration: none;
}

a:visited {
	color: #bf5656;
	text-decoration: none;
}

a:hover,
a:active {
	text-decoration: underline;
}

#container {
	color: #ccc;
	text-align: left;
	line-height: 1.4;
	margin: 0;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
	font-size: 13px;
	min-width: 990px;
}

.wrapper {
	width: 100%;
	min-width: 970px;
	max-width: 1500px;
	margin: auto auto;
}

#logo {
	background: #222;
	padding: 10px 0;
	border-bottom: 1px solid #263c30;
}

#content {
	background: #222;
	width: auto !important;
	padding: 20px 10px;
	overflow: hidden;
}

#header ul.menu {
	margin: 0;
	padding: 0;
	list-style: none;
}

#header ul.menu li {
	margin: 0 7px;
	display: inline;
}

#header ul.menu li a {
	padding-left: 20px;
	background-image: url(http://forums.acidch.at/images/headerlinks_sprite.png);
	background-repeat: no-repeat;
	display: inline-block;
	line-height: 16px;
}

#logo ul.top_links {
	font-weight: bold;
	text-align: right;
	margin: -10px 5px 0 0;
}

#logo ul.top_links a.search {
	background-position: 0 0;
}

#logo ul.top_links a.memberlist {
	background-position: 0 -20px;
}

#logo ul.top_links a.calendar {
	background-position: 0 -40px;
}

#logo ul.top_links a.help {
	background-position: 0 -60px;
}

#logo ul.top_links a.portal {
	background-position: 0 -180px;
}

#panel .upper a.logout {
	font-weight: bold;
	background: url(http://forums.acidch.at/images/headerlinks_sprite.png) right -80px no-repeat;
	padding-right: 20px;
	margin-left: 10px;
}

#panel .upper a.login,
#panel .upper a.lost_password {
	background: url(http://forums.acidch.at/images/headerlinks_sprite.png) 0 -100px no-repeat;
	padding-left: 20px;
	margin-left: 10px;
	font-weight: bold;
}

#panel .upper a.register {
	background: url(http://forums.acidch.at/images/headerlinks_sprite.png) right -80px no-repeat;
	padding-right: 20px;
	margin-left: 10px;
	font-weight: bold;
}

#panel .lower ul.panel_links {
	float: left;
}

#panel .lower ul.panel_links a.usercp {
	background-position: 0 -120px;
}

#panel .lower ul.panel_links a.modcp {
	background-position: 0 -140px;
}

#panel .lower ul.panel_links a.admincp {
	background-position: 0 -160px;
}

#panel .lower ul.user_links {
	float: right;
}

#panel .lower ul.user_links li a {
	padding: 0;
	background-image: none;
}

#panel .upper {
	background: #282828  repeat-x;
	color: #fff;
	border-top: 1px solid #444;
	border-bottom: 1px solid #000;
	padding: 7px;
	clear: both;
}

#panel .upper a:link,
#panel .upper a:visited,
#panel .upper a:hover,
#panel .upper a:active {
	color: #fff;
}

#panel .lower {
	background: #333;
	color: #999;
	border-top: 1px solid #222;
	border-bottom: 1px solid #444;
	padding: 5px;
}

#panel .lower a:link,
#panel .lower a:visited,
#panel .lower a:hover,
#panel .lower a:active {
	color: #bf5656;
}

#search {
	border: 0;
	padding: 0;
	margin: 0;
	float: right;
	vertical-align: middle;
}

#search input.button,
#search input.textbox {
	border-color: #000;
}

#search input.button {
	background: #444;
	color: #fff;
}

#search input {
	margin: -3px 0;
  background-color: #333;
    color: #ccc
}

#quick_login .remember_me input {
	vertical-align: middle;
	margin: -3px 0 0 5px;
}

#footer {
	clear: both;
}

#footer ul.menu {
	margin: 0;
	padding: 0;
	list-style: none;
}

#footer ul.menu li {
	margin: 0 5px;
	display: inline;
}

#footer .upper {
	background: #333;
	border-top: 1px solid #bbb;
	border-bottom: 1px solid #bbb;
	padding: 6px;
	font-size: 12px;
	overflow: hidden;
}

#footer a:link,
#footer a:visited,
#footer a:hover,
#footer a:active {
	color: #bf5656;
}

#footer .upper .language {
	float: right;
	margin: -1px;
	margin-left: 15px;
}

#footer .upper .language select {
	border-color: #ccc;
}

#footer .upper .theme {
	float: right;
	margin: -1px;
	margin-left: 15px;
}

#footer .upper .theme select {
	border-color: #ccc;
  background-color: #333;
  color: #ccc
}


#footer .upper ul.bottom_links {
	float: left;
	margin: 4px 0 0 0;
}

#footer .lower {
	color: #666;
	padding: 6px 6px 12px 6px;
	overflow: hidden;
	font-size: 11px;
}

#footer .lower a:link,
#footer .lower a:visited {
	color: #56820E;
	font-weight: bold;
}

#footer .lower a:hover,
#footer .lower a:active {
	color: #A6E22E;
	text-decoration: underline;
	font-weight: bold;
}

#footer .lower #current_time {
	float: right;
	color: #bf5656;
}

#debug {
	float: right;
	text-align: right;
	margin-top: 20px;
	font-size: 11px;
}

.scaleimage img {
	max-width: 100%;
}

.forum_status {
	height: 30px;
	width: 30px;
	background: url(http://forums.acidch.at/images/forum_icon_sprite.png) no-repeat 0 0;
	display: inline-block;
}

.forum_on {
	background-position: 0 0;
}

.forum_off {
	background-position: 0 -30px;
}

.forum_offlock {
	background-position: 0 -60px;
}

.forum_offlink {
	background-position: 0 -90px;
}

.subforumicon {
	height: 10px;
	width: 10px;
	display: inline-block;
	margin: 0 5px;
	background: url(http://forums.acidch.at/images/mini_status_sprite.png) no-repeat 0 0;
}

.subforum_minion {
	background-position: 0 0;
}

.subforum_minioff {
	background-position: 0 -10px;
}

.subforum_miniofflock {
	background-position: 0 -20px;
}

.subforum_miniofflink {
	background-position: 0 -30px;
}

table {
	color: #ccc;
	font-size: 13px;
}

.tborder {
	background: #333;
	width: 100%;
	margin: auto auto;
	border: 1px solid #ccc;
	padding: 1px;
}

.tfixed {
	table-layout: fixed;
	word-wrap: break-word;
}

.thead {
	background: #333 ;
	color: #ffffff;
	border-bottom: 1px solid #263c30;
	padding: 8px;
}

.thead a:link {
	color: #bf5656;
	text-decoration: none;
}

.thead a:visited {
	color: #bf5656;
	text-decoration: none;
}

.thead a:hover,
.thead a:active {
	color: #bf5656;
	text-decoration: underline;
}

.tcat {
	background: #222  repeat-x;
	color: #ccc;
	border-top: 1px solid #444;
	border-bottom: 1px solid #000;
	padding: 6px;
	font-size: 12px;
}

.tcat a:link {
	color: #bf5656;
}

.tcat a:visited {
	color: #bf5656;
}

.tcat a:hover,
.tcat a:active {
	color: #bf5656;
}

.trow1 {
	background: #333;
	border: 1px solid;
	border-color: #383838 #383838 #383838 #383838;
}

  div.sceditor-toolbar {
   background: #333;
}
  .sceditor-container textarea {
    background: #333;
    color: #ccc
}
  .trow2 {
    background: #222
  }
  .sceditor-container {
   background: #444; 
  }
  div.sceditor-group {
    background: #222;
  }
  .sceditor-button.active {
      background: #333;
    border-color: #444
}
  .sceditor-button {
    border-color: #444;
}
  .sceditor-button:hover {
    background: #555;
    border-color: #555;
}

.trow_shaded {
	background: #ffdde0;
	border: 1px solid;
	border-color: #fff #ffb8be #ffb8be #fff;
}

.no_bottom_border {
	border-bottom: 0;
}

.post.unapproved_post {
	background: #ffdde0;
}

.post.unapproved_post .post_author {
	border-bottom-color: #ccc;
}

.post.classic.unapproved_post .post_author {
	border-color: #ccc;
}

.post.unapproved_post .post_controls {
	border-top-color: #383838;
}

.trow_deleted,
.post.deleted_post {
	background: #222;
}

.trow_selected,
tr.trow_selected td {
	background: #333;
	color: #ccc;
	border-right-color: #F7E86A;
	border-bottom-color: #F7E86A;
}

.trow_selected a:link,
.trow_selected a:visited,
.trow_selected a:hover,
.trow_selected a:active {
	color: #333;
}

.trow_sep {
	background: #333;
	color: #ccc;
	border-bottom: 1px solid #222;
	padding: 6px;
	font-size: 12px;
	font-weight: bold;
}

.tfoot {
	border-top: 1px solid #222;
	padding: 6px;
	background: #333;
	color: #ccc
}

.tfoot a:link {
	color: #bf5656;
	text-decoration: none;
}

.tfoot a:visited {
	color: #bf5656;
	text-decoration: none;
}

.tfoot a:hover,
.tfoot a:active {
	color: #bf5656;
	text-decoration: underline;
}

.thead input.textbox,
.thead select, .post input.textbox {
	border: 1px solid #263c30;
  background-color: #333;
  color: #ccc;
}

.bottommenu {
	background: #333;
	color: #ccc;
	border: 1px solid #4874a3;
	padding: 10px;
}

.navigation {
	color: #ccc;
	font-size: 12px;
}

.navigation a:link {
	text-decoration: none;
}

.navigation a:visited {
	text-decoration: none;
}

.navigation a:hover,
.navigation a:active {
	text-decoration: underline;
}

.navigation .active {
	color: #ccc;
	font-size: small;
	font-weight: bold;
}

.smalltext {
	font-size: 11px;
}

.largetext {
	font-size: 16px;
	font-weight: bold;
}

fieldset {
	padding: 12px;
	border: 1px solid #333;
	margin: 0;
}

fieldset.trow1,
fieldset.trow2 {
	border-color: #bbb;
}

fieldset.align_right {
	text-align: right;
}

input.textbox {
	background: #222;
	color: #ccc;
	border: 1px solid #444;
	padding: 3px;
	outline: 0;
	font-size: 13px;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
}

textarea {
	background: #222;
	color: #ccc;
	border: 1px solid #222;
	padding: 2px;
	line-height: 1.4;
	outline: 0;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
	font-size: 13px;
}

select {
	background: #333;
	padding: 3px;
	border: 1px solid #444;
	outline: 0;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
	font-size: 13px;
}

button,
input.button {
	padding: 3px 8px;
	cursor: pointer;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
	font-size: 13px;
	background: #222;
	border: 1px solid #222;
	color: #ccc;
	outline: 0;
}

button:hover,
input.button:hover {
	border-color: #bf5656;
}

form {
	margin: 0;
	padding: 0;
}

input.error, textarea.error, select.error {
	border: 1px solid #f30;
	color: #f30;
}

input.valid, textarea.valid, select.valid {
	border: 1px solid #333;
}

label.error {
	color: #ccc;
	margin: 5px;
	padding: 0px;
	display: block;
	font-weight: bold;
	font-size: 11px;
}

form #message {
    width: 500px;
}

.editor {
	background: #222;
	border: 1px solid #444;
}

.editor_control_bar {
	background: #222;
	border: 1px solid #444;
}

.post .editor_control_bar {
	background: #333;
}

.popup_menu {
	background: #333;
	border: 1px solid #444;
}

.popup_menu .popup_item {
	background: #efefef;
	color: #ccc;
}

.popup_menu .popup_item:hover {
	background: #383838;
	color: #bf5656;
}

