css:
@-moz-document url-prefix("http://raid-gaming.net/forums/") {
.home {
    background-size: 32px;
}

.about {
    background-size: 32px;
}

.forum {
    background-size: 32px;
}

.server {
    background-size: 32px;
}

.admin {
    background-size: 32px;
}

.policy {
    background-size: 32px;
}

#headerSplash {
    height: 402px;
}

#navContainer {
	background-color: rgba(18,18,18,.45);
    height: 72px;
    padding-top: 4px;
    padding-bottom: 16px;
}

#subNavMain {
    background-color: #161616;
}

#memberNavOption {
	margin-top: -5px;
}

#nav li span.profileicon {
	margin-top: -5px;
}

#logo-cont {
	height: 260px;
}

#nav li span {
    width: 32px;
    height: 32px;
}

#nav li:hover, .hoverActives {
    background-size: 75px;
}

#nav li #navProfilePic {
    width: 65px;
    height: 65px;
    border-radius: 65px !important;
    margin-top: 4px;
}

#logo_img {
    top: 38px;
}

span.author.vcard {
	font-size: 25px;
}
}
