css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2ch.hk"), domain("2ch.pm") {

  /*Скрытие рекламы в тематике*/
  .relap-default-wrapper {
    width: 0px;
    height: 0px;
    visibility: hidden;
  }

  /*'Скрытие' блока с группой ВК*/
  .vk-group{
    width: 0px;
    height: 0px;
    visibility: hidden;
  }
  /**/

  html, body{
    background-image: url(http://orig01.deviantart.net/da9d/f/2012/168/4/7/luna_minimalism_by_flashed00-d53uq9i.png);
    background-size: cover;
    background-attachment: fixed;
    background-color: #2D4689;
    background-position: 100% 100%;
    background-repeat: no-repeat;
    color: #E6E6FA;
  }
  
  .de-cfg-head:lang(de){
    background-color: #1C2A62;
  }
  .de-qarea-hanging > #de-pform{
    border: 0px;
  }
  
  button{
    padding: 5px;
    border-radius: 5px;
  }

/*Логотип на главной*/
  .mainlogo{
    background: url(http://orig02.deviantart.net/2aa4/f/2013/062/1/7/princess_luna_shades_by_kanske_2099-d5wwzry.png) no-repeat left;
    background-size: contain;
  }
/**/
  
  .cloudheader{
    background-color: #1C2A62;
    border: 0px;
  }
  
  .table .tbody .tr:nth-of-type(2n){
    background-color: transparent;
  }
  
  .table .thead .th{
    background: transparent;
    border: 0px;
    color: #E6E6FA;
  }
  
/*Картинки*/
  img{
    border-radius: 5px;
  }
/**/

/*Меню настроек*/
  .qr{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px;
    padding: 5px;
    border-radius: 5px;
  }
  .qr .qr-header{
    background: #1C2A62 none repeat scroll 0% 0%;
    color: #E6E6FA;
    border: 0px;
    padding: 5px;
  }
  select {
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    color: #E6E6FA;
    padding: 5px;
    border-radius: 5px;
  }
/**/

.mainpage #adminbar, #adminbar{
  margin-bottom: 40px;
  border: 1px;
}

/*Содержимое блока "Популярные треды"*/
  .pop-thread{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px;
    border-radius: 5px;
    padding: 5px;
    text-align: left;
  }
  .pop-stats{
    color: #E6E6FA;
    text-align: left;
  }
  .boardname{
    color: #FFF;
  }
/**/

/*Верхнее меню со ссылками*/
  .rmenu{
    text-align: center;
    padding: 5px;
  }
/**/

/*Верхний бар*/
  #adminbar ul.menu{
    padding: 5px;
    background: transparent none repeat scroll 0% 0%;
  }
/**/
  
/*Ссылки*/
  a, a:link, a:visited{
    color: #E6E6FA;
    text-decoration: none;
  }
  .header a:hover, a:hover, .postscontainer a:hover{
    color: #FFF;
    text-decoration: none;
  }
  .a-link-emulator{
    color: #E6E6FA;
  }
  .a-link-emulator:hover{
    color: #FFF;
  }
  .reply-label{
    color: #E6E6FA;
  }
  .reply-label:hover{
    color: #FFF;
  }
  .logo{
    color: #FFF;
  }
/**/
  
  .postscontainer{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    border-radius: 5px;
    text-align: center;
  }
  
  .reply{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    border-radius: 5px;
    padding: 5px;
  }
  .mess-post{
    color: #FFF;
  }
  .post .post-title{
    color: #FFF;
  }
  
  .hboardlist .column .boardsubj, .hdescription{
    color: #E6E6FA;
  }
  
  .welcomefriend{
    color: #E6E6FA;
  }
  
  /*Гринтекст*/
  .unkfunc{
    color: #8660D7;
  }
  
  #tabbed li{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    border-bottom: 0px;
  }
  #tabbed li.ui-tabs-active a{
    color: #E6E6FA;
  }
  
  .mselect{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    color: #E6E6FA;
    padding: 5px;
    border-radius: 5px;
  }
  
  .dropd-board-btn{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    padding: 5px;
    border-radius: 5px;
  }
  
  hr{
    border-width: 0px;
  }
  
  span.spoiler, span.spoiler a{
    color: transparent;
    background: transparent none repeat scroll 0% 0%;
    text-shadow: 0px 3px 3px #191970, 0px 1px 4px #111, 1px -1px 5px #191970;
    transition: all 0.5s ease 0s;
    
  }
  span.spoiler:hover{
    color: #E6E6FA;
    text-shadow: 0px 0px 0px #191970, 0px 0px 0px #111, 0px 0px 0px #191970;
  }
  
  .dropd-board-list ul{
    background: transparent none repeat scroll 0% 0%;
    border: 0px solid #191970;
    width: 300px;
    right: 0px;
    left: 0px;
    padding: 5px;
    border-radius: 5px;
  }
  .dropd-board-list{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    width: 300px;
    right: 0px;
    left: 0px;
    padding: 5px;
    border-radius: 5px;
  }
  .dropd-board-list a, .dropd-board-list a:hover{
    background: transparent none repeat scroll 0% 0%;
    border: 2px transparent;
    color: #FFF;
    width: 200px;
    right: 0px;
    left: 0px;
    padding: 5px;
    border-radius: 5px;
  }
  
  .boardstats{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    border-bottom: 0px;
    right: -100000px;
  }
  
  .thread{
    background: transparent none repeat scroll 0% 0%;
    padding: 5px;
  }
/*Вид тредов в каталоге*/  
  .thread_wrap{
    background: #1C2A62 none repeat scroll 0% 0%;
    border-bottom: 0px;
    border: 0px solid #191970;
    border-radius: 5px;
    padding: 5px;
  }
  
  .thread_wrap .comment {
    background: #1C2A62 none repeat scroll 0% 0%;
    padding: 5px;
    
  }
  
  .thread_wrap.expandble:hover .thread{
    background: #1C2A62 none repeat scroll 0% 0%;
    padding: 10px;
    border: 0px solid #191970;
    border-radius: 5px;
  }
  
  .thread_wrap::after {
    content: "";
    position: absolute;
    width: 100%;
    height: 30px;
    bottom: 0px;
    left: 0px;
    background: none repeat scroll 0 0 #1C2A62 !important;
  }
/**/  
  .oppost-wrapper{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    border-radius: 5px;
    padding: 5px;
  }

/*Переключатель стилей*/
  .style-switcher{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px solid #191970;
    color: #E6E6FA;
    padding: 5px;
    border-radius: 5px;
    visibility: hidden;
  }
/**/
  
/*Страницы*/
  .pager{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px;
    border-radius: 5px;
    padding: 5px;
  }
/**/
  
  /*Mobile Version*/
  .menu{
    background: #2D4689 none repeat scroll 0% 0%;
    border: 0px;
    position: relative;
  }
  .button-mob{
    background: #2D4689 none repeat scroll 0% 0%;
    border: 0px;
    color: #E6E6FA;
  }
  .post .post-details{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px;
  }
  .oppost{
    background: #1C2A62 none repeat scroll 0% 0%;
  }
  .oppost-options-mob{
    background: #1C2A62 none repeat scroll 0% 0%;
    border: 0px;
  }
  input[type="text"], textarea{
    background-color: #202E66;
    color: #FFF;
    border: 0px;
    padding: 5px;
    border-radius: 5px;
  }
  #postform td #submit{
    background: #2D4689 none repeat scroll 0% 0%;
    border: 0px;
    color: #E6E6FA;
  }
  
}
