css:
@-moz-document domain("dkriesel.com") {
body,.main,.page,.sidebar{
    background-color	: #111111 !important;
    color				: #eeeeee !important;
}

input{
	background-color    : #333333 !important;
    color				: #eeeeee !important;
}

    
h3,h2{
    background-color	: #333333 !important;
    color				: #ffffff !important;
}

h4{
    color				: #dddddd !important;
}

blockquote{
	border				: 1px solid #5A66FF !important;
    background-color	: #1E2042 !important;
}

div.dokuwiki a:hover, div.dokuwiki a:active {
    color				: #FFFFFF !important;
}
.header{
    position			: fixed !important;
    top					: 0px !important;
    left				: 0px !important;
    right				: 0px !important;
    bottom				: 97% !important;
    z-index				: 1;
}

div.plugin_translation {
    font-size			: 70% !important;
}

div.dokuwiki .logo {
    font-size			: 100% !important;
    margin-top			: 0px !important;
}

#minima__wide_right .header{
    padding				: 10px 17px 0px 46px !important;
}

div.dokuwiki .header {
    height				: 25px !important;
}

body{
    top					: -200px !important;
}

h3, h4{
    background-image	: none !important;
    text-shadow			: 0 0 2px white !important;
    border-radius		: 5px !important;
    
}

h2{
    background-image	: none !important;
    text-shadow			: 0 0 2px white !important;
    border-radius		: 10px;
    
}

h1{
    color               : #ffffff !important;
    width				: 85% !important;
    left				: -20% !important;
    background-image	: none !important;
    text-shadow			: 0 0 2px white !important;
    border-radius		: 3px !important;
    
}

div.dokuwiki h1 a, div.dokuwiki h2 a, div.dokuwiki h3 a, div.dokuwiki h4 a, div.dokuwiki h5 a, div.dokuwiki a.nolink {
    color				: #ffffff !important;
    text-decoration		: none !important;
}

div.dokuwiki{
	width				: 100% !important; 
   	bottom				: -10px !important;
    top					: -20px !important;
    position			: absolute !important;
}

.footerinc{
    display				: none !important;
}

img{
    border-radius 		: 15px !important;
}

.wikilink1{
    background-color	: transparent !important;
}

li .level1, li,.li, em{
    color				: #D49957 !important;
}

table{
    color				: #222222 !important;
}

#minima__wide_right div.page {
    position:absolute;
    top: 50px;
    left: 0px;
    width: auto;
    right: 20px;
    min-width: 300px !important;
}

.level1{
    width: 100%;
}

.sidebar{
    display: none;
    width: 200px;
    right: 35%;
    top:  70px;
    left: 35%;
    margin-top: 30px !important;
}
}
