css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.median-xl.com") {

a {
    color: #ffffff                                                      !important;
}

a:hover {
    color: #b60505                                                      !important;
}

a.button1, input.button1, input.button3, a.button2, input.button2 {
    color: #ffffff                                                      !important;
    background-color: #424242                                           !important;
    background-image: none                                              !important;
}

a.button1:hover, input.button1:hover, a.button2:hover, input.button2:hover, input.button3:hover {
    border-color: #d23636                                               !important;
    color: #d23636                                                      !important;
}


.abbc3_button {
    background-color: #cccccc                                           !important;
}

.abbc3_button:hover {
    border: 1px solid #242424                                           !important;
}

.abbc3_buttons_row {
    background-color: #cccccc                                           !important;
    border: 0px                                                         !important;
    border-radius: 2px                                                  !important;
}

.bg1 {
    background-color: #0a0a0a                                           !important;
    border-color: #1e1e1b #27251d #3a3a34                               !important;
    box-shadow: 0px 0px 6px 0px rgba(40, 40, 40, 1)                     !important;
    -moz-box-shadow: 0px 0px 6px 0px rgba(40, 40, 40, 1)                !important;
    -webkit-box-shadow: 0px 0px 6px 0px rgba(40, 40, 40, 1)             !important;
}

.bg2 {
    background-color: #0c0c0c                                           !important;
    border-color: #1e1e1b #27251d #3a3a34                               !important;
    box-shadow: 0px 0px 6px 0px rgba(46, 46, 46, 1)                     !important;
    -moz-box-shadow: 0px 0px 6px 0px rgba(46, 46, 46, 1)                !important;
    -webkit-box-shadow: 0px 0px 6px 0px rgba(46, 46, 46, 1)             !important;
}

blockquote {
    background-color: #141522                                           !important;
    border-color:#242431                                                !important;
}

body, html {
    color: #825353                                                     !important;
    background: #161512                                                !important;
}

.button {
    border-color: #383c40                                              !important;
    background-color: #999999                                          !important;
    background-image: -moz-linear-gradient(top, #000000, #161616)      !important;
    background-image: -webkit-linear-gradient(top, #000000, #161616)   !important;
    background-image: -o-linear-gradient(top, #000000, #161616)        !important;
    background-image: linear-gradient(to bottom, #000000, #161616)     !important;
    -ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorStr='#000000', EndColorStr='#161616')"                !important;
    box-shadow: 0 0 0 1px #000000 inset                                !important;
    -webkit-box-shadow: 0 0 0 1px #000000 inset                        !important;
    color: #D31141                                                     !important;
}

.button:hover, .dropdown-visible .dropdown-select,
.nojs .dropdown-container:hover .dropdown-select {
    border-color: #AB0A0A                                              !important;
    background-image: -moz-linear-gradient(top, #161616, #000000)      !important;
    background-image: -webkit-linear-gradient(top, #161616, #000000)   !important;
    background-image: -o-linear-gradient(top, #161616, #000000)        !important;
    background-image: linear-gradient(to bottom, #161616, #000000)     !important;
    -ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorStr='#161616', EndColorStr='#000000')"                !important;
    text-shadow: 1px 1px 0 #000000, -1px -1px 0 #000000, -1px -1px 0 rgba(188, 42, 77, 0.2)                                 !important;
}

#cat26 ul.forums, #cat17 ul.forums{
    border-color: #333327 #423f34 #44443a                               !important;
    background: #0c0c0c                                                 !important;
}

#cat26 ul.forums a{
    border: 1px solid #ffffff                                           !important;
}

code {
    background-color: #000000                                           !important;
    color: #2e8b57                                                      !important;
    border: 1px solid #362d27                                           !important;
}

.codebox {
    background-color: #000000                                           !important;
    border-color: #272c36                                               !important;
}

.codebox p {
    border-bottom-color:  #333333                                       !important;
}

.content {
    color: #cccccc                                                      !important;
}

.content h2, .panel h2 {
    color: #981111                                                      !important;
    border-bottom-color:  #333333                                       !important;
}

dd label {
    color: #cccccc                                                      !important;
}

dl.thumbnail dt a:hover {
    background-color: #0c0c0c                                           !important;
}

.donate-comment {
    background: none repeat scroll 0% 0% #121212                        !important;
    border-color: #262622 #292720 #42423c                               !important;
}

.dropdown .dropdown-contents {
    background: #000000                                                 !important;
    border-color: #464646                                               !important;
    box-shadow: 1px 3px 5px rgba(255, 255, 255, 0.2)                    !important;
}

.dropdown .pointer-inner {
    border-color: #000000 transparent                                   !important;
}

.dropdown-extended .header {
    background: #000e0e;
    background: -moz-linear-gradient(top, #000e0e 0%, #060606 100%)     !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #000e0e), color-stop(100%, #060606))         !important;
    background: -webkit-linear-gradient(top, #000e0e 0%, #060606 100%)  !important;
    background: -o-linear-gradient(top, #000e0e 0%, #060606 100%)       !important;
    background: -ms-linear-gradient(top, #000e0e 0%, #060606 100%)      !important;
    background: linear-gradient(to bottom, #000e0e 0%, #060606 100%)    !important;
}

.dropdown-extended .header {
    text-shadow: 1px 1px 1px #000000                                    !important;
}

.dropdown-extended .header, .dropdown-extended .footer {
    border-color: #464646                                               !important;
    color: #ffffff                                                      !important;
}

.dropdown-extended ul li {
    border-top-color: #464646                                           !important;
}

.dropdown-extended ul li:hover {
    background-color: #091e30                                           !important;
    color: #ffffff                                                      !important;
}

fieldset dl:hover dt label {
    color: #ffffff                                                      !important;
}

#flag-container {
    background-color: #424242                                           !important;
    border: 0px                                                         !important;
    border-radius: 2px                                                  !important;
    color: #d23636                                                      !important;
}

.forabg, .forumbg {
    background: #cfcfcf                                                 !important;
    border-color: #3a3934 #42423d #504f4a                               !important;
    box-shadow: 0px 0px 6px 0px rgba(44, 44, 44, 1)                     !important;
    -moz-box-shadow: 0px 0px 6px 0px rgba(44, 44, 44, 1)                !important;
    -webkit-box-shadow: 0px 0px 6px 0px rgba(44, 44, 44, 1)             !important;
}

.forabg ul.topiclist, .forumbg ul.topiclist {
    border-bottom: 2px solid #575757                                    !important;
}

.forabg ul.forums, .forumbg ul.forums {
    border-bottom-width: 0px                                            !important;
}

.forumbg .header a, .forabg .header a, th a {
    color: #000000                                                      !important;
}

.hidden {
    border: 1px dashed #3a3a3a                                          !important;
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.81)            !important;
}

/* Where is this used? This is the default color
h1 {
    color: #ffffff;
}*/

h2 {
    color: #d7cec0                                                      !important;
}

h3 {
    border-bottom-color: #333333                                        !important;
    color: #CF0707                                                      !important;
}

hr {
    border-color: #000000                                               !important;
    border-top-color: #333333                                           !important;
}

input {
    background-color: #424242                                           !important;
    border: 0px                                                         !important;
    border-radius: 2px                                                  !important;
    color: #cccccc                                                      !important;
}

.inputbox {
    background-color: #424242                                           !important;
    border-color: #3f4b4b                                               !important;
    color: #cccccc                                                      !important;
}

.inputbox.search.tiny {
    border-top-right-radius: 0px                                        !important;
    border-bottom-right-radius: 0px                                     !important;
}

li.row {
    border-top-color:  #151515                                          !important;
    border-bottom-color: #656a6f                                        !important;
}

li.row strong {
    color: #ffffff                                                      !important;
}

li.row:hover {
    background-color: #131313                                           !important;
}

.navbar {
    background-color: #242424                                           !important;
    border-color: #2e2d2a #34342f #44433e                               !important;
    box-shadow: 0px 0px 6px 0px rgba(44, 44, 44, 1)                     !important;
    -moz-box-shadow: 0px 0px 6px 0px rgba(44, 44, 44, 1)                !important;
    -webkit-box-shadow: 0px 0px 6px 0px rgba(44, 44, 44, 1)             !important;
}

#navigation a {
    color: #cccccc                                                      !important;
    background: #242424                                                 !important;
    background: -moz-linear-gradient(left, #242424 50%, #060606 100%)   !important;
    background: -webkit-gradient(left top, right top, color-stop(50%, #242424), color-stop(100%, #060606))                  !important;
    background: -webkit-linear-gradient(left, #242424 50%, #060606 100%)!important;
    background: -o-linear-gradient(left, #242424 50%, #060606 100%)     !important;
    background: -ms-linear-gradient(left, #242424 50%, #060606 100%)    !important;
    background: linear-gradient(to right, #242424 50%, #060606 100%)    !important;
}

#navigation a:hover {
    background: #060606                                                 !important;
    color: #BC2A4D                                                      !important;
}

#navigation #active-subsection a {
    background: #060606                                                 !important;
    color: #D31141                                                      !important;
}

.nfo {
    color: #ffffff                                                      !important;
    background-color: #000000                                           !important;
}

.panel {
    color: #d7cec0                                                      !important;
    background-color: #0c0c0c                                           !important;
    border-color: #1a1916 #201f1b #2f2e2a                               !important;
    box-shadow: 0px 0px 6px 0px rgba(46, 46, 46, 1)                     !important;
    -moz-box-shadow: 0px 0px 6px 0px rgba(46, 46, 46, 1)                !important;
    -webkit-box-shadow: 0px 0px 6px 0px rgba(46, 46, 46, 1)             !important;
}

.pagination li a {
    color: #8c7a5c                                                      !important;
    background-color: #131211                                           !important;
    border-color: #3f4b4b                                               !important;
}

.pagination li a:hover,
.pagination .dropdown-visible a.dropdown-trigger,
.nojs .pagination .dropdown-container:hover a.dropdown-trigger {
    border-color: #D23636                                               !important;
    background-color: #D23636                                           !important;
    color: #000000                                                      !important;
}

.pagination li.active span {
    color: #000000                                                      !important;
    background-color: #C53737                                           !important;
    border-color: #C53737                                               !important;
}

.phpbb_alert {
    border-color: #666666                                               !important;
    border-radius: 5px                                                  !important;
    background-color: #000000                                           !important;

}

.postbody {
    color: #cccccc                                                      !important;
}

.postprofile {
    color: #999999                                                      !important;
    border-color: #000000                                               !important;
}

.postprofile strong {
    color: #ffffff                                                      !important;
}

.scroll-btn {
    border: 2px solid #ffffff                                           !important;
    background-color: #000000                                           !important;
}

.scroll-btn-arrow {
    color: #ffffff                                                      !important;
}

.search-header {
    box-shadow: 0 0 10px #000000                                        !important;
}

select {
    border: 0px                                                         !important;
    border-radius: 2px                                                  !important;
    background-color: #424242                                           !important;
    color: #ffffff                                                      !important;
}

.spoilbtn {
    color: #cccccc                                                      !important;
}

.spoilcontent {
    color: #cccccc                                                      !important;
    border-top: 1px solid #333333                                       !important;
}

.spoilwrapper {
    border: 1px solid #242424                                           !important;
    color: #cccccc                                                      !important;
    background-color: #000000                                           !important;
}

table.table1 thead th {
    color: #000000                                                      !important;
}

#tabs .tab > a {
    background: #282828                                                 !important;
    color: #A80505                                                      !important;
}

#tabs .tab > a:hover {
    background: #222222                                                 !important;
    color: #D31141                                                      !important;
}

#tabs .activetab > a,
#tabs .activetab > a:hover {
    background: #060606                                                 !important;
    background: -moz-linear-gradient(top, #1a1a1a 0%, #060606 100%)     !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #1a1a1a), color-stop(100%, #060606))         !important;
    background: -webkit-linear-gradient(top, #1a1a1a 0%, #060606 100%)  !important;
    background: -o-linear-gradient(top, #1a1a1a 0%, #060606 100%)       !important;
    background: -ms-linear-gradient(top, #1a1a1a 0%, #060606 100%)      !important;
    background: linear-gradient(to bottom, #1a1a1a 0%, #060606 100%)    !important;
    border-color: #060606                                               !important;
    box-shadow: 0 1px 1px #0d0600 inset                                 !important;
    color: #cccccc                                                      !important;
}

textarea {
    background-color: #424242                                           !important;
    border: 0px                                                         !important;
    border-radius: 2px                                                  !important;
    color: #cccccc                                                      !important;
}

#ucp ul.topiclist li.header  dl.icon dt,
#ucp ul.topiclist li.header  dl.icon dd {
    color: #a7a7a6                                                      !important;
}

ul.forums {
    background-color: #cfcfcf                                           !important;
}

ul.forums dt a {
    color: #a61616                                                      !important;
}

ul.forums li {
    color: #774C4C                                                      !important;
    border-color: #333327 #423f34 #44443a                               !important;
    background: #0c0c0c                                                 !important;
}

ul.topiclist li.header a, ul.topiclist li.header dl.icon dt {
    color: #25231a                                                      !important;
}

ul.topiclist li.header dl.icon dd, ul.topiclist li.header dl.icon dt {
    color: #25231a                                                      !important;
}

#wrap {
    background-color: #161512                                           !important;
    border-color: #4a4a4a                                               !important;
}

}
