css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.gamestar.de/community/gspinboard/") {
    body > div.centeredDiv > div:first-child {
        display: none;
    }

    body > div.centeredDiv > div:nth-child(2) {
        display: none;
    }

    #path_date {
        position: relative;
        width: 980px !important;
        height: 32px !important;
        margin-bottom: 10px !important;
        padding: 0 !important;

        background: #596dc3; /* Old browsers */
        background:    -moz-linear-gradient(top, #596dc3, #384f7a); /* FF3.6+ */
        background: -webkit-linear-gradient(top, #596dc3, #384f7a); /* Chrome10+,Safari5.1+ */
        background:      -o-linear-gradient(top, #596dc3, #384f7a); /* Opera 11.10+ */
        background:     -ms-linear-gradient(top, #596dc3, #384f7a); /* IE10+ */
        background:   linear-gradient(to bottom, #596dc3, #384f7a); /* W3C */
        border-radius: 5px;

        overflow: hidden;
        z-index: 2;
    }

    #path_date > .fll {
        display: none;
    }

    #path_top {
        position: relative;
        width: 1070px !important;
        height: 32px;
        left: -90px;

        color: transparent !important;
        font-size: 12px !important;
        white-space: nowrap;

        overflow: hidden;
    }

    #path_top a {
        position: relative;
        top: 9px;
        padding: 9px 0px 9px 32px;

        color: white !important;
        font-weight: bold;
        white-space: nowrap;

        background-image: url('data:image/png;base64,');
        background-position: 1px 0px;
        background-repeat: no-repeat;
    }

    #path_top a:first-child {
        border-top-left-radius: 5px;
        border-bottom-left-radius: 5px;
    }

    #path_top a:last-child {
        border-top-right-radius: 5px;
        border-bottom-right-radius: 5px;
    }

    #date {
        display: none;
    }
}
