css:
@-moz-document domain("login.yahoo.com"){
  #mbr-login-ad-container{
    display: none;
  }
  .mbr-login-c.pure-g{
    width:275px;
    min-width:275px;
    margin-top: -61px;
    padding-top: calc( (( 100vh - 475px ) * 3) / 7 );
  }
  .mbr-login-c-right{
    position: relative;
    top: 0;
    padding-bottom: 0;
    width:275px;
    right:auto;
  }
  .mbr-login-box .mbr-login-form .mbr-overflow-ellipsis {
    width: auto;
  }
  #mbr-login-box{
    position: static;
  }
  .mbr-login-help-links.mbr-text-align.mbr-login-text-normal{
    min-height: unset;
  }
  #mbr-header{
    background-color: transparent;
  }
  body, #UH {
    min-width: 840px;
  }
  #mbr-footer{
    background-color: transparent;
    border:none;
    box-shadow:  unset;
  }
  #yucs-help_link{
    margin-right: 8px;
  }
  footer a{
    color: #400090 !important;
  }
  #mbr-login-contents-container{
    display: none !important;
  }
}



@-moz-document domain("mail.yahoo.com"), domain("calendar.yahoo.com"){
  #shellcontent,
  .wide-right-rail #shellcontent {
    right: 0;
  }
  .withoutad #main {
    margin-right: 0;
  }
  #UHSearchWeb,
  #masterNav,
  #web-search-btn,
  #storm-listnav ~ div{
    display:none;
  }
  #UHSearchProperty[value="Search Calendar"]{
    margin-right: 50px !important;
  }
  #mail-search-btn{
    margin-right: 50px;
    background-color: #32007F ;
    border-radius: 3px;
    box-shadow: 1px 2px 0 0 rgba(64,64,64,.5);
  }
  #mail-search-btn:hover,
  #mail-search-btn:focus,
  #mail-search-btn:active{
    background-color: #6c6c6c;
    background-color: #3a0887 ;
    background-color: #3e0c8b ;
  }
  #uhNavWrapper #yucs-home_link i.Ycon.Fz\(22px\){
    font-size: 18px;
  }
  #uhNavWrapper #yucs-help_button i.Ycon.Fz\(22px\){
    font-size: 20px;
    vertical-align: -7px;
  }
  #newsfeed-filterbar{
    width:100%!important;
  }
  .newsfeed-offset {
    top: 82px;
  }
  #Eyebrow{
    height: 0;
  }

  #main{
    top: 50px;
  }
  #UHSearch{
    max-width: 900px;
    max-width: 2000px;
  }
  #uhWrapper{
    margin-top: 10px;
  }
  button[aria-label="Refine"],
  button[aria-label="Scroll to top"]{
    opacity: .4375;
  }
  .icon.icon-back-to-top.scroll-to-top{
    opacity: .4375;
  }

  button[aria-label="Refine"]:hover,
  button[aria-label="Refine"]:focus,
  button[aria-label="Refine"]:active,
  button[aria-label="Scroll to top"]:hover,
  button[aria-label="Scroll to top"]:focus,
  button[aria-label="Scroll to top"]:active,
  .icon.icon-back-to-top.scroll-to-top:hover,
  .icon.icon-back-to-top.scroll-to-top:focus,
  .icon.icon-back-to-top.scroll-to-top:active{
    opacity: .875;
  }
  .icon.icon-search.scroll-to-search{
    opacity: .4375;
  }
  .icon.icon-search.scroll-to-search:hover,
  .icon.icon-search.scroll-to-search:focus
  .icon.icon-search.scroll-to-search:active,
  .icon.icon-search.scroll-to-search.wide{
    opacity: .875;
  }
  #spritzAd{}
  #theAd,
  #slot_MB{
    display: none
  }
  .newsfeed-mb-ad,
  .search-message-container-ad,
  #shellinner .adlink,
  #shellinner .mb-list-ad{
    display:none!important;
  }
  
  body, #UH {
    min-width: 720px;
  }
  /* Calendar */
  body{
    min-height: 360px;
  }
  #shellnavAd{
    display: none
  }
  #shellnavigation.hasAd #calList {
    bottom: 23px;
    bottom: 0
  }
  
  /****/
  .thread-content .fixed .info {
    left: 55px;
  }
  .thread-content .flex {
    margin-left: 77px;
  }
  .vp-off .flex .name-list {
    max-width: 100%;
  }
  .flex .first{
    display: block;
    white-space: nowrap;
    text-overflow: Ellipsis;
    overflow-y: hidden;
    max-width:100%;
  }
  .thread-content .list-view-item.to1.flagged:hover .from, .thread-content .list-view-item.to1:hover .from {
    padding-right: 65px;
  }
  .thread-content .list-view-item:hover .from, .thread-content .list-view-item .from {
    padding-right: 25px;
  }
  .action-toolbar {
    padding-right: 0;
    word-spacing: -2px;
  }
  .list-view-item .info-unread, .list-view-item .info-hover, .list-view-item .action-toolbar .icon {
    transition: unset;
  }
  .vp-off .flex .from, .vp-off .flex .title {
    right: 75%;
  }
  .vp-off .flex .subj {
    width: 75%;
    margin-left: 25%;
  }
  
  .message-subject {
    width: calc(100% - 419px + 25px);
  }
  .message-folder{
    width: calc(115px - 25px);
  }

  
  /**/
  /* Contacts */
  #contacts-content div.scroll-inner.home-landing-page{
    width: calc(100% - 25px);
    top: 60px;
  }
  
  
  /* Basic Mail */
  .uh-srch-btn[name="srchWeb"]{
    display: none
  }
  td.rightRailAdContainer,
  div.left_mb,
  div.mb{
    display: none;
  }
  .with-ads .layoutfixer .c3 {
    width: 0;
  }
  form[name="showFolderForm"] {
    overflow-y: scroll;
    height: calc(100vh - 180px - 34px);
    margin-top: 34px;
  }
  .contentnav.top.clearfix{
    position: absolute;
    top:35px;
    width: calc(100% - 220px);
    box-sizing: border-box;
  }
  html body > img{
    visibility: hidden;
    display: none;
  }
  #datatable tbody{
  }
  /***************/
  html body {
    min-height: 400px;
  }
  .contactlist{
    height: calc(100vh - (768px - 481px));
  }
  .navigation .container{
    height: calc(100vh - 180px);
    overflow-y: auto;
    overflow-x: auto;
  }

}

@-moz-document domain("yahoo.com"), domain("yimg.com"){
  #masthead .main-row-wrapper {
    padding: 12px 0 11px;
  }
  .type_tabbar {
    top: 15px;
  }
  /**
  #nav-col{
    top:5px;
  }
  .fixed-enabled .sticky{
    top: 83px !important;
  }**/
  /**/
  html#Stencil #UH, #Stencil #UH > #header {
    height: 85px;
  }
  #UH{
    transition-delay: 0s;
    transition-duration: .1s;
    transition-timing-function: step-start;
    transition-property:  border;
  }
  #ad-north-base, .fpad,
  .type_livenationpromo,
  .yom-ad,
  .ad-active,
  .media-native-ad,
  #yucs-trending-now,
  div.bd.type_trendingnow.type_trendingnow_default,
  div.bd.type_trendingnowcarousel.type_trendingnowcarousel_default,
  div.bd.type_ads.type_ads_default,
  div.fdb_close_els,
  div.CAN_ad,
  .content.rmx-ad,
  .content[data-reason="TRENDING"],
  /* .featured, */
  .featured-ads{
    display: none !important;
  }
  /**/

}
