css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  /**************
  * GENERAL CSS *
  **************/
  
  body {
    background: url("http://i.imgur.com/2pl0G0K.jpgp") !important;
    background-size: cover;
    font-family: "Linkin Park", sans-serif;
  }

  button {
    font-family: "Linkin Park", sans-serif;
  }

  .ps-room.ps-room-light {
    background: transparent;
    border-left: #000;
    color: #6C96C0;
  }
  
  .textbox, .setchart input, .setchart-nickname input, .statform input.inputform, .setstats, .setdetails, .pm-log-add .textbox {
    transition:All 1.0116s ease;    
    border-radius: 0px;
    background: #333;
    box-shadow: none;
    color: #999;
    border: 1px solid #333;
  }

  .textbox:hover, .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .setstats:hover, .setdetails:hover {
    border-radius: 0px;
    background: #333;
    box-shadow: none;
    color: #EEE;
    border: 1px solid #333;
  }

  .textbox:focus, .textbox:focus:hover, .setchart input:focus, .setchart-nickname input:focus, .statform input.inputform:focus, .setstats:focus, .setdetails:focus {
    transition:All 1.0116s ease;  
    border-radius: 0px;
    background: #333;
    box-shadow: none;
    color: #EEE;
    border: 1px solid #333;
  }
  
  .closebutton:hover {
    color: #F68383;
  }

  .minimizebutton:hover {
    color: #F7F2A6;
  }

  .closebutton, .minimizebutton {
    color: #ecf0f1;
    box-shadow: none;
  }
  
  /*******************
  * GENERAL CSS ~end *
  *******************/
  
  /************
  * POPUP CSS *
  ************/
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.3);
  }
  
  .ps-popup {
    background: #2B2B2B;
    border-radius: 0px;
    box-shadow: none;
    border: 1px solid #474747;
    color: #EFEFEF;
  }

  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #474747;
  }
  
  .ps-popup b {
    color: #FFF !important;
  }

  .ps-popup a {
    color: #6C96C0;
  }

  .ps-popup a:hover {
    color: #4C6986;
  }

  .ps-popup button {
    background-color: #808080;
    border: none;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    color: #FFF;
    padding: 3px;
  }

  .ps-popup button:hover {
    background-color: #808080;
    border: 2px solid #C0C0C0;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    color: #FFF;
    padding: 1px;
  }

  .popupmenu h3, .credits h2 {
    color: #6C96C0;
    text-shadow: 0px 0px 2px #111;
  }

  .popupmenu button {
    color: #DDD;
    border-radius: 0px;
    background: transparent;
    padding: 1px 4px;
  }

  .popupmenu button.sel {
    border: 1px solid #474747;
    border-radius: 0px;
  }

  .popupmenu button:hover, .popupmenu button.sel:hover {
    background: #474747;
    border: 1px solid #888888;
    padding: 0 3px;
  }

  a.subtle, .credits ul li p {
    color: #BBB;
  }

  .credits li small {
    color: #777;
  }

  p.or {
    color: #CCC;
  }
  
  /*****************
  * POPUP CSS ~end *
  *****************/
  
  /*************
  * HEADER CSS *
  *************/
  
  .header {
    background: rgba(0, 0, 0, 0.90);
    height: 56px;
  }

  .userbar .username {
    text-shadow: 0px 0px 3px #000;
  }

  .userbar button.icon {
    border: none;
    background: transparent;
    color: #EEE;
  }

  .userbar button.icon:hover {
    color: #6C96C0;
  }
  
  .tabbar .closebutton, .tabbar a.cur+.closebutton {
    top: 19px;
  }

  .maintabbar .inner {
    padding-top:0px;
    height: 56px;
  }

  .tabbar.maintabbar {
    margin-left: 170px;
    margin-right: 0px;
    overflow: hidden;
    height: 60px;
    background-position: right;
    background-repeat: no-repeat;
  }
  
  .tabbar a.button.notifying {
    border: 1px solid #C15A00;
    background: #F0994C;
    text-shadow: 0px -1px 0px #C15A00;
    color: #FFF;
  }
  
  .tabbar a.button.notifying:hover {
    border: 1px solid #AB5000;
    background: #E98935;
    text-shadow: 0px -1px 0px #AB5000;
    color: #FFF;
  }
  
  .tabbar a.button.subtle-notifying {
    background: #376C8E;
    color: #132E4B;
    text-shadow: none;
    border: 1px solid #132E4B;
  }
  
  .tabbar a.button.subtle-notifying:hover {
    background: #132E4B;
    color: #000;
    text-shadow: none;
  }

  .tabbar a.button.cur, .tabbar a.button.cur:hover, .tabbar a.button {
    box-shadow: none;
    border: none;
    cursor: default;
    top: 1px;
    border-radius: 0px;
    text-shadow: none;
    margin-left: 5px;
    margin-top:15px;
  }

  .tabbar a.button.cur {
    background: #132E4B;
    box-shadow: none;
    border: 1px solid #132E4B;
    cursor: default;
    top: 0px;
    border-radius: 0px;
    text-shadow: none;
    margin-left: 5px;
    margin-top:15px;
    color: #FFF;
    text-shadow: 0px -1px 0px #132E4B;
  }

  .tabbar a.button.cur:hover {
    background: #132E4B;
    box-shadow: none;
    border: 1px solid #132E4B;
    cursor: default;
    top: 0px;
    border-radius: 0px;
    text-shadow: none;
    margin-left: 5px;
    margin-top:15px;
    color: #FFF;
    text-shadow: 0px -1px 0px #132E4B;
  }

  .tabbar a.button {
    background: #376C8E;
    color: #FFF;
    text-shadow: 0px 0px 3px #000;
    border: 1px solid #132E4B;
  }

  .tabbar a.button:hover {
    background: #132E4B;
    color: #FFF;
    text-shadow: 0px 0px 3px #132E4B;
  }

  .maintabbarbottom {
    background: linear-gradient(to bottom, rgba(0,0,0,.4) 0%, rgba(0,0,0,0) 100%);
    border: none;
    box-shadow: none;
    z-index:2;
  }

  .tabbar li:last-child a.button {
    border-top-right-radius: 0px;
  }

  .tabbar li:first-child a.button {
    border-top-left-radius: 0px;
  }

  .tabbar li:first-child a.button:hover {
    margin-left: 6px;
  }
  
  /******************
  * HEADER CSS ~end *
  ******************/
  
  /***********
  * CHAT CSS *
  ***********/
  
  .chat-log {
    background: #EEE;
    background-size: cover;
    color: #000 !important;
  }
  
  .message .utilichart .abilitycol, .message .utilichart .labelcol em, .message .utilichart .widelabelcol em, .message .utilichart .pplabelcol em, .message .utilichart .statcol em, .message .utilichart .bstcol em, .message .utilichart .statcol, .message .utilichart .bstcol, .message .utilichart .labelcol, .message .utilichart .widelabelcol, .message .utilichart .pplabelcol, .message .utilichart .numcol {
    color: #000;
  }

  .infobox {
    border: 1px solid #000;
  }

  .chat-log .chat small {
    color: #000;
  }

  .chat-log-add {
    background: rgba(0, 0, 0, 0.85);
    border-top: 1px solid #000;
  }

  .chat-log-add .chatbox label {
    margin-top: 3px;
  }
  
  .userlist {
    border-right: 1px solid #000;
    color: #FFF;
    text-shadow: 0px 0px 1px #000;
    background: transparent;
  }

  .userlist li {
    border-bottom: 1px solid #000;
    background: transparent;
  }

  .userlist li button {
    border-left: 1px solid #000;
    background: rgba(0, 0, 0, 0.85);
    text-shadow: 0px 0px 1px #000;
  }

  .userlist li button:hover {
    border-left: 1px solid #000;
    background: rgba(235, 235, 235, 0.85);
    text-shadow: none;
  }

  .userlist li em.group {
    color: #777;
  }
  
  /****************
  * CHAT CSS ~end *
  ****************/
  
  /****************
  * MENUGROUP CSS *
  ****************/
  
  .menugroup {
    background: rgba(0, 0, 0, 0.85);
    border-radius: 0px;
    color: #FFF;
    text-shadow: 0px 0px 2px #000;
  }

  .menugroup button, .select, .select:disabled, .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .rightmenu .menugroup .button {
    background: #393939;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
    font-family: "Linkin Park", sans-serif;
  }

  .menugroup button:hover, .select:hover, .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover, .rightmenu .menugroup .button:hover {
    background: #4C4C4C;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
  }

  .select.preselected, .select.preselected:disabled {
    color: #DDD;
  }

  .select::after {
    color: #6C96C0;
  }
  
  /*********************
  * MENUGROUP CSS ~end *
  *********************/
  
  /*********
  * PM CSS *
  *********/
  
  .pm-log, .pm-window.focused .pm-log {
    border-color: #000;
    background: #EEE;
    color: #000 !important;
  }

  .pm-log a {
    color: #36618B;
  }
  
  .message-pm {
    color: #132E4B;
  }

  div[style="font-size:9pt;padding:1px 10px;border-top:1px solid #AAAAAA"] {
    border-top: 1px solid #000 !important;
    border-bottom: 1px solid #000;
  }

  .challenge {
    border-color: #597C9F;
    box-shadow: 0px 0px 2px #597C9F;
    background: #6F9CC9;
    color: #FFF;
    text-shadow: 0px -1px 0px #597C9F;
  }

  .challenge .buttonbar button {
    border: 1px solid #4B6783;
    box-shadow: 0px 0px 2px #4B6783;
    background: #6085AB;
    color: #FFF;
    text-shadow: 0px -1px 0px #4B6783;
    padding: 3px;
  }

  .challenge .buttonbar button:hover {
    border: 1px solid #3A4F64;
    box-shadow: 0px 0px 2px #3A4F64;
    background: #516F8E;
    color: #FFF;
    text-shadow: 0px -1px 0px #3A4F64;
    padding: 3px;
  }

  .pm-window h3 {
    background: rgba(0, 0, 0, 0.85);
    border-color: #000;
    color: #DDD;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
  }

  .pm-window h3:hover {
    color: #6C96C0;
    background: rgba(0, 0, 0, 0.85);
    border-color: #000;
  }
  
  .pm-window h3.pm-minimized {
    border-radius: 0px;
  }

  .pm-window.focused h3, .pm-window.focused h3:hover {
    background: rgba(0, 0, 0, 0.85);
    border-color: #000;
    color: #FFF;
  }

  .pm-window h3 small, .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #AAA;
  }

  .pm-window h3 .closebutton:hover {
    color: #FF7979;
  }

  .pm-window h3 .minimizebutton:hover {
    color: #FFCA79;
  }

  .pm-log-add, .pm-window.focused .pm-log-add {
    background: rgba(0, 0, 0, 0.85);
    border-color: #000;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
  }
  
  /**************
  * PM CSS ~end *
  **************/

  /***************
  * ROOMLIST CSS *
  ***************/
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(0, 0, 0, 0.85); 
  }

  .ps-room.ps-room-light.scrollable .button {
    background: #393939;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
  }

  .ps-room.ps-room-light.scrollable .button:hover {
    background: #4C4C4C;
  }
  
  .roomlist h2 {
    text-shadow: 0px 0px 2px #000;
    color: #6C96C0;
  }

  .roomlist a.ilink {
    background: #393939;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
  }

  .roomlist a.ilink:hover {
    background: #4C4C4C;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
  }
  
  .roomlist a.ilink small {
    color: #DDD;
  }

  .roomcounters button {
    background: #808080 !important;
    border: none !important;
    border-radius: 0px !important;
    box-shadow: none !important;
    text-shadow: none !important;
    color: #FFF !important;
  }

  .roomcounters button:hover {
    background: #808080 !important;
    border: 2px solid #C0C0C0 !important;
    border-radius: 0px !important;
    box-shadow: none !important;
    text-shadow: none !important;
    color: #FFF !important;
  }
  
  /********************
  * ROOMLIST CSS ~end *
  ********************/

  /************
  * OTHER CSS *
  ************/

  .mainmenufooter a {
    transition:All 1.0116s ease;    
    transform: rotate(0deg) scale(1.05) skew(0deg) translate(0px);    
    color: #999999;
    text-shadow: none;
    text-decoration: none;
  }

  .mainmenufooter a:hover {
    color: #FFF;
    text-decoration: none;
  }

  .mainmenufooter {
    background: #333;
    bottom: 0px;
    left: 0px;
    padding: 15px;
    color: #999999;
    text-shadow: none;
    position: fixed;
    border: none;
  }
  
  .ladder th, .ladder td {
    background: #6085AB;
    color: #FFF;
    text-shadow: 0px -1px 0px #4B6783;
    padding: 3px;
  }

  .ladder table, .ladder td, .ladder th {
    border: 1px solid #4B6783;
    box-shadow: 0px 0px 2px #4B6783;
  }
  
  .tournament-title {
    background: rgba(0, 0, 0, 0.85);
  }
  
  .tournament-status, .tournament-toggle {
    background: #132E4B;
  }
  
  .tournament-tools, .tournament-tools.active {
    background: rgba(0, 0, 0, 0.85) !important;
  }

  /*****************
  * OTHER CSS ~end *
  *****************/
  
  /******************
  * TEAMBUILDER CSS *
  ******************/

  .teamchartbox {
    border: none;
  }

  .setchart, .setchart-nickname {
    border-radius: 0px;
    background: #222;
    border: 1px solid #444;
    box-shadow: 0px 0px 2px #111;
  }

  .setchart label, .setchart-nickname label {
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
  }

  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: #222;
    border: 1px solid #444;
    box-shadow: 0px 0px 2px #111;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
  }

  .teamlist .team {
    background: #393939;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
  }

  .teamlist .team:hover {
    background: #4C4C4C;
  }

  .setchart .setstats label, .setchart .setdetails label {
    color: #EEE;
    text-shadow: none;
  }

  .setstats .statrow-head em {
    color: #BBB;
  }

  .jslider .jslider-label {
    color: #FFF;
  }

  .jslider .jslider-value {
    border: 1px solid #111;
    background: #222;
    color: #FFF;
  }

  .suggested a {
    color: #6C96C0;
  }

  .suggested button {
    background: #393939;
    border-radius: 0px;
    box-shadow: none;
    text-shadow: none;
    border: none;
    border-left: 2px solid #FFF;
    color: #DDD;
  }

  .suggested button:hover {
    background: #4C4C4C;
  }

  .setmenu {
    top: 2px;
  }

  .setmenu button {
    color: #DDD;
    text-shadow: 0px 0px 2px #000;
    border-radius: 0px;
  }

  .setmenu button:hover {
    background: #222;
    border: 1px solid #444;
  }

  .teambuilder-results .firstresult a, .teambuilder-results .result a:hover {
    border-radius: 0px;
    background: #151515;
    border: 1px solid #444;
  }

  .teambuilder-results .firstresult a:hover {
    border: 1px solid #666;
  }

  .utilichart b {
    color: #6C96C0;
  }

  .setdetails .detailcell {
    border-left: 1px solid #333;
  }

  .teambar button {
    border-radius: 0px;
    background: #151515;
    border: 1px solid #444;
    color: #666;
  }

  .teambar button:hover {
    border-radius: 0px;
    background: #151515;
    border: 1px solid #666;
    color: #999;
  }

  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    border-radius: 0px;
    background: #151515;
    border: 2px solid #444;
    color: #666;
  }

  .utilichart .illegalcol em, .setchart input.incomplete {
    border: 1px solid #FF5F5F;
    color: #FF5F5F;
  }

  .utilichart .movenamecol, .utilichart .pokemonnamecol, .utilichart .namecol {
    color: #FEFEFE;
  }

  .utilichart .abilitycol, .utilichart .twoabilitycol {
    color: #E6E6E6;
  }

  .utilichart .col {
    color: #DDD;
  }

  .utilichart .typecol img {
    opacity: 0.75;
  }

  button[name="import"], .majorbutton, button[name="back"], button[name="saveImport"], button[name="delete"], button[name="edit"], button[name="newTop"], button[name="new"], button[name="backup"], button[class="savebutton"], button[name="undoDelete"] {
    background: #222;
    border: 1px solid #444;
    box-shadow: 0px 0px 2px #111;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
  }

  button[name="import"]:hover, .majorbutton:hover, button[name="back"]:hover, button[name="saveImport"]:hover, button[name="delete"]:hover, button[name="edit"]:hover, button[name="newTop"]:hover, button[name="new"]:hover, button[name="backup"]:hover, button[class="savebutton"]:hover, button[name="undoDelete"]:hover {
    background: #111;
  }
  
  /***********************
  * TEAMBUILDER CSS ~end *
  ***********************/

  /*************
  * Battle CSS *
  *************/

  .ps-room.ps-room-opaque {
    background: rgba(0, 0, 0, 0.85);
    font-family: "Linkin Park", sans-serif;
    color: #EEE;
  }
  
  .backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
  }
  
  .backdrop:before{
    display: block;
    content: "";
    background: url("http://www.drodd.com/images11/desktop-wallpaper9.jpg") bottom;
    background-size: cover;
    width: 640px;
    height: 360px;
  }

  .backdrop:after{
    display: block;
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 640px;
    height: 360px;
  }

  button[name="instantReplay"], button[name="saveReplay"], button[name="closeAndMainMenu"], button[name="closeAndRematch"], button[name="undoChoice"], button[name="setTimer"]/*, button[], button[]*/ {
    background: #222;
    border: 1px solid #444;
    box-shadow: 0px 0px 2px #111;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    padding: 3px;
  }

  button[name="instantReplay"]:hover, button[name="saveReplay"]:hover, button[name="closeAndMainMenu"]:hover, button[name="closeAndRematch"]:hover, button[name="undoChoice"]:hover, button[name="setTimer"]:hover {
    background: #111;
  }

  .battle-log-add {
    background: transparent;
    border-color: #000;
    color: #EEE;
  }

  .battle-log {
    border-color: #000;
    background: #EEE;
    color: #000 !important;
  }

  .battle-log h2 {
    background: #222;
    border-color: #000;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
  }

  .ps-room .battle-controls {
    background: transparent;
  }

  .battle-log .rated strong {
    background: #376C8E;
  }

  .battle-log .chat strong {
    color: #132E4B;
  }

  .messagebar {
    border-radius: 0px;
    left: 100px;
    right: 100px;
    bottom: 0px;
    background: rgba(0, 0, 0, 0.85);
    text-align: center;
  }
  
  .rightbar {
    position: absolute;
    top: 0px;
    right: 0px;
    height: 450px;
    width: 100px;
    background-color: rgba(0, 0, 0, 0.85);
    border-left: none;
    border-bottom: none;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
  }
  
  .leftbar {
