css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jin115.com") {

  /* 非表示 */
  
  #header-top-banner-rss,/* RSSチャンネル */
  nav#top-header > ul.submenu,/* ヘッダーメニュー項目 */
  nav#footer-menu > ul.submenu,/* フッターメニュー項目 */
  #sponsered,/* スポンサードリンク */
  #headline,/* 注目記事 */
  #rt-controller-container,/* 注目記事移動ボタン　*/
  #side_left,/* 左カラム */
  #side_right,/* 右カラム */
  .dix_ad_n,/* 記事に挟まれた広告 */
  #contents > div:last-child,/* 記事等の最下パーツ　*/
  #index > div:last-of-type,/* 人気記事・新着記事 */  
  .index_article_footer_comment,/* コメント数 */   
  
  #out_button,/* ツイートボタン・Followボタン */ 
  .article_header ul > li:nth-child(2),/* コメント数*/  
  div[class^="google-user-ad"],/* 広告 */
  .article_bodymore table,/* 記事関連の商品紹介 */ 
  .footer_social_ad,/* 広告・SNSボタン */
  .article_bodymore > br:nth-last-child(-n+5),/* <br>で調整された余白 */ 
  #hot_tweet,/* 今週の人気記事（RT） */
  #popular_articles_comment,/* 今週の人気記事（コメント） */
  #other_news_website,/* その他おすすめサイト */ 
  .jin-ads,/* 広告 */
  #comment,/* コメント欄 */
  #top-container > div:nth-last-child(3),/* 人気記事・新着記事 */
  .paging,/* ページナビゲーション */
  #fixfooter ul > li:nth-child(-n+2)/* フレックスナビゲーションボタン */  
  {display: none!important;}

  /* 幅調整 */ 

  #container,/* メイン幅 */
  #contents_left/* 左カラム・中央カラム */
  {width: 770px!important;}
  
  #top-container, /* 個別記事幅 */
  nav#top-header,/* ヘッダーメニュー */
  #header,/* ヘッダーメイン */
  nav#footer-menu,/* フッターメニュー */  
  footer/* フッターメイン */  
  {width: 776px!important;}
  
  /* 個別記事配置 */
  #article_template
  {min-width: 770px!important;}
  
  /* 記事下部余白削除 */
  .article_body, .article_bodymore
  {margin-bottom: 0!important;}
  
  /* 余白削除 */
  .related-articles,/* 最新の関連記事 */
  .article_footer/* ページナビゲーション */
  {margin: 0!important;}
  
  /* フレックスナビゲーション */
  #fixfooter {
   margin-left: 776px!important;
   height: 105px!important;
   bottom: 0!important;
  }
}

@-moz-document url-prefix("http://jin115.com/archives/"),url-prefix("http://jin115.com/search"){  
  
  /* カテゴリー・ブログ内記事検索 */
  /* 非表示 */
  
  #archives_template > div:last-of-type/* 人気記事・新着記事 */
    {display: none!important;}
}
