css:
@-moz-document regexp("http://www.neopets.com/(?!(p|u)l_preview.phtml*|~.*).*"), domain("ncmall.neopets.com") {
/**************************************************************
***************************************************************
TOP BANNER
***************************************************************
***************************************************************/
#ban {
    background: url("http://i.imgur.com/oh3vOpt.png") no-repeat;
    background-size: 100.1%;
    background-position: 0px;
}
                                          
/*Hides top banner ad*/
#ad-table {
padding: 43px !important;
border-collapse: separate !important;
}
#ad-table .slug img {
    display: none !important;
}
#pushdown_banner .ad_wrapper_fixed, #pushdown_banner_btf {
    display: none;
}
/***************************************************************
****************************************************************
NAVIGATION
****************************************************************
****************************************************************/

#header {
    background: url("http://i.imgur.com/KvPqZOM.png") no-repeat;
    background-size: 100.1%;
    height: 76px;
    border-bottom: 2px solid #305880;
}
#navigation {
    padding-left: 23px;
    position: relative;
    top: -2px;
}

/*New navigation images: Crewniverse font!*/
#template_nav > li a img {display: none;}
li.nav_image > a {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
li.nav_image:nth-child(1) {
    margin-left: 6px;
}
li.nav_image {
    margin-right: -1px;
}
li.nav_image:nth-child(1) > a {background: url(http://i.imgur.com/Q3mshvw.png) no-repeat; width: 115px; height: 38px;}
li.nav_image:nth-child(2) > a {background: url(http://i.imgur.com/BsAGCjd.png) no-repeat; width: 90px; height: 38px;}
li.nav_image:nth-child(3) > a {background: url(http://i.imgur.com/rkSgfQu.png) no-repeat; width: 65px; height: 38px;}
li.nav_image:nth-child(4) > a {background: url(http://i.imgur.com/UlJocUr.png) no-repeat; width: 74px; height: 38px;}
li.nav_image:nth-child(5) > a {background: url(http://i.imgur.com/PuZ6SP2.png) no-repeat; width: 57px; height: 38px;}
li.nav_image:nth-child(6) > a {background: url(http://i.imgur.com/UqxWCYG.png) no-repeat; width: 97px; height: 38px;}
li.nav_image:nth-child(7) > a {background: url(http://i.imgur.com/i6KQu6B.png) no-repeat; width: 61px; height: 38px;}
li.nav_image:nth-child(9) > a {background: url(http://i.imgur.com/UiAGWAp.png) no-repeat; width: 70px; height: 38px;}
li.nav_image:nth-child(11) > a {background: url(http://i.imgur.com/Wbi7bG3.png) no-repeat; width: 70px; height: 38px;}

ul.dropdown {
    border: 1px solid #aa7788;
    background-color: #F7BCCB;
}
ul.dropdown a:hover {
    color: #A12B5A !important;
}
#header .eventIcon {
color: #FFF !important;
}
/*Time*/
#nst {
    position: relative;
    left: -10px;
    top: -1px;
    width: 96px;
}
.user {
    color: #ff99bb;
    position: relative;
    top: 8px;
}
.user a {
    color: #FFFFFF !important;
}

/***************************************************
****************************************************
FOOTER
****************************************************
****************************************************/
#footer {
    background-image: url("http://i.imgur.com/NKNb3SQ.png");
    background-size: 112%;
    border-top: 2px solid #305880 !important;
}

/*Lapis Lazuli footer image!*/
#footer:before {
    width: 297px;
    content: "";
    background: url("http://i.imgur.com/RADbwst.png") no-repeat;
    background-size: 100%;
    position: absolute;
    left: 717px;
    top: -23px;
    height: 173px;
}
.footerNifty {
    display: none;
}

/****************************************************
*****************************************************
SIDEBAR
*****************************************************
*****************************************************/
.sidebarHeader {
    background-color: #C38BD6;
    color: #FFFFFF !important;
}

/*Rounded sidebar bottom corners*/
.sidebarTable > tbody > tr:last-child > td:last-child {
    border-radius: 0px 0px 6px 6px;
}
/*Only add border to sides of box, not header*/
.sidebarTable > tbody > tr > td {
    border: 2px solid #c9c9c9;
    border-top: 0px;
}

/*****************************************************
******************************************************
CONTENT
******************************************************
******************************************************/
.content a {
    color: #F21B39;
}
.content a:hover {
    color: #A12B5A !important;
}
.blistSmall a {
    color: #A12B5A !important;
}
.blistSmall a:hover {
    color: #F21B39 !important;
}

/*Rounded top and bottom corners*/
#content table, .contentModuleTable {
    border-radius: 8px;
    overflow: hidden;
}
.contentModuleHeaderAlt {
    background-color: #FA6496;
    color: #FFFFFF !important;
}
.contentModuleContent, .contentModuleBody {
    border: 2px solid #c9c9c9;
    border-top: 0px;
    border-radius: 0px 0px 6px 6px;
}

/*Getting rid of unnecessary borders*/
.sidebarModule, .sidebarTable, .contentModuleTable, .contentModule {
    border: none;
    overflow: hidden;
}

#content .contentModuleHeader  #content .contentModuleHeaderAlt, .sidebarHeader {
    border-color: transparent !important;
}

/*News Flash titles*/
.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {
    background-color: #ff99bb;
    color: #FFFFFF;
}
.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {
    background-color: #FA6496;
    color: #FFFFFF;
}

.moreLink, .moreLinkAlt {
    border-radius: 4.5px;
}
.moreLinkBottom {
    border-top: 0px !important;
    padding-top: 0px !important;
    padding-bottom: 8px !important;
}

.newItems {
    border: 2px solid #FCF0F1 !important;
    background-color: #FCF0F1;
}

/***Exceptions***/

/*Bottom left new item box*/
.contentModule > tbody > tr > td > table > tbody > tr:nth-child(4) td.newItems:nth-child(1) {
    border-radius: 0px 0px 0px 6px !important;
}

/*Bottom right new item box*/
.contentModule > tbody > tr > td > table > tbody > tr:nth-child(4) td.newItems:nth-child(2) {
    border-radius: 0px 0px 6px 0px !important;
}

/*News Flash titles*/
.content > table:nth-child(1) > tbody > tr > td:nth-child(1) > div > table > tbody > tr:nth-child(2) > td > div:nth-child(2) > table > tbody > tr > td:nth-child(2) > table {
    border-radius: 0px !important;
}




}
