css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com"),
               domain("io9.com"),
               domain("kinja.com"),
               domain("deadspin.com"), 
               domain("jalopnik.com"), 
               domain("jezebel.com"), 
               domain("gawker.com"),
               domain("gizmodo.com"),
               domain("kotaku.com") {


body {
    background: #282e33;
    color: #fff;
    cursor: auto;
    font-family: Georgia,serif;
    font-style: normal;
    font-weight: normal;
    line-height: 24px;
    margin: 0;
    padding: 0;
    position: relative;
}

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6 {
    color: #fff;
    font-family: "ProximaNovaCond",sans-serif;
    font-style: normal;
    font-weight: bold;
    line-height: 1.3;
    margin-bottom: 1rem;
    margin-top: 0;
    text-rendering: optimizeLegibility;
}

.headline, .headline a, .headline a:hover, .headline a:active, .headline a:focus {
    color: #fff;
}

.f_newsidebar_on .sidebar-module header h2 strong, .f_newsidebar_on .sidebar-module header .h2 strong {
    color: #fff;
}

.f_newsidebar_on .sidebar-module header h2, .f_newsidebar_on .sidebar-module header .h2, .f_newsidebar_on .sidebar-module header h2 a, .f_newsidebar_on .sidebar-module header .h2 a {
    color: #fff;
}

.f_newsidebar_on .sidebar-item .headline {
    color: #fff;
}

.f_newsidebar_on .sidebar-item .meta strong {
    color: #fff;
}

.global-nav {
    height: 60px;
    background: #282e33;
    transform: translateZ(0);
    border-bottom: 0px solid #eaeaea;
    margin-bottom: 0px;
    position: relative;
    width: 100%;
    z-index: 98;
    line-height: 60px;
}

.icon-dustinburger span {
    display: block;
    height: 1px;
    background: #fff;
    margin-bottom: 5px;
}

.svg-icon {
    width: 18px;
    height: 18px;
    margin-top: -4.5px;
    display: inline-block;
    vertical-align: middle;
    opacity: 0.3;
    fill: #fff;
    pointer-events: none;
}

.thumb-inset .inset-headline {
    font-size: 15px;
    font-weight: bold;
    line-height: 1.2;
    color: #c5c7cc;
    display: block;
}
                                                                                                                                                      }
