css:
@-moz-document domain(www.ideaboardz.com) {
  body {
    background-color: #EEE;
  }

  .whiteboard {
    border: none;
    width: 100%;
    border-radius: 3px 3px;
    box-shadow: none;
  }

  .sticky {
    border-radius: 3px 3px;
    box-shadow: 1px 1px 1px #AAA;
  }

  h1, h2, h3, h4, .sectionTitle { 
    font-family: 'Delius', helvetica, cursive;
  }

  /* LOGO */
  a.logo img {
    display: none;
  }
  a.logo:before {
    content: "IdeaBoardz";
  }
  a.logo {
    text-decoration: none;
    color: #555;
    font-size: 30px;
  }

  .filter {
      margin-top: 10px;
  }

  #search {
    padding: 5px 0 5px 10px;
  }

  #menu {
    position: absolute;
  }

  .addStickyButton {
    background: transparent;
  }
  .addStickyButton:after {
    content: "+";
  }

  /* OMG, so many hax */
  #menu li.start-item, #menu li.last-item {
    display: none;
  }
  #menu li.middle-item {
    min-width: 0;
    width: auto;
    border-right: #555 1px solid;
  }
  .corner_inset_left, .corner_inset_right {
    display: none;
  }
  #menu li {
    background-color: #444;
  }
  ul#inner {
    margin: 0;
  }
  #menu li ul {
    left: 0;
    width: auto;
    padding: 0 10px;
    background-color: #444;
  }
  #menu li ul li {
    width: auto;
    margin: 5px 0;
    border: 0;
  }
  #menu li ul li a {
    color: #CCC;
  }
  #menu .middle {
    display: none;
  }
}
