	/*chart border */

	.chart-controls-bar {
		background: #222!important;
	}

	#chart-area.fullscreen {
		background: #222!important;
	}

	.chart-page {
		background: #222!important
	}


	/* BitcoinJesse - END */

	.tv-floating-toolbar {
		border: 1px solid #666;
		background-color: #444;
	}

	.tv-floating-toolbar__widget {
		border-right: 1px solid #666;
		background-color: #444;
	}

	.chart-warning warn-bats{
		display: none;
	}
	.tv-account-manager__secondary-panel {
			background-color: #111;
	}

	.trading-journal-dialog table.history tr:nth-child(even), .trading-journal-dialog table.journal tr:nth-child(even) {
			background: #333;
	}

	.tv-account-manager__data{
		color: #fff !important; 
	}
	.tv-trading-toolbar__value{
		color: #fff;
	}
	.tv-trading-toolbar__qty-input{
		color: #fff;
		background: #333;
	}
	

	@-webkit-keyframes highlight-animation {
		from {
			background: #222;
		}
	}

	@-moz-keyframes highlight-animation {
		from {
			background: transparent;
		}
		to {
			background: #444;
		}
	}

	@-o-keyframes highlight-animation {
		from {
			background: transparent;
		}
		to {
			background: #444;
		}
	}

	@keyframes highlight-animation {
		from {
			background: transparent;
		}
		to {
			background: #444;
		}
	}
