css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jin115.com") {

  /* 広告 */
  .btn-ldb-bottomAdd,
  #index_head,
  #article_head,
  .plugin-extra,
  #index_top,
  #article_top,
  .article_mid,
  #article_mid_lower,
  #article-contents > table,
  #article_low,
  #article_bottom,
  #index_bottom,
  .content > div:nth-last-of-type(2),
  #article-1_5,
  [id*="ad_popular_articles"],
  [id*="ad_related_articles_bottom"]  
  {display: none!important;}
  
  /* 広告以外 */
  .plugin-twitter_follow_button,
  .plugin-popular_articles-new,
  .plugin-blogroll,
  .footer-ranking,
  .footer-nav,
  .footer-bottom,
  .article-button,
  .article-button-simple,
  .footerPcLink,
  #article-contents > br:nth-last-of-type(-n+2)
  {display: none!important;}
  
  /* 画像リンク */
  .pict {
    pointer-events: none!important;
    -moz-user-select: none!important;
  }
  
  /* コメント */
  .article-comment-count,
  [id^="comments"]
  {display: none!important;}

@-moz-document url-prefix("http://jin115.com/archives/"),url-prefix("http://jin115.com/tag/") {
  
  .container-inner > div:nth-last-of-type(2)  .image-item
    {display:none!important;}
}  
}
