css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sankei.com") {

  /* 除去 */
  [id^="picAd"],
  #modPickup,
  #fbFollow,
  #modDiscussion,

  [id*="textAd"],
  .switchView,
  .socialbutton,
  .twitter-share-button,
  .fb-like,
  #modSmprecommend,
  #modTopisblock,
  .topQuicklink,
  .btnRanking,
  .footerMainarea ul,

  .sugoiCounter,
  #rankingSugoi,
  .banner1,
  .chartbeatRankingWidget,
  #modSankeinews,
  #iconAd,
  .footerRelevant,
  
  .fncbtn > li:nth-child(n+2):nth-child(-n+3),
  .footerQuicklink ul >li:first-child,  
  .footerQuicklink ul >li:nth-child(3)  
  {display: none!important;}

  .addition:after {
    content: ""!important;
    display: block!important;
    clear: both!important;
  }
  
  .read-part h1
  {font-weight: bold!important;}
  
  .read-part .addition
  {padding: 8px 0!important;}
  
  .photoframe a {
    pointer-events: none!important;
    -moz-user-select: none!important;
  }
  

}
