css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedspot.com") {

    /* Top bar takes up unnecessary space, make it hover to display */
    .top {
        background-color: gray !important;
        background-image: none;
        height: 10px;
    }

    .top > * {
        display: none;
    }

    .top:hover {
        height: 44px;
    }

    .top:hover > * {
        display: block;
    }

    /* make the feed area use this space cleared at top */
    #body {
        padding-top: 0px;
    }

    /* Are you kidding with the useless empty space below buttons?! */
    .views   {
        padding-bottom: 0px !important;
    }
    #fixed_subnav_body {
        margin-top: 70px;
    }

    /* "Add interests" thing at top left  */
    .c1_86 {
        display: none;
    }

    /* I'll decide who to follow */
    #f_rec {
        display: none;
    }

    /* Mark all as read: What a dangerous button to have! */
    #ajaxmarkasread {
        display: none;
    }

    /* disable right sidebar with the checklists */
    #f_subnav_i2[style] {
        display: none !important;;
    }

    /* Upgrade to Feedspot Gold spam in sidebar */
    div.c1_151:nth-child(8) {
        display: none;
    }

    /* Upgrade to Feedspot Gold spam in top navbar */
    .top-bar-in > a:nth-child(3) {
        display: none;
    }

    /* External integration (evernote, etc.) message container */
    #bcrumbad {
        float: right;
    }

    /* External integration message */
    #bcrumbad > a[style] {
        font-size: 0.55em !important;;
    }

    /* "New" in bright red! */
    #bcrumbad > sup:nth-child(2), sup.chp, sup.chp1 {
        display: none;
    }

    /* Upgrade spam via individual "RSS" feature hook and "Backup to Dropbox" feature hook */
    .cushp > a:nth-child(2), .cushp > a:nth-child(3) {
        display: none;
    }

    /* Delete buttons in left navbar - dangerous and intrusive */
    .c1_a1, .c1_a1:hover {
        display: none !important;
    }

    /* Autopost button, Save to Evernote button */
    li.tsbimg_ap, li.tsbimg_ev {
        display: none !important;
    }

    /* The confusing 'View Full article' button (and similar Preview button) */
    .vmore, .prepnbw {
        display: none !important;
    }

    /* share buttons shown when hovering over snippet */
    ul#sbtn_main > .fb_sss, ul#sbtn_main > .gp_sss, ul#sbtn_main > .tw_sss {
        display: none;
    }

    /* "Upgrade" popunder message */
    #upgrade_sites_alt {
        display: none !important;
    }

    /* width of left navbar containing feed names */
    td.c1x:nth-child(1), #c1outerwrap, #mainleftnavouter[style], #mainleftnavouter > div.jspContainer, #mainleftnavouter div.jspPane {
        width: 12em !important;
    }

    /* Right side area including "all","unread",etc. buttons above and the feed contents below*/
    /* Increase width */
    .c2x {
        position: relative;
        left: -75px;
    }

    /* Same area, when the feeds list is hidden - change so content doesn't go beyond left edge of screen*/
    .c1outerhide + .c2x {
        position: relative;
        left: 0px;
    }

    /* Main feed area (excluding button area) */
    #fixed_subnav_body {
        width: calc(100% + 75px);
    }

    /* Current feed's text content */
    #current_node > div:nth-child(2) {
        margin-left: 25px;
    }

    /* Current feed's title area */
    #current_node div.ocn-meta {
         text-align: center !important;
    }

    /* Sharing buttons in current feed */
    #current_node .top-sb {
        display: none;
    }

}
