css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.leboncoin.fr),
               url-prefix(https://www.leboncoin.fr),
               url-prefix(http://www2.leboncoin.fr),
               url-prefix(https://www2.leboncoin.fr) {
  body { 
    background-color: #eee !important;
    background-image: none !important;
    margin: 0px !important; 
    font-weight: normal;
    color: #aaa;
  }
  a:link, a:hover, a:active {
    font-weight: normal;
    color: #000;
    background-image: none;
    background-color: #fff;
  }
  #images a {
    opacity: 0.1 !important;
  }
  .images_cadre {
    border: 0;
    opacity: 0.1 !important;
  }
  .images_cadre:hover {
    opacity: 0.8 !important;
  }
  span {
    opacity: 0.1 !important;
    background: none;
    color: #000;
  }
  span:hover {
    opacity: 0.8 !important;
    background: none;
    color: #000;
  }
  img {
   opacity: 0.1 !important;
  }
  img:hover {
   opacity: 0.8 !important;
  }
  .page_width {
    background: none repeat scroll 0 0 #eee;
  }
  .content-border {
    background: none;
  }
  .content-color {
    background: none repeat scroll 0 0 #fff;
  }
  #account_login_f {
    background: none;
  }
  .lbc_option_box_title {
    background: none;
  }
  #screen_form input[type="submit"], #pass_lost_f input[type="submit"] {
    background: none no-repeat scroll 0 0 #ddd;
  }
  .lbc_title_triangle {
    border: none;
  }
  .lbc_nosalesmen {
    height: 0px;
    overflow: hidden;
  }
  .cnil_frame .cnil_title {
    color: #999;
  }
  .line {
      border-bottom: 1px solid #ccc;
  }
  .navlist.type li.selected {
    background: none repeat scroll 0 0 #fff;
  }
  .between .type {
    background: none repeat scroll 0 0 #ddd;
    color: #aaa;
  }
  .paging li.selected {
    background: none repeat scroll 0 0 #ddd;
  }
  .search_box {
    background: none repeat scroll 0 0 #fff;
  }
  #searchbutton {
    background: none repeat scroll 0 0 #ddd;
  }
  #headermain {
    width: 0;
    overflow: hidden;
  }
  div.list-gallery,
  #categories_container,
  #footer {
    height: 0;
    width: 0;
    overflow: hidden;
  }
}
