css:
@-moz-document domain(live.paloaltonetworks.com) 
{

.header {padding-top: 0.2em!important;}
.lia-content .hero_other_page_head {height: 80px!important;}
.lia-quilt-row {margin-top: 0px!important;margin-bottom: 0px!important;}
.header-border {height: 0px!important;}
.lia-panel-message-root {padding: 0px!important;}
.lia-message-heading {background-color: #F2F2F2; border: 1px solid #000000; padding: 4px!important; border-radius: 5px; border-bottom: 1px solid #000000!important;}
.lia-message-body-content p {font-size: 18px; line-height: 26px;}

.lia-message-body-content ul li {font-size: 18px; line-height: 26px;}
.lia-message-body-content ol li {font-size: 18px; line-height: 26px;}
.lia-message-body-content p span {font-family: 'DejaVu Sans Mono'!important;font-size: 14px!important; font-weight: bold; line-height: 26px!important;}
.lia-message-body-content ol li span {font-family: 'DejaVu Sans Mono'!important;font-size: 14px!important; font-weight: bold; line-height: 26px!important;}
.lia-message-body-content ul li span {font-family: 'DejaVu Sans Mono'!important;font-size: 14px!important; font-weight: bold; line-height: 26px!important;}

.lia-message-body-content table tbody tr td {font-family: 'Lato'!important;font-size: 18px; line-height: 26px;}

.lia-message-body-content h3 {font-size: 20px!important; font-weight: bold!important; padding-top: 8px!important; padding-bottom: 8px!important; background-color: #F2F2F2;}
.lia-message-body-content h4 {font-size: 18px!important; font-weight: bold!important; padding-top: 8px!important; padding-bottom: 8px!important; background-color: #F2F2F2;}

.lia-decoration-border-content {border: 1px solid #828282;padding: 10px;border-radius: 5px;width:104%;}
#actionMenuDropDown {background: #A8C438; padding: 4px;border-radius: 5px;}
.lia-content a {text-decoration: underline!important;}
.lia-content a:hover {text-decoration: underline!important;}

/*
.lia-decoration-border-content {position: relative; left: -10%; width: 110%;}
*/
}
