css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("piratebays.eu") {
  
  
  footer {
    color: #ffffff;
    text-align: center;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  a, a:link, a:visited, a:focus {
    color: #CECECE;
    text-decoration: none;
    border-bottom: 1px dotted #000;
}
  
  #fp {
    display: block;
    margin: 0px auto 20px;
    text-align: center;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  body {
    background: none repeat scroll 0% 0% #FFF;
    margin: 10px;
    font: 0.7em/1.3em Verdana,Arial,Helvetica,sans-serif;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  thead#tableHead th {
    background: none repeat scroll 0% 0% #505050;
}
  
  
  table#searchResult {
    color: #ffffff;
    font-size: 1em;
    border-collapse: collapse;
    border-spacing: 1px;
    text-align: left;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
}
  
  
  
  
  
  table#searchResult th, table#searchResult td {
    padding: 3px;
    border: 1px solid #4CA6E7;
    background-color: #444444;
    border-collapse: collapse;
    border-spacing: 1px;
}
  
  
  .detDesc {
    color: #10A7DA;
}
  
  
  
  
  h2 {
    font-size: 1.1em;
    line-height: 1.5em;
    text-align: right;
    padding: 2px 5px;
    margin: 0px;
    border-bottom: 0px solid #ffffff;
    background-image: url("chrome://global/skin/media/imagedoc-darknoise.png");
    font-weight: normal;
    clear: both;
}
  
  
  #fp h1 a {
    background: url("http://i.imgur.com/V3g0R0s.png") no-repeat scroll 0% 0% transparent;
    margin: 0px auto;
    display: block;
    border: 0px none;
    width: 275px;
    height: 295px;
    outline: medium none !important;
}
  
  #details {
    padding: 0px 10px 10px;
    background: #333 none repeat scroll 0% 0%;
}
  #detailsframe #title {
    height: 1%;
    background: #00ACEE none repeat scroll 0% 0%;
}
  
#details dt {
    float: left;
    font-weight: bold;
    padding: 0px 5px 0px 0px;
    color: #00ACEE;
    border-bottom: 1px dashed #00acee;
    clear: both;
}
  #details dd {
    padding: 0px 0px 0px 40px;
    border-bottom: 1px dashed #00acee;
}
.download a {
    font-size: 1em;
    color: #00acee !important;
    font-weight: bold;
    text-transform: uppercase;
    border-bottom: 1px dotted #00acee;
    padding: 0px 0px 0px 13px;
}
  #comments p.byline {
    color: #00acee;
    margin-top: 12px;
}
#detailsframe {
    text-align: center;
}
  .nfo {
    margin: 1em 0px;
    width: 600px;
    min-height: 50px;
    border: 1px solid #00acee;
    background: #333 none repeat scroll 0% 0%;
    padding: 10px;
    font-size: 12px;
    font-family: "Courier New",Courier,monospace;
    clear: left;
    line-height: 0.9em;
}
  #details h4 {
    margin: 20px 0px 0px;
    color: #00acee;
    font-size: 1.2em;
}
  #comments .comment {
    border: 1px solid #000;
    background: #333 none repeat scroll 0% 0%;
    margin-top: 0.3em;
}
  body {
    text-align: center;
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 0.7em;
    color: #ccc;
    line-height: 1.3em;
}
#q {
    text-align: center;
}
  thead#tableHead a {
    color: #999;
    font-weight: bold;
}
  table#searchResult td.vertTh {
    font-weight: bold;
    color: #CCC;
}
}
