css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("himado.in") {

/* プロフィールページのみ 黒化しない(not) */
body:not(#body_profile)  {
    background: #272727 !important;
    color: #C0C0C0 !important;
}


/* ◆全体◆ */
/* FC2ロゴ消去 */
.m-hder-101_logo {
    background: none;
    padding: 0px 0px 0px 0px;
    display: block;
}
.m-hder-101_logo img {
    position: absolute;
    clip: rect(0px 400px 100px 0px);
    top: 0px;
    margin-left: -15px;
}
#header a{
   color: #288A7C !important;
}
#header a:hover {
   color: #32ACA7 !important;
   text-shadow: #EEC41F 0px 0px 10px !important;
}

/* ヘッダー・フッター調整 */
#cndxh {
    background-color: #272727;
}
#cndxh .fdtjf,
#rczzp .fdtjf {
    border-bottom: 1px solid #EF5763;
    background-color: #272727;
    visibility: hidden;
}
.m-hder-101{
    background-color: #272727 !important;
    border-bottom: 1px solid #fcc800;
}
.m-hder-101_cnt{
    color: #C0C0C0 !important;
    margin-bottom: 0px;
}

/* デフォルト文字・文字リンク 色調整 */
p {
    color: #C0C0C0 !important;
    margin: 0 !important;
    line-height: 1.2 !important;
}
a {
    color: #C0C0C0 !important;
    text-decoration: none !important;
}
a:link {
    color: #32ACA7 !important;
     -webkit-transition: all 700ms cubic-bezier(0.1, -0.55, 0.265, 1.55);
     -moz-transition: all 700ms cubic-bezier(0.1, -0.55, 0.265, 1.55);;
}
a:visited {
    color: #217367 !important;
}
a:hover {
    color: #3BCAC5 !important;
    text-shadow: #EEC41F 0px 0px 10px;
}


/* ◆TOP・登録一覧ページ◆ */
/* 日時等 文字色調整 */
.home_info  {
    color: #C0C0C0 !important;
}

/* 検索・アカウント別ページ 文字色調整 */
.thumbdataback,
.sortkey,
.thumbcomment {
    color: #C0C0C0;
}

/* 検索語句ボーダー 文字色調整 */
.mostsearch a {
    border: 1px solid #8C7000 !important;
}

/* 検索・視聴履歴 文字色調整 */
#header a,
.sortnavi_short a,
.searchhistorya {
    color: #288A7C !important;
}
#header a,
.sortnavi_short a,
.searchhistory a,
.mostsearch a:link {
    color: #288A7C !important;
}
#header a,
.sortnavi_short a,
.searchhistory a,
.mostsearch a:visited {
    color: #288A7C !important;
}
#header a,
.sortnavi_short a,
.searchhistory a,
.mostsearch a:hover {
    color: #288A7C !important;
}

/* 検索・アカウント別ページ サムネイル調整 */
div#topLayout div.movie_imageBox {
    border: 1px solid #696969;
    height: 140px;
}
div#topLayout div.movie_imageMask {
    overflow: hidden;
    height: 144px;
}

/* カテゴリーアイコン等 色調整 */
.force_info,
.movie_beforeafter,
.pagenavi,
.tb_sb,
.tb_sbh  {
    background-color: #000000;
    color: #C0C0C0 !important;
    xxxborder-left: 1px solid #7fbfff !important;
}
.pager{
    background-color: #000000;
    color: #C0C0C0 !important;
    text-decoration: none !important;
    xxxborder-left: 1px solid #9eceff !important;
    border-radius: 3px;
    margin: 2px;
}
.sortnavi_short {
    background-color: #111111 !important;
    color: #C0C0C0 !important;
    text-decoration: none !important;
    xxxborder-left: 1px solid #7fbfff !important;
    border-radius: 3px;
    margin: 2px;
}
.categories,
.categoriesthis{
    background-color: #000000;
    color: #C0C0C0 !important;
}
a.pagelink:hover span.pager,
a.pagelink:hover span.categories,
a.pagelink:hover span.headmenus {
    background-color: #31330A;
    color: #C0C0C0 !important;
    border: 1px solid #CD8500 !important;
    border-radius: 3px;
    margin: 2px;
}

/* ホームの薄緑ヘッダー 黒化 */
.home_head {
    color: #C0C0C0 !important;
    background-color: #000000 !important;
}
.home_head {
    border-left: 18px solid #626613 !important;
    margin: 5px 0px 5px;
}


/* ◆動画ページ◆ */
/* バランス調整 */
#topmovie_left_box{
    width: 70% !important;
}
#topmovie_right_box{
    width: 26.5% !important;
}
.movieinfo_title{
    display: none !important;
}

/* 動画ページの文字リンク 文字色調整 */
#body_movie a:link {
    color: #2D9A89;
}
#body_movie a:visited {
    color: #2D9A89;
}
#body_movie a:hover {
    color: #2D9A89;
}

/* 登録コメント文字色 調整 */
#movie_left_box,
#author_comment,
#textcomment_ret {
    background: #272727 !important;
    color: #C0C0C0 !important;
}

/* 配信先URL欄 背景色調整 */
#movieinfo,
.movie_infobox,
#report {
    background-color: #272727 !important;
    font-weight: bolder; 
}
.movie_infobox td.tb_sb{
    background-color: #000000 !important;
}
.tab_button{
    border-radius: 10px 15px 0px 0px / 10px 10px 0px 0px;
    text-shadow: 0 0 20px #111;
    font-weight: bolder;
    border-right: 7px solid #272727 !important;
}
.tab_button a{
    color: #edf2d8 !important;
}
#undermovie {
    opacity: 0.75;
}

/* コメントグループ 行 色変更 */
.tb_sbh_g {
    background-color: #58632B !important;  
}
.tb_sb_g {
    background-color: #000000 !important;
}

/* テキストコメントポップアップ 背景調整 */
#anchor_txt  {
    background: #272727 !important;
    color: #000 !important;
}
#anchor_txt a {
    color: #000000 !important;
    text-decoration: underline !important;
}
#anchor_txt a:link {
    color: #000000 !important;
}

/* 同シリーズの動画欄 調整 */
#movie_beforeafter {
    overflow: auto;
}

/* 動画プレイヤー設定画面 色調整 */
#ytPlayerSettingPanel {
    background-color: rgba(0, 0, 0, 0.8);
}
.settingMenuGroup label,
#defaultBtn,
#saveBtn {
    background-color: rgba(80, 80, 80, 0.9);
}
.settingMenuGroup input[type="radio"]:checked + label {
    color: #fff;
    background-color: rgba(150, 150, 150, 1);
}
#videoControllerBg{
    color: #222222 !important;
}


/* ◆その他◆ */
/* 動画登録ページ 一部 色調整 */
.infoblock {
    background: #272727 !important;
    color: #C0C0C0 !important;
}
.infoblock h3 {
    background-color: #626613;
}

/* コメントページ 行 色互い違い */
.cg_row_1 {
    background-color: #43453D !important;
}

/* メッセージページヘッダー 色調整 */
#message_head_left {
    background-color: #4A4D0F !important;
    border-bottom: 1px solid #7FBFFF !important;
}

#message_head_right {
    background-color: #4A4D0F !important;
    border-bottom: 1px solid #7FBFFF !important;
}

/* メッセージページ等 一部 色調整 */
#body_mypage a,
#body_useredit a,
#body_message a,
#body_cssedit a,
#body_contact a {
    color: #C0C0C0 !important;
    text-decoration: underline !important;
}
#body_mypage a,
#body_useredit a,
#body_message a,
#body_cssedit a,
#body_contact a:link {
    color: #2D9A89 !important;
}
#body_mypage a,
#body_useredit a,
#body_message a,
#body_cssedit a,
#body_contact a:visited {
    color: #258072 !important;
}
#body_mypage a,
#body_useredit a,
#body_message a,
#body_cssedit a,
#body_contact a:hover {
    color: #2D9A89 !important;
}

/* 検索フォーム 色調整 */
.c-input-searchbox-101,
.c-input-searchbox-102 {
    background-color: #000000 !important;
}
.c-input-searchbox-101_txt,
.c-input-searchbox-102_txt {
    color: #ffffff !important;
    background-color: #272727 !important;
}
.c-btn-106 {
    background-image: -moz-linear-gradient(top, #aaa, #ddd);
    background-image: -ms-linear-gradient(top, #aaa, #ddd);
    background-image: -o-linear-gradient(top, #aaa, #ddd);
    background-image: -webkit-gradient(linear, center top, center bottom, from(#aaa), to(#ddd));
    background-image: -webkit-linear-gradient(top, #aaa, #ddd);
    background-image: linear-gradient(top, #aaa, #ddd);
}

/* 一部アフィリエイト広告 クリーンアップ */
.ad_player, .l-cnt-100_box10, .l-cnt-100_box30, #player_ad_window, #lianyue_single_header_ad, a[target="_blank"] img, .barrage, .m-fter-wp {
    height: 0px !important;
    visibility: collapse !important;
    display: none !important;
    position: absolute !important;
}
iframe:not(#youtubeVideo) {
    height: 0px !important;
    visibility: collapse !important;
    display: none !important;
    position: absolute !important;
}

/* 「himado GoodByeTop Script」用 */
#PickUpHIT {
    color:#C0C0C0 !important;
    background-color:#111111 !important;
    text-shadow: 0px 0px 30px rgba(244, 255, 214, 0.4) !important;
}
#PickUpHIT .thumbblock_3colum {
    color:#C0C0C0 !important;
    background-color: rgba(255,76,76,0.3) !important;
    text-shadow: 1px 1px 8px rgba(255,255,255,0.5), 1px 1px 0px rgba(0,0,0,0.5) !important;
}


}
