css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.confrerie-des-traducteurs.fr/forum/") {
  
  body{
    background : transparent url("http://i.imgur.com/4Xpak79.jpg") repeat scroll 0px 100%;
  }
  #wrap {
    background: url("http://i.imgur.com/Em1hEzd.jpg") repeat scroll 0 100%;
    max-width: 90%;
    box-shadow: -1px 2px 5px 1px rgba(0, 0, 0, 0.7);
  }
  #logo {
    background: rgba(0, 0, 0, 0) url("http://i.imgur.com/RUNOgJh.png") no-repeat scroll 50% 0 / 100% 100%;
  }
  @media screen and (max-width: 1333px), only screen and (max-device-width: 1333px) {
	#logo {
	    background: url("http://i.imgur.com/SMbhpLN.png") no-repeat 50% 0;
		width: 250px;
		height: 241px;
	}
  }
  div.mChatBodyFix { 
    border-style: dashed;
    border-bottom-width: 2px;
  }
  .scroll-btn {
  background-color: #823E03;
  opacity: 0.75;
  } 
}
