@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("prohardver.hu") {

/*
red: #eb2626;
deep red: #962120;
gray: #939598;
dark gray: #58595b;

*/


body.background_banner{
  background-image: url(http://users.atw.hu/b95/PH!mod/lenovo/lenovo_ph7_bigad-guide.jpg) !important;
  background-repeat: no-repeat !important;
  padding: 1px 0 0 17px;
  background-color: #000;
}
body.background_banner #network div.bg {
	background:none;
}
body {
	background: #000;
}
#network li.ph {
  background: url(http://users.atw.hu/b95/PH!mod/lenovo/network-bul-ph.png) 0 0 no-repeat;
}
#main #head{
	background: #eb2626 url(http://users.atw.hu/b95/PH!mod/lenovo/head-full.png) repeat-x 0 0;	
}
#main #head .sepline{
	  background: #eb2626 url(http://users.atw.hu/b95/PH!mod/lenovo/head-full.png) no-repeat 0 -59px;
}
#main #navi{
	  height: 26px;
	background:	#939598;
}
#main #navi li{
	background:	#939598;
}
#main #navi li.act{
	background:#eb2626;
}
#main  #navi ul.menu{
	background:	#939598;
}
#main #navi a{
	color:#FFF;
	
}
#center .tabsbox .menu .act a{
	background:#962120;
	color:#FFF;
}
#center .tabsbox .menu a{
	color: #fff;
	  background: #eb2626;
	  border: 1px solid #962120;
}

#center .tabsbox .menu{
	  border-bottom: 2px solid #962120;
	  background:none;
}
#main .boxl_coloured h4{
	  background: url(http://users.atw.hu/b95/PH!mod/lenovo/boxl-head-yellow.gif) left center no-repeat;
	  color:#FFF;
}
#main .anyagok li span.type{
	background:#eb2626;
	color:#fff;
	
}
#main .anyagok .blog h1 a{
	  color: #58595b;
}
#main #right .nwcm>h4 {

  background: url(http://users.atw.hu/b95/PH!mod/lenovo/boxr-head-pp.gif) #b42224;
}
a{
	color:	#eb2626;
}
#network li a{
	color:#eb2626;
}
#page #network li.ph a{
	color:#fff;
}
#network {
	color:#939598;
	
}

#shadow #main{
	background:#f3f3f3;
	
}
#main #foot{
	background: #58595b;;
	
}
#main #foot .sepline{
	background:	#eb2626;
}
#main #foot h4, #main  #foot h4 a{
	color:#eb2626;
}
}
