css:
@-moz-document domain("www.goatlings.com") {
/* 
background image is public domain
if you want a redder/less magenta version of the bg image i made a version with a filter:
http://i.imgur.com/PtBuy7y.jpg
*/

/* closet edit */

.base_area {
    float: left;
    width: 130px;
    margin-top: 42px;
}

#item_area_ajax {
    float: right;
    width: 585px !important;
    height: 500px !important;
    overflow: scroll;
}

.item_area {
    width: 582px !important;
    float: right !important;
   
}

.categories {
    margin-bottom: 5px;
}

.closetItem {
    border-radius: 5px;
    padding: 0;
    margin: 1;
}

/* style edits */

body {
    background-image: url(http://i.imgur.com/rRc3f4l.jpg);
    background-repeat: no-repeat;
    background-position:top;
    background-attachment:fixed;
    background-size: cover;
}

#wrapper {
    background-color: rgba(255, 255, 255, 0.7);
}

#content {
    background-color: rgba(255, 255, 255, 1);
    border-radius: 5px 5px 5px 5px;
}

#user-info-wrap {
    background-color: rgba(255, 255, 255, 1);
    border-radius: 5px 5px 5px 5px;
    padding: 5px;
    margin-top: 5px;
    margin-left: 5px;
    margin-right: 13px;
}

a { 
    text-decoration: none; 
    color: #5CA637; 
    font-weight: bold;
    -moz-transition-duration:0.3s;
    -webkit-transition-duration:0.3s;
    -o-transition-duration:0.3s;
}

a:hover { 
    color: #ED8080;
    transition: color .25s ease-in-out;
    -moz-transition-duration:0.3s;
    -webkit-transition-duration:0.3s;
    -o-transition-duration:0.3s;
} 

.button { 
    border: none !important; 
    background: #7EB562; 
    padding: 4px; 
    color: #FFF; 
    border-radius: 4px 4px 4px 4px; 
    cursor: pointer;
    -moz-transition-duration:0.3s;
    -webkit-transition-duration:0.3s;
    -o-transition-duration:0.3s;
}

.button:hover { 
    background: #ED8080; 
    color: #FFF;
    border: none !important;
    transition: background .25s ease-in-out;
    -moz-transition-duration:0.3s;
    -webkit-transition-duration:0.3s;
    -o-transition-duration:0.3s;
}

.closetItem {
    width: 100px;
    border-radius: 5px;
    padding: 5px;
}

.item_area {
    border: 2px solid #ED8080;
}

.base_area {
    border: 2px solid #ED8080;
}

.item-invent {
    border: 1px solid #BD9975;
    padding: 3px;
    border-radius: 5px;
}

.itema {
    border: 1px solid #BD9975;
    padding: 3px;
    border-radius: 5px;
}

.news-post {
    border: 1px solid #BD9975;
}

.item-invent-view {
    border: 1px solid #BD9975;
}

.page-title {
    border-bottom: 1px solid #BD9975;
    background: none !important;
}

.flash_data { 
    border: 1px solid #BD9975;
}

.random_event {
    border: 1px solid #BD9975;
}

.event {
    border: 1px solid #BD9975;
}

.mypets-pet { 
    border: 1px solid #BD9975;
}

.profile-info {
    border: 1px solid #BD9975;
    padding-left: 15px;
    padding-right: 15px;
}

.profile-part-two {
    text-align: center;
}


/* scrollbar */

::-webkit-scrollbar {
    width: 14px;
}

::-webkit-scrollbar:horizontal {
    display: none;
}
 
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
}
 
::-webkit-scrollbar-thumb {
    background-color: rgba(237, 128, 128, 0.5);
    -webkit-box-shadow: inset 0 0 6px rgba(237, 128, 128, 1);
    border-radius: 10px;
}

::-webkit-scrollbar-button {
     background-color: #fff;
    -webkit-box-shadow: inset 0 0 6px rgba(237, 128, 128, 1);
}
}

@-moz-document url("http://www.goatlings.com/") {
#m, #n {
    border: 6px solid #ED8080 !important;
}

#n {
    padding-bottom: 15px !important;
}

h1 {
    background-color: #ED8080 !important;
    border-bottom: 10px solid #8CD468 !important;
}

h2, h3 {
    background-color: #8CD468 !important;
}
}

@-moz-document url("http://www.goatlings.com/itemsorter") {
.table-header {
    background-color: #BDFF9C;
}

tr {
    border: 1px solid #BD9975 !important;
    background-color: #FFE6E6;
}

/* the border code doesn't work, i dunno why. i kept it there anyway. */
}

@-moz-document url("http://www.goatlings.com/forums/index") {
tr {
    background-color: #FFD1D1;
}
}
