css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.gaiaonline.com/avibuilder/") {
  
  #yui-main{
    height: 1050px !important;
  }
  
  @font-face {
    font-family: 'Questrial';
    font-style: normal;
    font-weight: 400;
    src: local('Questrial'), local('Questrial-Regular'), url(http://fonts.gstatic.com/s/questrial/v6/MYWJ4lYm5dbZ1UBuYox79FtXRa8TVwTICgirnJhmVJw.woff2) format('woff2'), url(http://fonts.gstatic.com/s/questrial/v6/MYWJ4lYm5dbZ1UBuYox79D8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
  } 

  #page_title{
    color: white;
    font-family: 'Questrial', 'Arial', sans;
    font-size: 30px;
    font-weight: normal;
    text-align: center;
    width: 400px;
    background: #6B5B79;
    border-radius: 10px;
    padding: 5px 0;
    margin-bottom: 20px;
  }
  
  #ghost_container{
    font-size: 12px;
    width: 400px;
    margin: 0 auto;
    text-align: center;
    line-height: 25px;
  }
  
  #ghost_container a{
    color: #333 !important;
    background: linear-gradient(180deg, white, #F4B033);
    border: 1px solid #B5B0A9;
    padding: 5px 10px;
    border-radius: 3px;
  }
  
  #ghost_container a:hover{
    background: linear-gradient(180deg, white, #F4D392);
  }
  
  #ghost_form{
    margin-top: 10px;
  }
  
  #ghost_container:before{
    content: '';
    background: linear-gradient(180deg, #6B5B79, #857196);
    position: absolute;
    bottom: -67px;
    left: -290px;
    height: 40px;
    width: 979px;
  }
  
  #pick_cont, #alt_gender_container, #allow_ghosting_container{
    color: white;
    font-size: 13px;
  }
  
  #pick_cont{margin-left: 200px;}
  
  #char_image{
    padding-top: 0;
    position: relative;
    top: 20px;
    width: 727px;
    margin: 0 auto 20px auto;
  }
  
  /* ===== Bases ===== */
  
  td:nth-of-type(1) .section_header{
    top: 3px;
    left: 10px !important;
    position: relative;
    color: white;
    font-weight: normal;
    font-size: 10px;
    line-height: 10px;
    text-transform: uppercase;
    background: #999999;
    padding: 2px 10px;
    border-radius: 5px 5px 0 0;
  }
  
  td:nth-of-type(1) .section_header strong{font-weight: normal;}
  
  #base_options{
    border: 1px solid #999999;
    height: 300px;
    width: 200px;
    overflow-y: auto;
    overflow-x: hidden;
    text-align: center;
    padding: 5px;
    white-space: normal;
    top: 5px;
    position: relative;
  }
  
  #base_options .base{display: inline-block; float: none;}
  
  #base_options br, td:nth-of-type(2) br{display: none;}
  
  #current_base_container{
    color: #999999;
    position: absolute;
    top: 340px;
    left: 10px;
    font-size: 12px;
    text-align: center;
    width: 190px;
  }
  
  #selected_base_name{
    font-weight: normal;
    color: black;
    display: block;
  }
  
  /* ===== Equipped ===== */
  
  td:nth-of-type(2) .section_header:nth-of-type(1){
    top: 3px;
    left: 10px;
    position: relative;
    color: white;
    font-weight: normal;
    font-size: 10px;
    line-height: 10px;
    text-transform: uppercase;
    background: #999999;
    padding: 2px 10px;
    border-radius: 5px 5px 0 0;
  }
  
  td:nth-of-type(2) .section_header:nth-of-type(1) strong{font-weight: normal;}
  
  #equip_list{
    border: 1px solid #999999;
    padding: 5px;
    width: 245px;
    height: 93px;
    overflow-y: scroll;
    margin-left: 2px;
    margin-bottom: 5px;
  }
  
  /* ===== Buttons ===== */
  
  #avi_controls{
    background: none;
    position: absolute;
    top: 0px;
    left: 385px;
    font-size: 0;
  }
  
  #avi_controls td{
    display: inline;
    vertical-align: top;
  }
  
  #avi_options{
    position: absolute;
    top: 44px;
    left: 160px;
    font-size: 0;
  }
  
  #naked{
    position: relative;
    margin-right: 10px;
    left: 70px;
    top: -11px;
  }
  
  #pose{
    position: absolute;
    top: 40px;
    left: 100px;
  }
  
  #undo{
    position: absolute;
    top: 28px;
    left: 130px;
  }
  
  #avibuilder_reset{
    position: absolute;
    top: -15px;
    left: 165px;
  }
  
  #item_marketdetail_link{
    background: linear-gradient(180deg, white, #FBB227);
    position: absolute;
    top: 29px;
    left: 780px;
  }
  
  #avi_controls #download{
    top: 80px;
    right: 81px;
    left: auto;
    position: absolute;
  }
  
  #bbmale, #bbfemale, #bbpaw{
    position: absolute;
    top: 80px;
  }
  
  #bbmale{left: 540px;}
  #bbfemale{left: 645px;}
  #bbpaw{left: 765px;}
  
  #item_marketdetail_link:hover{
    background: linear-gradient(180deg, white, #F4D392);
  }
  
  #undo, #avibuilder_reset, #naked, #bbmale, #bbfemale, #bbpaw{
    background: linear-gradient(180deg, white, #DBDBDB);
  }
  
  #undo, #item_marketdetail_link, #avibuilder_reset, #naked, #bbmale, #bbfemale, #bbpaw{
    color: #333 !important;
    border: 1px solid #B5B0A9;
    padding: 3px 10px;
    border-radius: 3px;
    font-size: 13px;
  }
  
  #undo:hover, #item_marketdetail_link:hover, #avibuilder_reset:hover, #naked:hover, #bbmale:hover, #bbfemale:hover, #bbpaw:hover{
    color: #3987df !important;
  }
  
  #undo, #item_marketdetail_link, #naked, #bbmale, #bbfemale, #bbpaw{
    padding: 3px 5px;
  }
  
  #bbmale:after, #bbfemale:after, #bbpaw:after{content: ' BBCode';}
  
  #equipment{font-size: 0;}
  
  /* ===== Search Area ===== */
  
  td:nth-of-type(2) .section_header:nth-of-type(2){
    font-size: 14px;
    font-weight: bold;
    line-height: 20px;
    display: inline;
    float: left;
    margin-top: 20px;
    padding: 0 10px;
    position: relative;
  }
  
  #search{
    background: #F0EBE6;
    border: 2px solid #C4BCB2;
    border-radius: 5px;
    box-sizing: border-box;
    padding: 5px;
    width: 728px;
    margin-top: 5px;
    margin-bottom: 10px;
    font-size: 12px;
  }
  
  #search .liteoption{
    background: linear-gradient(180deg, white, #FBB227);
    color: #333 !important;
    text-transform: capitalize;
    border: 1px solid #B5B0A9;
    padding: 2px 5px;
    border-radius: 3px;
    font-size: 13px;
    margin-left: 5px;
  }
  
  #page1{
    margin-left: 5px;
  }
  
  #page1 tr td{
    padding: 2px;
  }
  
  #equipment #page1{
    overflow-y: auto;
    overflow-x: hidden;
    height: 300px;
    width: 750px;
  }
  
  /* ==== Pop-up Modals ==== */
  body #simplemodal-container{
    height: auto !important;
  }
  
  #simplemodal-container .simplemodal-wrap{
    height: 200px !important;
    width: calc(100% - 10px) !important;
    overflow-y: auto !important;
    overflow-x: hidden !important;
    padding: 5px;
  }
  
}
