css:
@-moz-document domain("www.tripulse.net") {
html body {
background-color: rgb(30,30,30) !important;
background-image: url(http://i.imgur.com/3yTpS3i.png) !important;
color: rgb(150,150,150) !important;
}

.tcat-likebar {
    background: #23272B;
    font-size: 11px;
    color: #A0A0A0;
    padding: 5px 9px 5px 9px;
    border: 1px solid #7D7B7B;
    min-height: 20px;
    line-height: 20px;
}

.tcat-likebar a:link {
    color: rgb(208, 208, 208);
}

#stream .entry {
        background-color: #171616;
}

.tcat-likebar a:hover {
    color: rgb(211, 211, 211);
}

#panel .logoutlink {
background: #46535C;
}

.profile-mini {
 background: none !important;
              box-shadow: none !important;
}

#panel .logoutlink:hover {
background: #5A6267;
}

.poup a:hover {
    background: #1A1D21;
}

#page-memberprofile .member-info-box {
        border-top: 1px solid #646464
}

.poup a {
    background: #1F1F1F;
}

.dropdown {
    background: #1E1E1E;
    border-top: 1px solid #666 !important;
}

.thead .forumdesc {
        color: #98A4AF;
    background: #0A0A0A;
}

.pm_alert {
    background: #1A1D20;
    border: 1px solid #646464;
}

div.yuieditor-dropdown label {
    color: #969696;
}

div.yuieditor-dropdown {
        border: 1px solid #A3A3A3;
    background: #212121 !important;
    color: #969696;
}


.announcement_bar.one {
    color: #AAAAAA;
    background: #1A1D21;
}

div.yuieditor-dropdown input, div.yuieditor-dropdown textarea {
    background: #191C20;
        border: 1px solid #A3A3A3;
    border-top-color: #A3A3A3;
        color: #AAAAAA;
}

div.yuieditor-dropdown .button {
    font-weight: bold;
    color: #969696;
    padding: 6px 12px;
    background: #212121;
    border: solid 1px #A3A3A3;
}

.popup_menu .popup_item {
    color: #AAAAAA;
    background: #1A1D21;
}

.post_body a:hover {
    color: #7495A7;
    background: #212121;
}

.post_body a {
    color: #AAAAAA;
}

.announcement_bar {
    text-align: center;
    border: 1px solid #646464;
}

body::-webkit-scrollbar {
    width: 1em;
}
 
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
}
 
body::-webkit-scrollbar-thumb {
  background-color: #474747;
  outline: 1px solid #272727;
}
table, user agent stylesheettable, .spoiler_body {
    color: rgb(150,150,150);
        border-color: rgb(50,50,50);
}

div.sceditor-toolbar, .sceditor-container iframe, .sceditor-container textarea {
    background: #141414 !important;
}

.pagination a:hover, .button:hover,input[type="button"]:hover{
    background:#202427 !important;
}

.search-box:hover .button:hover {
    background:#202427;
    background-image: url(http://www.tripulse.net/forum/images/audentio/dazzle/icon-sprite.png) !important;
    background-position: -50px -8px !important;
}

.search-box .textbox {
    height: 30px !important;
}

.sceditor-container iframe, .sceditor-container textarea {
            color: #989898 !important;
}

.sceditor-container {
            background: #1B1B1B !important;
}

.sceditor-container {
    border: 1px solid #676767 !important;
}


div.sceditor-toolbar {
    border-bottom: 1px solid #505050 !important;
}

div.error {
    padding: 5px 10px;
    border-top: 2px solid #646464;
    border-bottom: 2px solid #646464;
    background: #141414;
    font-size: 12px;
}    

.tborder {
    border-color: rgb(100,100,100);
}

#header {
background:url(http://i.imgur.com/FrbMQM2.png)
}

blockquote {
    background: #272727
}

#content {
    background-color: rgb(30,30,30) !important;
    border-top: 0px
}

.editor_control_bar, select, input.textbox, .trow_sep, textarea {
    background: #212121
}



input.text, .post_author {
    background-color: rgb(30,30,30) !important;
}

.thead {
background:url(http://i.imgur.com/5w1AUwe.png)
}

.thead, .tcat, .subforumbox {
    border: 1px solid #3B3D3E;
}

.thead {
    box-shadow: none
}

.post_controls, .pagination .pagination_current, .head {
background:#090f0f url(http://i.imgur.com/5w1AUwe.png)center
}

.trow1, .trow2 {
background: rgb(20,20,20) !important;
                border-bottom: 1px solid #525252;
}

.forumdesc {
    font-size: 11px;
    font-style: italic;
    color: #666;
    margin-top: 2px;
}

.col-forumdetails a{
    color: #aaa;
    font-size: 12px;
}

.subforumbox {
background: rgb(10,10,10);
}

.navigation {
        background-color: rgb(30,30,30) !important;
    color: rgb(100,100,100);
            border-bottom: 1px solid #525252;
}

.navigation .active {
   color: rgb(154, 145, 145);

}

#tab-container > div, #tab-container ul.etabs li a {
        background: #313131;
    border: 1px solid #524848;
}

.tcat {
    background: #141616
}

.trow_sep, .tfoot{
    background: #202427
}

textarea, #shoutbox input.text {
    color: rgb(150,150,150);
}

.post_controls {
    background: #23272B
}

#shoutbox .entry > div, #shoutbox .user {
    border-bottom: dashed 1px rgb(97, 97, 97);
}

.select2-container-multi .select2-choices {
        border: 1px solid #524848;
        background: #212121 !important;
}

.select2-container-multi .select2-choices .select2-search-choice {
        color: #899696 !important;
        border: 1px solid #524848;
        background: #212121 !important;
}

#shoutbox .user {
    border-right: solid 1px rgb(93, 93, 93);
}

#shoutbox .head {
    background: url(http://i.imgur.com/5w1AUwe.png)
                                            center;
}

#shoutbox .text {
    color: #888
}

.avatar img, .user-avatar, .avatar img, .profile-avatar {
    border: none
}

.avatar img, .profile-avatar {
    background: none
}

.pagination a {
        border: 1px solid #46525F;
}

.pagination a {
    background: linear-gradient(to bottom,rgb(26, 29, 33) 0%,rgb(26, 29, 33) 100%);
}

.pagination a {
    box-shadow: inset 0px 1px 0px #46525F;
}

.post.classic .post_controls, .tfoot, input.textbox, .thead input.textbox, #panel input.textbox, .tfoot select, .thead select, .tfoot .button, .tfoot input[type="button"], #panel .button, select, .button, input[type="button"] {
    border: 1px solid #524848;
}

#panel a:hover {
background: #646464;
}

.button, input[type="button"] {
    background: linear-gradient(to bottom,rgb(26, 29, 33) 0%,rgb(26, 29, 33) 100%);
}

#shoutbox .data, .post .post_author div.author_avatar img {
background: none
}

.bottommenu, .user-dropdown-box-inner, .trow_deleted, .post.deleted_post, #quick_login .user-dropdown-box-inner a {
    background: #1E1E1E;
}

.showthread_spinner {
    opacity: 0;
}

.fancybox-skin {
    background: #1E2225;
}

.user-dropdown-box a {
    border-top: 1px solid #646464;
}

.post .post_head {
        border-bottom: 1px solid #7D7B7B;
}

.post_content .signature {
        border-top: 1px solid #7D7B7B;
}

hr {
        background-color: #7D7B7B;
}

.spoiler_header {
 background: #191D20 ;
}

.spoiler_header {
        border: 1px solid #646464;
}

.post_body a {
    color: #4A778E;
}

.g-background-default {
    background-color: #1E2125 !important;
}

a.sc-link-dark {
    color: #C7C7C7 !important;
}

a.sc-link-medium {
    color: #8E9092!important;
}

.post_body a:hover {
    color: #7495A7;
}
}
