css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.telegram.org") {
    body, .modal-content{
    background-color: /*[[bgc]]*/ !important;
    color:/*[[fgc]]*/ !important;
    }
    a.tg_checkbox {
        color: /*[[fgc]]*/ !important;
    }

    .im_page_wrap{ /*background color of contentwrapper under header*/
        background-color: rgba(50,50,50,0.5) !important;
        border-left: 1px solid /*[[decor]]*/ !important;
        border-right: 1px solid /*[[decor]]*/ !important;
        border-bottom: 1px solid /*[[decor]]*/ !important;
        box-shadow: none;
    }

    .im_dialog_peer { /*peer nickname unselected*/
        color:/*[[fgc]]*/ !important;
    }
    
    .contacts_modal_members_list a.contacts_modal_contact { /*nicknames in contactlist via hamburger menu*/
       color: /*[[fgc]]*/;
    }


    .active a.im_dialog { /*selected peer */
    background-color: /*[[decor]]*/ !important;
    }

    .tg_head_split, .md_modal_head{ /*page header block*/
    background-color: /*[[decor]]*/ !important;
    }

    .dropdown.open .tg_head_btn, .tg_head_btn:hover { 
        background: /*[[decor]]*/ !important;;
    }


    .form-control { /*input field*/
    background-color: rgba(20,20,20,0.5) !important;
    color: /*[[fgc]]*/ !important;
    }

    .im_dialogs_col_wrap { /*contactlist right border*/
        border-right: 2px solid /*[[decor]]*/; 
    }

    .im_dialogs_col .nano > .nano-pane > .nano-slider { /*scrollbarbutton when hover*/
        background-color: /*[[decor]]*/ !important;
    }


    a:hover {
      background-color: rgba(80,80,80,0.5) !important;
    }

        /*emoji theming:*/

    .composer_emoji_tooltip, .icon-tooltip-tail, .dropdown-menu  {
        background-color: /*[[bgc]]*/ !important;
        border-color: /*[[decor]]*/;
    }   
    
}
