@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("trogazeta.ru") {
@font-face {
  font-family: 'Marck Script', cursive;
  src: url('https://fonts.googleapis.com/css?family=Marck+Script&subset=latin,cyrillic,latin-ext') format('truetype');
}

* {
  font-family: 'Marck Script', cursive !important;
}
  html, body {font-family: 'Marck Script', cursive !important;}
  .navbar > .container .navbar-brand, .navbar-default .navbar-nav > li > a {color: black !important;}
  #secondary .widget a {color: black !important; font-family: 'Marck Script', cursive !important;}
  .main-content-area {margin-top: 0 !important;}
  .site-header {height: 100px !important;}
 p {font-family: 'Marck Script', cursive !important; font-size: 20pt !important;}
  .main-content-area {
  margin-top: 10px !important;
  margin-bottom: 10px !important;
}
  .post-inner-content {
  padding: 10px 10px !important;
  background-color: #fff;
  border: 1px solid #dadada;
  border-top: none;
}
  .single-view, .blog-item-wrap {margin-bottom: 10px !important;}
  #colophon {border: none !important;}
  .post-inner-content, .well, .navbar.navbar-default, #colophon {
        position:relative !important;
        background:#fff !important;
        border-radius:4px !important;
    -webkit-box-shadow:0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1) inset !important;
       -moz-box-shadow:0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1) inset !important;
            box-shadow:0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1) inset !important;
        -moz-border-radius:4px;
 
}
 
.post-inner-content:before, .post-inner-content:after, .well:before, .well:after {
        content:"";
        position:absolute !important;
        z-index:-2 !important;
        bottom:15px !important;
        left:10px !important;
        width:50% !important;
        height:20% !important;
        max-width:300px !important;
    -webkit-box-shadow:0 15px 10px rgba(0, 0, 0, 0.7) !important;
       -moz-box-shadow:0 15px 10px rgba(0, 0, 0, 0.7) !important;
            box-shadow:0 15px 10px rgba(0, 0, 0, 0.7) !important;
    -webkit-transform:rotate(-3deg) !important;
       -moz-transform:rotate(-3deg) !important;
        -ms-transform:rotate(-3deg) !important;
         -o-transform:rotate(-3deg) !important;
            transform:rotate(-3deg) !important;
}
 
.post-inner-content:after, .well:after, .navbar.navbar-default:after {
    right:10px !important;
    left:auto !important;
    -webkit-transform:rotate(3deg) !important;
       -moz-transform:rotate(3deg) !important;
        -ms-transform:rotate(3deg) !important;
         -o-transform:rotate(3deg) !important;
            transform:rotate(3deg) !important;
}
  .btn.btn-default {
  position: relative;
  color: white;
  font-weight: bold;
  text-decoration: none;
  text-shadow: -1px -1px #000;
  user-select: none;
  padding: .8em 2em;
  outline: none;
  background-color: #000;
  background-image: linear-gradient(45deg, rgba(255,255,255,.0) 30%, rgba(255,255,255,.8), rgba(255,255,255,.0) 70%), radial-gradient(190% 100% at 50% 0%, rgba(255,255,255,.7) 0%, rgba(255,255,255,.5) 50%, rgba(0,0,0,0) 50%);
  background-repeat: no-repeat;
  background-size: 200% 100%, auto;
  background-position: 200% 0, 0 0;
  box-shadow: rgba(0,0,0,.3) 0 2px 5px;
} 
.btn.btn-default:active {
  top: 1px;
  box-shadow: none;
}
.btn.btn-default:hover {
  transition: .5s linear;
  background-position: -200% 0, 0 0;
}
  .btn-default, .label-default {border: none !important;}
  #secondary .widget, .widget ul li {margin-bottom: 0 !important;}
}
