css:
@-moz-document domain("naruho.do") {
/* Fondo */
#wrap_content {
	background: #333 no-repeat top center !important;
  	background: fixed !important;
	background-position:50% 0px !important;
	left: 30px !important;
}

body {
  	background-image:url("/*[[ndwall]]*/") !important;
	background-color: #000;
	background-attachment: fixed !important;
	background-position:50% 0px !important;
	background-size: /*[[ndwallsize]]*/ !important;
  	background-repeat: no-repeat !important;
	left: 30px !important;
}

/* Feeds 
#long_feeds {
    width: 100% !important;
	max-width: 800px;
}*/

#long_feeds > div > div, .box, #footer {
	background-color: rgba(20,20,20,.60) !important;
    color: rgba(256,256,256,.8) !important;
	border:solid 0 !important;
	box-shadow:0 0 3px rgba(0,0,0,.3);
   	margin: 3px !important;
	border-radius: 0px !important;
}

/* Comentarios */
#shareForm {
	margin-top: 50px !important;
}

#shareForm #typeshare, input, select, option {
	background-color: rgba(0,0,0,.5) !important;
    color: rgba(256,256,256,.8) !important;
    border: 0 !important;
}

#shareForm #typeshare div {
	background-color: rgba(0,0,0,.4) !important;
    border: 0 !important;
    margin-top: 5px !important;
}

.comments > ul > li {
	background-color: rgba(0,0,0,.5) !important;
    margin: 3px !important;
    border: 0 !important;
    padding: 5px 2px !important;
} 

.more-comments {
    background-color: rgba(0,0,0,.5) !important;
    margin-left: -8px;
    padding: 10px 0;
    text-align: center;
    text-decoration: none;
    width: 102.3%;
    border: 0 !important;
}

textarea {
    background-color: rgba(0,0,0,.5) !important;
    border: 0 !important;
    color: rgba(256,256,256,.8) !important;
}

.division_line, #series-nav, .edit_series.ng-scope {
    border-top: 1px solid rgba(256,256,256,.3) !important;
}

.avatar.rounded {
	width: 9% !important;
    height: auto !important;
    max-height: 120px !important;
    padding: 0 !important;
}

.avatar.rounded img {
	width: 100% !important;
    height: auto !important;
    max-height: 120px !important;
    padding: 0 !important;
}

.tail {
	display: none !important;
}

/* Perfil */
#top_image_banner {
    background-color: rgba(0,0,0,.7) !important;
}

.info {
	background-color: rgba(20,20,20,.60) !important;
    color: rgba(256,256,256,.7) !important;
	border:solid 0 !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important;
	border-radius: 0px !important;
    padding: 5px !important;
}

/* Notificaciones */

#header {
	background-color: rgba(20,20,20,.60) !important;
    color: rgba(256,256,256,.8) !important;
	position: fixed !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important;
    border: 0 !important;
    width: 100% !important;
    height: 24px !important;
	z-index:5 !important; 
}

.ua-box, #ndopanel, .atwho-view {
	background-color: rgba(0,0,0,.7) !important;
    color: rgba(256,256,256,.8) !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important; 
    border: 0 !important;
	margin-top: 5px !important;
}

.notificationsList > a:hover, #ndopanel li:hover, #ndoplus:hover, #ndoplus.plusON, #plusextra span, #plusreset, .list:hover, .tabs.nav-tabs .active, .tabs.nav-tabs > li > a:hover, .atwho-view > ul > li:hover, .atwho-view .cur {
	background-color: rgba(256,256,256,.2) !important;
    transition: all 150ms ease-in-out;
}

.ua-notifications h4, #ndo-settings-box > ul > li, h2.innerS, .top-nav, #series-nav, .subtitle, #fullnotification .list {
	color: rgba(256,256,256,.9) !important;
    border-bottom: 1px solid rgba(256,256,256,.3) !important;
}

#fullnotification {
	background-color: rgba(20,20,20,.60) !important;
	box-shadow:0 0 3px rgba(0,0,0,.3);
	border: 0 !important;
}

/* Texto */
span {color: rgba(256,256,256,.5) !important;}
h3, strong {color: rgba(256,256,256,.9) !important;}
a {color: rgba(256,256,256,1) !important;text-shadow: 0px 0px 3px rgba(150,200,256,.6);}

/* Imagenes */
.center.zoom {
	height: auto !important;
}

.center.zoom img {
	width: auto !important;
    max-width: 100% !important;
}

/* Anime */
.top-nav {
    padding: 10px 10px !important;
}

.descrip p, .descrip h3, .atwho-view ul li {
    border-bottom: 1px solid rgba(256,256,256,.3) !important;
}

#user_seriesList {
	background-color: rgba(20,20,20,.60) !important;
    color: rgba(256,256,256,.8) !important;
	border:solid 0 !important;
	box-shadow:0 0 3px rgba(0,0,0,.3);
   	margin: 3px !important;
	border-radius: 0px !important;
    padding: 5px 5px !important;
}

.row.ng-scope {
	background-color: rgba(0,0,0,.5) !important;
    margin: 3px !important;
    border: 0 !important;
} 

.edit_series.ng-scope {
	background-color: rgba(256,256,256,.1) !important;
    transition: all 150ms ease-in-out;
}
}

@-moz-document url-prefix("https://naruho.do/es/anime"), url-prefix("https://naruho.do/anime"), url-prefix("https://naruho.do/settings/"), url-prefix("https://naruho.do/es/chart"), url-prefix("https://naruho.do/search") {
#wrap_content .content {
	background-color: rgba(20,20,20,.60) !important;
    color: rgba(256,256,256,.8) !important;
	border:solid 0 !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important;
	border-radius: 0px !important;
    margin-top: 50px !important;
    padding: 10px 10px !important;
}

.pub728 {
	display: none !important;
}

#reviews .add-comment, .gridded_list > li, .card {
	background-color: rgba(0,0,0,.5) !important;
    border: 0 !important;
    padding: 5px 5px !important;
} 
}

@-moz-document url-prefix("https://naruho.do/forum/") {
.forum-header {
	background-color: rgba(20,20,20,.60) !important;
    color: rgba(256,256,256,.8) !important;
	border:solid 0 !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important;
	border-radius: 0px !important;
    padding: 10px 10px !important;
    margin-top: 60px !important;
}

#header {
	background-color: rgba(20,20,20,.60) !important;
    margin-top: -60px !important;
    height: 56px !important;
}

.discussion-stats {
    margin: 10px !important;
    background: rgba(0,0,0,.6) !important;
    color: #fff !important;
    padding: 5px 10px !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important;
    text-align: center !important;
}

.discussion-stats-content {
	padding: 0px !important;
}

h3.discussion-post-author {
    font-weight:bold;
    margin: 2px !important;
}

.discussion-stream {
	margin: 0px 100px !important;
}

.discussion-stream > .discussion-body, .forum-body {
    background: rgba(0,0,0,.5);
    padding: 5px;
	box-shadow:0 0 5px rgba(0,0,0,.3);
}

.discussion-post-content .discussion-item-author img {
	width: 120px !important;
    height: auto !important;
    max-height: 200px !important;
    margin: 3px !important;
    border-radius: 0px !important;
    border:solid 1px rgba(256,256,256,.2) !important;
    transition: all 150ms ease-in-out !important;
}

.discussion-post-content .discussion-item-author img:hover {
	box-shadow:0 0 20px rgba(0,0,0,.3);
    transition: all 150ms ease-in-out !important;
    transform: scale(1.3) !important;
}

.discussion-post {
	min-height: 210px;
	border-bottom: 1px solid rgba(256,256,256,.2);
}

.discussion-post-content {
	padding: 2px 0 42px 74px !important;
}

.discussion-post-body {
	margin-left: 5px;
}

.discussion-post-body > a {
	float: left !important;
    left: 133px;
    top: 19px;
    position: absolute;
}

.discussion-post-body > a + h3 + div {
	margin-top: 15px !important;
}

.post-actions {
	display: block !important;
    position: absolute;
    right: 6px;
    top: 8px;
}

.discussion-post-text, .in-reply-to, blockquote, .post-positive {
    background: rgba(0,0,0,.3) !important;
    color: rgba(256,256,256,.8) !important;
    padding: 1px 5px !important;
    margin: 3px !important;
    max-width: 92% !important;
    border: solid 1px rgba(256,256,256,.2) !important;
}

.discussion-post-text > p {
	margin: 3px;
}

.btn, .tag-label {
	background: rgba(256,256,256,.1) !important;
    border:solid 1px rgba(256,256,256,.4) !important;
    color: #fff !important;
    opacity: .7 !important;
    border-radius: 0px !important;
    text-decoration: none !important;
    transition: all 150ms ease-in-out !important;
}

.discussion-actions-items a, #forum-nav .actions a {
    float: right !important;
    margin: 2px !important;
    padding: 1px 5px !important;
    font-size: 14px !important;
	background: rgba(256,256,256,.1) !important;
    border:solid 1px rgba(256,256,256,.4) !important;
    color: #fff !important;
    opacity: .7 !important;
    border-radius: 0px !important;
    text-decoration: none !important;
    transition: all 150ms ease-in-out !important;
}

.btn:hover, .discussion-actions-items a:hover, #forum-nav .actions a:hover {
	background: rgba(256,256,256,.2) !important;
    border:solid 1px rgba(256,256,256,.6) !important;
    opacity: 1 !important;
    transition: all 150ms ease-in-out !important;
    transform: scale(1.2) !important;
}

.discussion-actions-items {
    position: static !important;
	margin-left: -113px !important;
}

.forum-side label {
	color: rgba(256,256,256,.7) !important;
}

.discussion-item:hover, .tags-results-container > li:hover {
	background: transparent !important;
}

.post-positive, .tags-results-container > li {
    transition: all 150ms ease-in-out !important;
    color: #fff !important;
}

.post-positive:hover, .tags-results-container > li:hover {
    margin-left: -10px !important;
	/*transform: scale(1.02) !important;*/
    transition: all 150ms ease-in-out !important;
}

#composer-box, .fancybox-skin {
    background: rgba(0,0,0,.7) !important;
    color: rgba(256,256,256,.8) !important;
	box-shadow:0 0 5px rgba(0,0,0,.3) !important;
}

.editor-toolbar, .preview-nav .active a {
    background: rgba(0,0,0,.3) !important;
    border: solid 1px rgba(256,256,256,.2) !important;
}

.preview-nav a:hover {
    background: rgba(0,0,0,.3) !important;
    border: solid 1px rgba(256,256,256,.2) !important;
}

.CodeMirror-scroll span {
	color: #999 !important;
}

#preview-box img {
	max-width: 100% !important;
    max-height: 300px !important;
}
}
