css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://blog.livedoor.jp/itsoku/") {

  .headline,/* ヘッドライン */
  [class^="ad"],/* 広告 */
  .banner_item,
  .plugin-memo,/* サイバーパーツ */
  .widget-area,/* フッター */
  #comments,/* コメント欄 */
  .article-sub-rss,/* オススメ記事一覧 */
  .article-header-bottom,
  .comment,
  .article-footer,
  .article-sub-popular,/* 人気記事ランキング */
  .linkwithin-art,/* 似たような内容の記事 */
  .share
  {display: none!important;}
  
  .article-body {
    margin-bottom: 15px!important;
  }
   
  .left-container,
  #sidebar
  {margin-top: 11px!important;}
  
  #sidebar {
    position: static!important;
  }

}
