css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fxp.co.il") {
  body {background:#EFEFEF;}
#topmenunew {background:url(http://themesteam.com/cube/themes/Cube/design/steam/98.png) fixed no-repeat !important; width:100%; height:180px; background-size:cover !important; box-shadow:inset 0 0 10px gray; position:fixed; top:0; right:0; z-index:999999;}
  .toplogin {background:white;border:none;border-radius:0;width:100%;padding:25px 20px;left:0;}
  .toplogin a {color:black;text-decoration:none;}
  .toplogin .log_in2:first-child:after {background:#EB3D59;width:50px;height:47px;content:"FxP";margin:-45px 20px 0 0;display:block;color:white;font-size:20pt;font-weight:bold;padding:15px 25px 0;box-shadow:none;}
  #topmenunew .barssize {background:#EB3D59;margin:0;margin-top:25px;width:100%;border-radius:0;border:none;}
  #top-menu a:hover {background:#DB2E48!important;}
  #topmenunew .spacer {background:none;}
  .sec.barssize.graygr {background:none!important;box-sizing: border-box;text-align:left;margin:0;z-index:99999999;color:white!important;font-weight:bold;}
  a[title='fxp'] {display:none;}
  .divhed {padding-top:200px;}

  .big-image .first,.bluegr,.thread_controls,.blockhead {background:#EB3D59;border-radius:0;border-bottom:3px solid #DB2E48;color:white;}
  .thread_controls a,.greengr .prefixtit,.greengr h1  {color:white!important;}
  .image_text_main,.textsik,.greengr {background:#EB3D59;border-radius:0;border-top:3px solid #DB2E48;}
  .big-image .first:hover,.image_text_main:hover,.textsik:hover {background:#DB2E48;}
  .videohome {background:white;border:3px solid #DB2E48;}
  .leftbl {padding:10px 3px;border-radius:0;}
  .threadlisthead {background:#2E3138;padding:7px 0px;}
}
