css:
@-moz-document domain("www.space.com") {

.container
{
	color: #E0E0E0; background-color: #202020 !important;
}

.sect-items
{
	color: #D0D0D0; background-color: #303030 !important;
}

.search-item
{
	color: #D0C0A0;
}

body
{
	color: #E0E0E0; background-color: #202020 !important;
}

h1,h2,h3
{
	color: #A0A0A0;
}

.h1
{
	color: #A0A0A0;
}

h2.nolinks
{
	color: #A0A0A0;
}

.section_asset_title
{
	color: #A0A0A0;
}

.section_asset_desc
{
	color: #E0E0E0;
}

a
{
	color: #50A0FF !important;
}

a:visited
{
	color: #90B0E0 !important;
}

a:hover
{
	color: #A0D0FF !important;
}

#searchbox
{
	background-color: #383838 !important;
}

#container,#containerBg,.white_box,.ad_white_box,.headlines ul,.product-description
{
	background-color: #303030;
}

.left-rail
{
	background-color: #202020;
}

.fig-desc
{
	color: #A0A0A0;
}

.fig-credit
{
	color: #707070;
	font-style: italic;
	text-align: right;
}

.multiPageItem,.multiPagePager
{
	background-color: #202020 !important;
}

.slideText
{
	color: #C0C0C0 !important;
}

.related-title
{
	color: #A0A0A0 !important;
}

div.product-price,div.product-name
{
	color: #808080 !important; background-color: #303030 !important;
}

div.sidebar a div div
{
	background-color: #404040 !important;
}

div.content-preview a.thumb
{
	background-color: #404040 !important;
}

div.slideshow-nav-items
{
	background-color: #404040 !important;
}

div#side_nav
{
	border-left-color: #606060;
}

div.thebufferzone
{
	background-color: #303030;
}

#containerBg div div div span table tbody tr td,#containerBg div div div div.article_img_i02 div div
{
	color: #A0A0A0; border-color: #000000 !important;
}

body > div.site-wrapper > div.content-wrapper.filling.overall > div > div > div.listing-wrapper.pure-g > div
{
	background-color: #202020;
	color: #A0A0A0; border-color: #000000 !important;
}

body > div.site-wrapper > div.content-wrapper.filling.overall > div > div > div.listing-wrapper.pure-g > div > ul > li > a
{
	color: #909090 !important;
}

}
