css:
@-moz-document domain("arcgames.com") {
/* apply new main font color (grey) */







   body, p, textarea {
        font-family: sans-serif !important; font-size: 15px !important;
        /* background: #0b0605; */
         background: #000000;                         
         background-position: bottom;
         background-image: url("http://ricardo.ishibashi.com.br/neverwinter/img/main-background-footer.jpg") !important; 
         background-repeat: no-repeat;                         
         
    }
 
 
  #Head
{
    background:  #0b0605;
    background-image: url("http://ricardo.ishibashi.com.br/neverwinter/img/main-background.jpg") !important; 
    background-position: 0% 35%;
    text-align:center;

    
   /* height:150px !important; */
   
    
}

/* background colors */
div * { background-color: transparent !important; }
.Announcement { background-color: #1d110E !important; }
.ButtonBar, .cleditorToolbar, .cleditorMain { background-color: #ff0000 !important; }
.Breadcrumbs, .UserQuote, .UserSpoiler, .HasNew, pre { background-color: #14110F !important; }
.Row, .ItemDiscussion, .TextBox, .InputBox, .PopList, .Dropdown, .Flyout, .tipsy { background-color: #1C110E !important; }





/* adjust post tags */
.QnA-Tag-Answered, .QnA-Tag-Accepted, .QnA-Accepted {
    background: #56AB56 none repeat scroll 0% 0%;
    color: #00FF2A;
}

.Tag-Closed {
    background: #9D2E2E none repeat scroll 0% 0%;
    color: #D35429;
    font-size: 10pt !important;
}

.QnA-Tag-Question {
    background: #fba000 none repeat scroll 0% 0%;
    color: #ffa200;
}

.Tag-Announcement {
    background: #4e7edb none repeat scroll 0% 0%;
    color: #ffa200;
}

.NewCommentCount {
    background: #1C110E none repeat scroll 0% 0%;
    color: #ffa200;
    font-size: 10pt !important;

}








div.Message:hover, div.Item-Body:hover
{
    background: #1C110E;
}

div.Message, div.Item-Body
{
    background: #1C110E;
}



div.Comment
{
    background: #1C110E; 
}


.ItemDiscussion, .Comment, .Activities > .Activity, .MessageList > li, .Item-Search 
{
  background: #1C110E;
  padding: 5px;
    
}







/* Changes to pretty the avatar area */
.AuthorWrap > .Author .ProfilePhotoMedium
{
    width:auto !important;
    height:auto;
    max-width: 125px !important;
    max-height:150px !important;
    border:0px solid #000;
  
}





.CommentHeader .AuthorWrap, .CommentHeader .AuthorWrap > .Author
{
    text-align:center;
}

.Comment .CommentInfo
{
    display:block;
    background-color:rgba(255,255,255,0.05);
    padding:3px 3px 2px 3px;
    border-radius:5px;
    text-align:center;
    
    
}







body {
    /* цвет шрифта сообщений */
  color: #c0c0bc;
}

.Button {
  color: #C3C3C3;
}

.NavButton {
	color: #A7A7A7;
    text-shadow: none;
}

.HomepageTitle {
  color: #A7A7A7;
}

#Head {
  color: #A7A7A7;
}

.ChildCategoryList .ItemContent {
  padding: 0;
}








/* new warning message */
.WarningMessage {
  background: #bbb;
  border: none;
}

/* make quotes separate from other text */


blockquote.UserQuote {
    /* цвет фона цитаты,  */
	background: rgba(20, 17, 15, 1);
     /* цвет полосы слева от цитаты и ширина в пикселах */
    border-left: 2px solid rgba(80, 80, 80, 1);
    border-right: 2px solid rgba(80, 80, 80, 0.5);
    border-top: 2px solid rgba(80, 80, 80, 1);
    border-bottom: 2px solid rgba(80, 80, 80, 0.5);
    border-radius:5px;
}





blockquote.Quote:hover
{
    box-shadow:0 0 8px 2px rgba(255,255,255,0.4);
}














/* add borders to improve visibility */
.UserSignature {
	border-top: 1px solid rgba(80, 80, 80, 1);   
}

.Separator {
  background: rgba(80, 80, 80, 1);   
}

.BoxTop {
  border: 1px solid rgba(80, 80, 80, 1);   
}

.DataList .Item {
  border: 1px solid rgba(80, 80, 80, 1);   
}

.ItemDiscussion {
  border: 1px solid rgba(80, 80, 80, 1);   
}

.FormWrapper {
   border: 1px solid rgba(80, 80, 80, 1);   
}

.Box {
  border: 1px solid rgba(80, 80, 80, 1);   
}

.DataTableWrap {
    border: 1px solid rgba(80, 80, 80, 1);   
}  









/* Fixes codebox colors */
pre
{
	background-color:rgba(255,255,255,0.025);
	border-radius:5px;
    border: 1px solid rgba(80, 80, 80, 1);
}

pre:hover
{
    box-shadow:0 0 8px 2px rgba(255,255,255,0.4);
}

div.SpoilerTitle input {
  color: #000; 
}

.SpoilerText > pre
{
    background-color:rgba(80,80,80,0.5);
}

div.UserSpoiler {
  background-color: rgba(55, 55, 55, 0.5);
}

.UserSpoiler:hover
{
    box-shadow:0 0 8px 2px rgba(255,255,255,0.4);
}

/* Stop overly large image displays */
.Signature.UserSignature img
{
    width:auto;
    height:auto;
    max-width:500px;
    max-height:150px;
}














/* Adds page listing to bottom of page */
#PagerAfter
{
    display:block !important;
}

/* Fixes "Post Edited" message color (less intense) */
div.PostEdited
{
    color: rgba(255,128,128,0.5);
}

/* show icons properly (they used FFFFFF instead of ffffff - credit to dblazen1 for finding this one */
.ReactSprite
{
    background-image: url("http://perfectworld.vanillaforums.com/applications/dashboard/design/images/sprites-14-ffffff.png") !important;
}

/* Fixes bookmarked threads */
.Bookmarked::before
{
    display:none;
}

a.Bookmarked::before
{
    display:inline-block;
    font-family:"vanillicon";
    font-variant:normal;
    font-weight:normal;
    font-style:normal;
    color:#FFF;
    min-width:1em;
    text-align:center;
    text-decoration:inherit;
    text-transform:none;
    line-height:1;
    -webkit-font-smoothing:antialiased;
    content: "\f199";
}










/* new link colors and link related */
a {
  color: #6C89C1;
}

a:hover {
  text-decoration: underline;
  color: #6C89C1;
}

#Head a {
  color: #6C89C1;
  font-weight: normal;
}

.MiniPager a {
  text-decoration: underline;
  color: #6C89C1;
}

.MiniPager span {
    color: #6C89C1;
}

a.Bookmark {
   text-decoration: none; 
}
    
.BlockTitle {
    font-size: 13px;
    font-weight: normal;
}

.UserLink {
    color: #A7A7A7;
}

.SiteMenu a {
    font-size: 13px;
}














/* other unspecific changes (or can't remember what they do...) */
.ProfilePhoto {
    border: 0px solid #000;
    border-radius: 6px;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
} /* smoother profile pic */
    
.CategoryTable .ChildCategories {
    font-size: 13px;
}
    
mark {
  background: rgba(88, 88, 88, 1);
}

.Preview {
  background: rgba(0, 0, 0, 0.03);
  color: #fff;
}

.DataList > li {
  margin-bottom: 5px !important;
}

#Form_Search {
  background-color: rgba(80, 80, 80, 1);   
}

#Form_Go {
  margin-top: 2px;
}

.Button:hover {
  text-decoration: none;
}

.NavButton:hover {
	text-decoration: none;
    color: #fff;
}

.Profile > .DataListWrap > .FormWrapper {
	margin-bottom: 5px;   
}

ul.Conversation > li > .ConversationMessage > .Message {
	margin-top: 25px;  /* places pm message under profile pic */ 
}

.ProfilePhotoSmall {
    display: none; /* do not display profile pics on last posts on categories */
} 
    
.UserBox .Username, .MeBox .Username {
    font-size: 20px; /* adjusts font size to the profile pic */
}
    
tbody td.BigCount {
    font-size: 14px; /* bring count size in line */
}
    
.DiscussionHeader .DiscussionMeta {
    margin-bottom: 20px;
    border-bottom: 1px solid rgba(80, 80, 80, 1);
    padding-bottom: 40px;
}


   





/* adjust profile post area */
.CommentHeader .ProfilePhotoMedium, .DiscussionHeader .ProfilePhotoMedium {
    height: 75px;
    width: 75px;
}

.CommentHeader .PhotoWrap.Online .ProfilePhoto, .DiscussionHeader .PhotoWrap.Online .ProfilePhoto {
    border-bottom: 3px solid #6C89C1;
}

.CommentHeader .PhotoWrap.Offline .ProfilePhoto, .DiscussionHeader .PhotoWrap.Offline .ProfilePhoto {
    border-bottom: 3px solid #6C89C1;
}

.CommentHeader .AuthorTitle {
    display: block;
}
    
.AuthorTitle {
   /* color: #fba000; */
   color: #e58b00;
}

.MItem.Category {
    display: block;
}

.MItem {
    margin-left: 0px;
}












/* Fixes awful look of subforum categories subforums of thread lists */
.ChildCategoryList
{
    padding:20px 0;
}

.ChildCategoryList .Item
{
    box-sizing:border-box;
    width:24%;
    height:160px;
    margin:0 3px;
    overflow:hidden;
    box-shadow:0 0 4px 0px #000;
}

.ChildCategoryList .Item:nth-child(odd)
{
    clear:none;
}

.ChildCategoryList .Item h3.CategoryName
{
    height:40px;
    display:block;
    margin-bottom:8px;
    padding-top:4px;
    padding-bottom:6px;
    text-align:center;
    border-bottom:1px solid #222224;
    background-color:#222224;
}

.ChildCategoryList .Item div.CategoryDescription
{
    font-size:0.8em;
    line-height:1.3em;
    padding:0 10px;
}

.ChildCategoryList .ItemContent
{
    padding:0;
    vertical-align:top;
}

.DataList.ChildCategoryList > li.Item
{
    margin-bottom:6px !important;
    border-radius:10px 4px 10px 4px;
    position:relative;
}

.ChildCategoryList .Item .Meta.Hidden
{
    display:block;
    overflow:hidden;
    /*padding-top:10px;*/
    position:absolute;
    width:95%;
    padding:0 10px;
    bottom:0;
}

.ChildCategoryList .Item .Meta.Hidden .MItem
{
    display:inline-block;
    width:45%;
    overflow:visible;
    position:relative;
}

.ChildCategoryList .Item .Meta.Hidden .MItem.CommentCount
{
    text-align:right;
}
   


 













}
